INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3403, 'nvi', 'Levítico', 'lv', 23, 1, 'Disse o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3404, 'nvi', 'Levítico', 'lv', 23, 2, '"Diga o seguinte aos israelitas: Estas são as minhas festas, as festas fixas do Senhor, que vocês proclamarão como reuniões sagradas:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3405, 'nvi', 'Levítico', 'lv', 23, 3, '"Em seis dias realize os seus trabalhos, mas o sétimo dia é sábado, dia de descanso e de reunião sagrada. Não realizem trabalho algum; onde quer que morarem, será sábado dedicado ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3406, 'nvi', 'Levítico', 'lv', 23, 4, '"Estas são as festas fixas do Senhor, as reuniões sagradas que vocês proclamarão no tempo devido:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3407, 'nvi', 'Levítico', 'lv', 23, 5, 'A páscoa do Senhor começa no entardecer do décimo quarto dia do primeiro mês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3408, 'nvi', 'Levítico', 'lv', 23, 6, 'No décimo quinto dia daquele mês começa a festa do Senhor, a festa dos pães sem fermento; durante sete dias vocês comerão pães sem fermento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3409, 'nvi', 'Levítico', 'lv', 23, 7, 'No primeiro dia façam uma reunião sagrada e não realizem trabalho algum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3410, 'nvi', 'Levítico', 'lv', 23, 8, 'Durante sete dias apresentem ao Senhor ofertas preparadas no fogo. E no sétimo dia façam uma reunião sagrada e não realizem trabalho algum".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3411, 'nvi', 'Levítico', 'lv', 23, 9, 'Disse o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3412, 'nvi', 'Levítico', 'lv', 23, 10, '"Diga o seguinte aos israelitas: Quando vocês entrarem na terra que lhes dou e fizerem colheita, tragam ao sacerdote um feixe do primeiro cereal que colherem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3413, 'nvi', 'Levítico', 'lv', 23, 11, 'O sacerdote moverá ritualmente o feixe perante o Senhor para que seja aceito em favor de vocês; ele o moverá no dia seguinte ao sábado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3414, 'nvi', 'Levítico', 'lv', 23, 12, 'No dia em que moverem o feixe, vocês oferecerão em holocausto ao Senhor um cordeiro de um ano de idade e sem defeito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3415, 'nvi', 'Levítico', 'lv', 23, 13, 'Apresentem também uma oferta de cereal de dois jarros da melhor farinha amassada com óleo, oferta ao Senhor preparada no fogo, de aroma agradável, e uma oferta derramada de um litro de vinho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3416, 'nvi', 'Levítico', 'lv', 23, 14, 'Vocês não poderão comer pão algum, nem cereal tostado, nem cereal novo, até o dia em que trouxerem essa oferta ao Deus de vocês. Este é um decreto perpétuo para as suas gerações, onde quer que morarem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3417, 'nvi', 'Levítico', 'lv', 23, 15, '"A partir do dia seguinte ao sábado, o dia em que vocês trarão o feixe da oferta ritualmente movida, contem sete semanas completas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3418, 'nvi', 'Levítico', 'lv', 23, 16, 'Contem cinqüenta dias, até um dia depois do sétimo sábado, e então apresentem uma oferta de cereal novo ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3419, 'nvi', 'Levítico', 'lv', 23, 17, 'Onde quer que morarem, tragam de casa dois pães feitos com dois jarros da melhor farinha, cozidos com fermento, como oferta movida dos primeiros frutos ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3420, 'nvi', 'Levítico', 'lv', 23, 18, 'Junto com os pães apresentem sete cordeiros, cada um com um ano de idade e sem defeito, um novilho e dois carneiros. Eles serão holocausto ao Senhor, juntamente com as suas ofertas de cereal e ofertas derramadas; é oferta preparada no fogo, de aroma agradável ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3421, 'nvi', 'Levítico', 'lv', 23, 19, 'Depois sacrifiquem um bode como oferta pelo pecado e dois cordeiros, cada um com um ano de idade, como oferta de comunhão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3422, 'nvi', 'Levítico', 'lv', 23, 20, 'O sacerdote moverá os dois cordeiros perante o Senhor como gesto ritual de apresentação, juntamente com o pão dos primeiros frutos. São uma oferta sagrada ao Senhor que pertencem ao sacerdote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3423, 'nvi', 'Levítico', 'lv', 23, 21, 'Naquele mesmo dia vocês proclamarão uma reunião sagrada e não realizarão trabalho algum. Este é um decreto perpétuo para as suas gerações, onde quer que vocês morarem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3424, 'nvi', 'Levítico', 'lv', 23, 22, '"Quando fizerem a colheita da sua terra, não colham até às extremidades da sua lavoura, nem ajuntem as espigas caídas da sua colheita. Deixem-nas para o necessitado e para o estrangeiro. Eu sou o Senhor, o Deus de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3425, 'nvi', 'Levítico', 'lv', 23, 23, 'Disse o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3426, 'nvi', 'Levítico', 'lv', 23, 24, '"Diga também aos israelitas: No dia primeiro do sétimo mês vocês terão um dia de descanso, uma reunião sagrada, comemorada com toques de trombeta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3427, 'nvi', 'Levítico', 'lv', 23, 25, 'Não realizem trabalho algum, mas apresentem ao Senhor uma oferta preparada no fogo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3428, 'nvi', 'Levítico', 'lv', 23, 26, 'Disse o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3429, 'nvi', 'Levítico', 'lv', 23, 27, '"O décimo dia deste sétimo mês é o Dia da Expiação. Façam uma reunião sagrada e humilhem-se, e apresentem ao Senhor uma oferta preparada no fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3430, 'nvi', 'Levítico', 'lv', 23, 28, 'Não realizem trabalho algum nesse dia, porque é o Dia da Expiação, quando se faz propiciação por vocês perante o Senhor, o Deus de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3431, 'nvi', 'Levítico', 'lv', 23, 29, 'Quem não se humilhar nesse dia será eliminado do seu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3432, 'nvi', 'Levítico', 'lv', 23, 30, 'Eu destruirei do meio do seu povo todo aquele que realizar algum trabalho nesse dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3433, 'nvi', 'Levítico', 'lv', 23, 31, 'Vocês não realizarão trabalho algum. Este é um decreto perpétuo para as suas gerações, onde quer que vocês morarem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3434, 'nvi', 'Levítico', 'lv', 23, 32, 'É um sábado de descanso para vocês, e vocês se humilharão. Desde o entardecer do nono dia do mês até ao entardecer do dia seguinte vocês guardarão esse sábado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3435, 'nvi', 'Levítico', 'lv', 23, 33, 'Disse o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3436, 'nvi', 'Levítico', 'lv', 23, 34, '"Diga ainda aos israelitas: No décimo quinto dia deste sétimo mês começa a Festa das Cabanas do Senhor, que dura sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3437, 'nvi', 'Levítico', 'lv', 23, 35, 'No primeiro dia haverá reunião sagrada; não realizem trabalho algum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3438, 'nvi', 'Levítico', 'lv', 23, 36, 'Durante sete dias apresentem ao Senhor ofertas preparadas no fogo, e no oitavo dia façam outra reunião sagrada, e também apresentem ao Senhor uma oferta preparada no fogo. É reunião solene; não realizem trabalho algum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3439, 'nvi', 'Levítico', 'lv', 23, 37, '( Estas são as festas fixas do Senhor, que vocês proclamarão como reuniões sagradas para trazerem ao Senhor ofertas preparadas no fogo, holocaustos e ofertas de cereal, sacrifícios e ofertas derramadas exigidas para cada dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3440, 'nvi', 'Levítico', 'lv', 23, 38, 'Isso fora as do sábado do Senhor e fora as dádivas e votos de vocês, e todas as ofertas voluntárias que vocês derem ao Senhor. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3441, 'nvi', 'Levítico', 'lv', 23, 39, '"Assim, começando no décimo quinto dia do sétimo mês, depois de terem colhido o que a terra produziu, comemorem a festa do Senhor durante sete dias; o primeiro dia e também o oitavo serão dias de descanso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3442, 'nvi', 'Levítico', 'lv', 23, 40, 'No primeiro dia vocês apanharão os melhores frutos das árvores, folhagem de tamareira, galhos frondosos e salgueiros, e se alegrarão perante o Senhor, o Deus de vocês, durante sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3443, 'nvi', 'Levítico', 'lv', 23, 41, 'Comemorem essa festa do Senhor durante sete dias todos os anos. Este é um decreto perpétuo para as suas gerações; comemorem-na no sétimo mês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3444, 'nvi', 'Levítico', 'lv', 23, 42, 'Morem em tendas durante sete dias; todos os israelitas de nascimento morarão em tendas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3445, 'nvi', 'Levítico', 'lv', 23, 43, 'para que os descendentes de vocês saibam que eu fiz os israelitas morarem em tendas quando os tirei da terra do Egito. Eu sou o Senhor, o Deus de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3446, 'nvi', 'Levítico', 'lv', 23, 44, 'Assim anunciou Moisés aos israelitas as festas fixas do Senhor.');
