INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3447, 'nvi', 'Levítico', 'lv', 24, 1, 'Disse o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3448, 'nvi', 'Levítico', 'lv', 24, 2, '"Ordene aos israelitas que lhe tragam azeite puro de oliva batida para as lâmpadas, para que fiquem sempre acesas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3449, 'nvi', 'Levítico', 'lv', 24, 3, 'Na Tenda do Encontro, do lado de fora do véu que esconde as tábuas da aliança, Arão manterá as lâmpadas continuamente acesas diante do Senhor, desde o entardecer até a manhã seguinte. Este é um decreto perpétuo para as suas gerações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3450, 'nvi', 'Levítico', 'lv', 24, 4, 'Mantenha sempre em ordem as lâmpadas no candelabro de ouro puro perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3451, 'nvi', 'Levítico', 'lv', 24, 5, '"Apanhe da melhor farinha e asse doze pães, usando dois jarros para cada pão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3452, 'nvi', 'Levítico', 'lv', 24, 6, 'Coloque-os em duas fileiras, com seis pães em cada uma, sobre a mesa de ouro puro perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3453, 'nvi', 'Levítico', 'lv', 24, 7, 'Junto a cada fileira coloque um pouco de incenso puro como porção memorial para representar o pão e ser uma oferta ao Senhor preparada no fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3454, 'nvi', 'Levítico', 'lv', 24, 8, 'Esses pães serão colocados regularmente perante o Senhor, cada sábado, em nome dos israelitas, como aliança perpétua.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3455, 'nvi', 'Levítico', 'lv', 24, 9, 'Pertencem a Arão e a seus descendentes, que os comerão num lugar sagrado, porque é parte santíssima de sua porção regular das ofertas dedicadas ao Senhor, preparadas no fogo. É decreto perpétuo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3456, 'nvi', 'Levítico', 'lv', 24, 10, 'Aconteceu que o filho de uma israelita e de um egípcio saiu e foi para o meio dos israelitas. No acampamento houve uma briga entre ele e um israelita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3457, 'nvi', 'Levítico', 'lv', 24, 11, 'O filho da israelita blasfemou o Nome com uma maldição; então o levaram a Moisés. O nome de sua mãe era Selomite, filha de Dibri, da tribo de Dã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3458, 'nvi', 'Levítico', 'lv', 24, 12, 'Deixaram-no preso até que a vontade do Senhor lhes fosse declarada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3459, 'nvi', 'Levítico', 'lv', 24, 13, 'Então o Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3460, 'nvi', 'Levítico', 'lv', 24, 14, '"Leve o que blasfemou para fora do acampamento. Todos aqueles que o ouviram colocarão as mãos sobre a cabeça dele, e a comunidade toda o apedrejará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3461, 'nvi', 'Levítico', 'lv', 24, 15, 'Diga aos israelitas: Se alguém amaldiçoar seu Deus, será responsável pelo seu pecado;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3462, 'nvi', 'Levítico', 'lv', 24, 16, 'quem blasfemar o nome do Senhor terá que ser executado. A comunidade toda o apedrejará. Seja estrangeiro, seja natural da terra, se blasfemar o Nome, terá que ser morto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3463, 'nvi', 'Levítico', 'lv', 24, 17, '"Se alguém ferir uma pessoa a ponto de matá-la, terá que ser executado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3464, 'nvi', 'Levítico', 'lv', 24, 18, 'Quem matar um animal fará restituição: vida por vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3465, 'nvi', 'Levítico', 'lv', 24, 19, 'Se alguém ferir seu próximo, deixando-o defeituoso, assim como fez lhe será feito:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3466, 'nvi', 'Levítico', 'lv', 24, 20, 'fratura por fratura, olho por olho, dente por dente. Assim como feriu o outro, deixando-o defeituoso, assim também será ferido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3467, 'nvi', 'Levítico', 'lv', 24, 21, 'Quem matar um animal fará restituição, mas quem matar um homem será morto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3468, 'nvi', 'Levítico', 'lv', 24, 22, 'Vocês terão a mesma lei para o estrangeiro e para o natural. Eu sou o Senhor, o Deus de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3469, 'nvi', 'Levítico', 'lv', 24, 23, 'Depois que Moisés falou aos israelitas, levaram o que blasfemou para fora do acampamento e o apedrejaram. Os israelitas fizeram conforme o Senhor tinha ordenado a Moisés.');
