INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3571, 'nvi', 'Levítico', 'lv', 27, 1, 'Disse também o SENHOR a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3572, 'nvi', 'Levítico', 'lv', 27, 2, '"Diga o seguinte aos israelitas: Se alguém fizer um voto especial, dedicando pessoas ao Senhor, faça-o conforme o devido valor;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3573, 'nvi', 'Levítico', 'lv', 27, 3, 'atribua aos homens entre vinte e sessenta anos o valor de seiscentos gramas de prata, com base no peso padrão do santuário;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3574, 'nvi', 'Levítico', 'lv', 27, 4, 'e, se for mulher, atribua-lhe o valor de trezentos e sessenta gramas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3575, 'nvi', 'Levítico', 'lv', 27, 5, 'Se for alguém que tenha entre cinco e vinte anos, atribua aos homens o valor de duzentos e quarenta gramas e às mulheres o valor de cento e vinte gramas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3576, 'nvi', 'Levítico', 'lv', 27, 6, 'Se for alguém que tenha entre um mês e cinco anos de idade, atribua aos meninos o valor de sessenta gramas de prata e às meninas o valor de trinta e seis gramas de prata.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3577, 'nvi', 'Levítico', 'lv', 27, 7, 'Se for alguém que tenha de sessenta anos para cima, atribua aos homens o valor de cento e oitenta gramas e às mulheres o valor de cento e vinte gramas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3578, 'nvi', 'Levítico', 'lv', 27, 8, 'Se quem fizer o voto for pobre demais para pagar o valor especificado, apresentará a pessoa ao sacerdote, que estabelecerá o valor de acordo com as possibilidades do homem que fez o voto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3579, 'nvi', 'Levítico', 'lv', 27, 9, '"Se o que ele prometeu mediante voto for um animal aceitável como oferta ao Senhor, um animal assim dado ao Senhor torna-se santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3580, 'nvi', 'Levítico', 'lv', 27, 10, 'Ele não poderá trocá-lo nem substituir um animal ruim por um bom, nem um animal bom por um ruim; caso troque um animal por outro, tanto o substituto quanto o substituído se tornarão santos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3581, 'nvi', 'Levítico', 'lv', 27, 11, 'Se o que ele prometeu mediante voto for um animal impuro, não aceitável como oferta ao Senhor, o animal será apresentado ao sacerdote,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3582, 'nvi', 'Levítico', 'lv', 27, 12, 'que o avaliará por suas qualidades. A avaliação do sacerdote determinará o valor do animal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3583, 'nvi', 'Levítico', 'lv', 27, 13, 'Se o dono desejar resgatar o animal, terá que acrescentar um quinto ao seu valor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3584, 'nvi', 'Levítico', 'lv', 27, 14, '"Se um homem consagrar a sua casa ao Senhor, o sacerdote avaliará a casa por suas qualidades. A avaliação do sacerdote determinará o valor da casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3585, 'nvi', 'Levítico', 'lv', 27, 15, 'Se o homem que consagrar a sua casa quiser resgatá-la, terá que acrescentar um quinto ao seu valor, e a casa voltará a ser sua.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3586, 'nvi', 'Levítico', 'lv', 27, 16, '"Se um homem consagrar ao Senhor parte das terras da sua família, sua avaliação será de acordo com a semeadura: seiscentos gramas de prata para cada barril de semente de cevada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3587, 'nvi', 'Levítico', 'lv', 27, 17, 'Se consagrar a sua terra durante o ano do jubileu, o valor será integral.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3588, 'nvi', 'Levítico', 'lv', 27, 18, 'Mas, se a consagrar depois do Jubileu, o sacerdote calculará o valor de acordo com o número de anos que faltarem para o próximo ano do jubileu, e o valor será reduzido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3589, 'nvi', 'Levítico', 'lv', 27, 19, 'Se o homem que consagrar a sua terra desejar resgatá-la, terá que acrescentar um quinto ao seu valor, e a terra voltará a ser sua.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3590, 'nvi', 'Levítico', 'lv', 27, 20, 'Mas se não a resgatar, ou se a tiver vendido, não poderá mais ser resgatada;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3591, 'nvi', 'Levítico', 'lv', 27, 21, 'quando a terra for liberada no Jubileu, será santa, consagrada ao Senhor, e se tornará propriedade dos sacerdotes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3592, 'nvi', 'Levítico', 'lv', 27, 22, '"Se um homem consagrar ao Senhor terras que tenha comprado, terras que não fazem parte da propriedade da sua família,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3593, 'nvi', 'Levítico', 'lv', 27, 23, 'o sacerdote determinará o valor de acordo com o tempo que falta para o ano do jubileu; o homem pagará o valor no mesmo dia, consagrando-o ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3594, 'nvi', 'Levítico', 'lv', 27, 24, 'No ano do jubileu as terras serão devolvidas àquele de quem ele as comprou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3595, 'nvi', 'Levítico', 'lv', 27, 25, 'Todos os valores serão calculados com base no peso padrão do santuário, que são doze gramas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3596, 'nvi', 'Levítico', 'lv', 27, 26, '"Ninguém poderá consagrar a primeira cria de um animal, pois já pertence ao Senhor; seja cria de vaca, seja de cabra, seja de ovelha, pertence ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3597, 'nvi', 'Levítico', 'lv', 27, 27, 'Mas se for a cria de um animal impuro, poderá resgatá-la pelo valor estabelecido, acrescentando um quinto a esse valor. Se não for resgatada, será vendida pelo valor estabelecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3598, 'nvi', 'Levítico', 'lv', 27, 28, '"Todavia, nada que um homem possua e consagre ao Senhor, seja homem, seja animal, sejam terras de sua propriedade, poderá ser vendido ou resgatado; todas as coisas assim consagradas são santíssimas ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3599, 'nvi', 'Levítico', 'lv', 27, 29, '"Nenhuma pessoa consagrada para a destruição poderá ser resgatada; terá que ser executada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3600, 'nvi', 'Levítico', 'lv', 27, 30, '"Todos os dízimos da terra, seja dos cereais, seja das frutas das árvores, pertencem ao Senhor; são consagrados ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3601, 'nvi', 'Levítico', 'lv', 27, 31, 'Se um homem desejar resgatar parte do seu dízimo, terá que acrescentar um quinto ao seu valor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3602, 'nvi', 'Levítico', 'lv', 27, 32, 'O dízimo dos seus rebanhos, um de cada dez animais que passem debaixo da vara do pastor, será consagrado ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3603, 'nvi', 'Levítico', 'lv', 27, 33, 'O dono não poderá retirar os bons dentre os ruins, nem fazer qualquer troca. Se fizer alguma troca, tanto o animal quanto o substituto se tornarão consagrados e não poderão ser resgatados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3604, 'nvi', 'Levítico', 'lv', 27, 34, 'São esses os mandamentos que o Senhor ordenou a Moisés no monte Sinai para os israelitas.');
