INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2796, 'nvi', 'Levítico', 'lv', 4, 1, 'O Senhor ordenou a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2797, 'nvi', 'Levítico', 'lv', 4, 2, '"Diga aos israelitas: Quando alguém pecar sem intenção, fazendo o que é proibido em qualquer dos mandamentos do Senhor, assim se fará:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2798, 'nvi', 'Levítico', 'lv', 4, 3, '"Se for o sacerdote ungido que pecar, trazendo culpa sobre o povo, trará ao Senhor um novilho sem defeito como oferta pelo pecado que cometeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2799, 'nvi', 'Levítico', 'lv', 4, 4, 'Apresentará ao Senhor o novilho na entrada da Tenda do Encontro. Porá a mão sobre a cabeça do novilho que será morto perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2800, 'nvi', 'Levítico', 'lv', 4, 5, 'Então o sacerdote ungido pegará um pouco do sangue do novilho e o levará à Tenda do Encontro;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2801, 'nvi', 'Levítico', 'lv', 4, 6, 'molhará o dedo no sangue e o aspergirá sete vezes perante o Senhor, diante do véu do santuário.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2802, 'nvi', 'Levítico', 'lv', 4, 7, 'O sacerdote porá um pouco do sangue nas pontas do altar do incenso aromático que está perante o Senhor na Tenda do Encontro. Derramará todo o restante do sangue do novilho na base do altar do holocausto, na entrada da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2803, 'nvi', 'Levítico', 'lv', 4, 8, 'Então retirará toda a gordura do novilho da oferta pelo pecado: a gordura que cobre as vísceras e está ligada a elas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2804, 'nvi', 'Levítico', 'lv', 4, 9, 'os dois rins com a gordura que os cobre e que está perto dos lombos, e o lóbulo do fígado, que ele removerá junto com os rins,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2805, 'nvi', 'Levítico', 'lv', 4, 10, 'como se retira a gordura do boi sacrificado como oferta de comunhão. Então o sacerdote os queimará no altar dos holocaustos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2806, 'nvi', 'Levítico', 'lv', 4, 11, 'Mas o couro do novilho e toda a sua carne, bem como a cabeça e as pernas, as vísceras e os excrementos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2807, 'nvi', 'Levítico', 'lv', 4, 12, 'isto é, tudo o que restar do novilho, ele os levará para fora do acampamento, a um local cerimonialmente puro, onde se lançam as cinzas. Ali os queimará sobre a lenha de uma fogueira, sobre o monte de cinzas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2808, 'nvi', 'Levítico', 'lv', 4, 13, '"Se for toda a comunidade de Israel que pecar sem intenção, fazendo o que é proibido em qualquer dos mandamentos do Senhor, ainda que não tenha consciência disso, a comunidade será culpada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2809, 'nvi', 'Levítico', 'lv', 4, 14, 'Quando tiver consciência do pecado que cometeu, a comunidade trará um novilho como oferta pelo pecado e o apresentará diante da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2810, 'nvi', 'Levítico', 'lv', 4, 15, 'As autoridades da comunidade porão as mãos sobre a cabeça do novilho perante o Senhor. E o novilho será morto perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2811, 'nvi', 'Levítico', 'lv', 4, 16, 'Então o sacerdote ungido levará um pouco do sangue do novilho para a Tenda do Encontro;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2812, 'nvi', 'Levítico', 'lv', 4, 17, 'molhará o dedo no sangue e o aspergirá sete vezes perante o Senhor, diante do véu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2813, 'nvi', 'Levítico', 'lv', 4, 18, 'Porá o sangue nas pontas do altar que está perante o Senhor na Tenda do Encontro e derramará todo o restante do sangue na base do altar dos holocaustos, na entrada da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2814, 'nvi', 'Levítico', 'lv', 4, 19, 'Então retirará toda a gordura do animal e a queimará no altar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2815, 'nvi', 'Levítico', 'lv', 4, 20, 'e fará com este novilho como fez com o novilho da oferta pelo pecado. Assim o sacerdote fará propiciação por eles, e eles serão perdoados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2816, 'nvi', 'Levítico', 'lv', 4, 21, 'Depois levará o novilho para fora do acampamento e o queimará como queimou o primeiro. É oferta pelo pecado da comunidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2817, 'nvi', 'Levítico', 'lv', 4, 22, '"Quando for um líder que pecar sem intenção, fazendo o que é proibido em qualquer dos mandamentos do Senhor seu Deus, será culpado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2818, 'nvi', 'Levítico', 'lv', 4, 23, 'Quando o conscientizarem do seu pecado, trará como oferta um bode sem defeito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2819, 'nvi', 'Levítico', 'lv', 4, 24, 'Porá a mão sobre a cabeça do bode, que será morto no local onde o holocausto é sacrificado, perante o Senhor. Esta é a oferta pelo pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2820, 'nvi', 'Levítico', 'lv', 4, 25, 'Então o sacerdote pegará com o dedo um pouco do sangue da oferta pelo pecado, o porá nas pontas do altar dos holocaustos e derramará o restante do sangue na base do altar do holocausto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2821, 'nvi', 'Levítico', 'lv', 4, 26, 'Queimará toda a gordura no altar, como queimou a gordura do sacrifício de comunhão. Assim o sacerdote fará propiciação pelo pecado do líder, e ele será perdoado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2822, 'nvi', 'Levítico', 'lv', 4, 27, '"Se for alguém da comunidade que pecar sem intenção, fazendo o que é proibido em qualquer dos mandamentos do Senhor seu Deus, será culpado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2823, 'nvi', 'Levítico', 'lv', 4, 28, 'Quando o conscientizarem do seu pecado, trará como oferta pelo pecado que cometeu uma cabra sem defeito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2824, 'nvi', 'Levítico', 'lv', 4, 29, 'Porá a mão sobre a cabeça do animal da oferta pelo pecado, que será morto no lugar dos holocaustos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2825, 'nvi', 'Levítico', 'lv', 4, 30, 'Então o sacerdote pegará com o dedo um pouco do sangue, o porá nas pontas do altar dos holocaustos e derramará o restante do sangue na base do altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2826, 'nvi', 'Levítico', 'lv', 4, 31, 'Então retirará toda a gordura, como se retira a gordura do sacrifício de comunhão; o sacerdote a queimará no altar como aroma agradável ao Senhor. Assim o sacerdote fará propiciação por esse homem, e ele será perdoado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2827, 'nvi', 'Levítico', 'lv', 4, 32, '"Se trouxer uma ovelha como oferta pelo pecado, terá que ser sem defeito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2828, 'nvi', 'Levítico', 'lv', 4, 33, 'Porá a mão sobre a cabeça do animal, que será morto como oferta pelo pecado no lugar onde é sacrificado o holocausto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2829, 'nvi', 'Levítico', 'lv', 4, 34, 'Então o sacerdote pegará com o dedo um pouco do sangue da oferta pelo pecado e o porá nas pontas do altar dos holocaustos, e derramará o restante do sangue na base do altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2830, 'nvi', 'Levítico', 'lv', 4, 35, 'Retirará toda a gordura, como se retira a gordura do cordeiro do sacrifício de comunhão; o sacerdote a queimará no altar, em cima das ofertas dedicadas ao Senhor, preparadas no fogo. Assim o sacerdote fará em favor dele propiciação pelo pecado que cometeu, e ele será perdoado".');
