INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2850, 'nvi', 'Levítico', 'lv', 6, 1, 'E disse o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2851, 'nvi', 'Levítico', 'lv', 6, 2, '"Se alguém pecar, cometendo uma ofensa contra o Senhor, enganando o seu próximo no que diz respeito a algo que lhe foi confiado ou deixado como penhor ou roubado, ou se lhe extorquir algo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2852, 'nvi', 'Levítico', 'lv', 6, 3, 'ou se achar algum bem perdido e mentir a respeito disso, ou se jurar falsamente a respeito de qualquer coisa, cometendo pecado;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2853, 'nvi', 'Levítico', 'lv', 6, 4, 'quando assim pecar, tornando-se por isso culpado, terá que devolver o que roubou ou tomou mediante extorsão, ou o que lhe foi confiado, ou os bens perdidos que achou,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2854, 'nvi', 'Levítico', 'lv', 6, 5, 'ou qualquer coisa sobre a qual tenha jurado falsamente. Fará restituição plena, acrescentará a isso um quinto do valor e dará tudo ao proprietário no dia em que apresentar a sua oferta pela culpa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2855, 'nvi', 'Levítico', 'lv', 6, 6, 'E por sua culpa trará ao sacerdote uma oferta dedicada ao Senhor: um carneiro do rebanho, sem defeito e devidamente avaliado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2856, 'nvi', 'Levítico', 'lv', 6, 7, 'Dessa forma o sacerdote fará propiciação por ele perante o Senhor, e ele será perdoado de qualquer dessas coisas que fez e que o tornou culpado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2857, 'nvi', 'Levítico', 'lv', 6, 8, 'O Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2858, 'nvi', 'Levítico', 'lv', 6, 9, '"Dê este mandamento a Arão e a seus filhos: Esta é a regulamentação acerca do holocausto: ele terá que ficar queimando até de manhã sobre as brasas do altar, onde o fogo terá que ser mantido aceso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2859, 'nvi', 'Levítico', 'lv', 6, 10, 'O sacerdote vestirá suas roupas de linho e os calções de linho por baixo, retirará as cinzas do holocausto que o fogo consumiu no altar e as colocará ao lado do altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2860, 'nvi', 'Levítico', 'lv', 6, 11, 'Depois trocará de roupa, e levará as cinzas para fora do acampamento, a um lugar cerimonialmente puro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2861, 'nvi', 'Levítico', 'lv', 6, 12, 'Mantenha-se aceso o fogo no altar; não deve ser apagado. Toda manhã o sacerdote acrescentará lenha, arrumará o holocausto sobre o fogo e queimará sobre ele a gordura das ofertas de comunhão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2862, 'nvi', 'Levítico', 'lv', 6, 13, 'Mantenha-se o fogo continuamente aceso no altar; não deve ser apagado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2863, 'nvi', 'Levítico', 'lv', 6, 14, '"Esta é a regulamentação da oferta de cereal: os filhos de Arão a apresentarão ao Senhor, em frente do altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2864, 'nvi', 'Levítico', 'lv', 6, 15, 'O sacerdote apanhará um punhado da melhor farinha com óleo, juntamente com todo o incenso que está sobre a oferta de cereal, e queimará no altar a porção memorial como aroma agradável ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2865, 'nvi', 'Levítico', 'lv', 6, 16, 'Arão e seus filhos comerão o restante da oferta, mas deverão comê-lo sem fermento e em lugar sagrado, no pátio da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2866, 'nvi', 'Levítico', 'lv', 6, 17, 'Essa oferta não será assada com fermento; eu a dei a eles como porção das ofertas feitas a mim com fogo. É santíssima, como a oferta pelo pecado e como a oferta pela culpa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2867, 'nvi', 'Levítico', 'lv', 6, 18, 'Somente os homens descendentes de Arão poderão comer da porção das ofertas dedicadas ao Senhor, preparadas no fogo. É um decreto perpétuo para as suas gerações. Tudo o que nelas tocar se tornará santo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2868, 'nvi', 'Levítico', 'lv', 6, 19, 'O Senhor disse também a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2869, 'nvi', 'Levítico', 'lv', 6, 20, '"Esta é a oferta que Arão e os seus descendentes terão que trazer ao Senhor no dia em que ele for ungido: um jarro da melhor farinha, como na oferta regular de cereal, metade pela manhã e metade à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2870, 'nvi', 'Levítico', 'lv', 6, 21, 'Prepare-a com óleo numa assadeira; traga-a bem misturada e apresente a oferta de cereal partida em pedaços, como aroma agradável ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2871, 'nvi', 'Levítico', 'lv', 6, 22, 'Todo sacerdote ungido, descendentes de Arão, também preparará essa oferta. É a porção do Senhor por decreto perpétuo e será totalmente queimada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2872, 'nvi', 'Levítico', 'lv', 6, 23, 'Toda oferta de cereal do sacerdote será totalmente queimada; não será comida".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2873, 'nvi', 'Levítico', 'lv', 6, 24, 'O Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2874, 'nvi', 'Levítico', 'lv', 6, 25, '"Diga a Arão e aos seus filhos: Esta é a regulamentação da oferta pelo pecado: O animal da oferta pelo pecado será morto perante o Senhor no local onde é sacrificado o holocausto; é uma oferta santíssima.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2875, 'nvi', 'Levítico', 'lv', 6, 26, 'O sacerdote que oferecer o animal, o comerá em lugar sagrado, no pátio da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2876, 'nvi', 'Levítico', 'lv', 6, 27, 'Tudo o que tocar na carne se tornará santo, e se o sangue respingar na roupa, será lavada em lugar sagrado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2877, 'nvi', 'Levítico', 'lv', 6, 28, 'A vasilha de barro em que a carne for cozida deverá ser quebrada; mas, se for cozida numa vasilha de bronze, a vasilha deverá ser esfregada e enxaguada com água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2878, 'nvi', 'Levítico', 'lv', 6, 29, 'Somente os homens da família dos sacerdotes poderão comê-la; é uma oferta santíssima.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2879, 'nvi', 'Levítico', 'lv', 6, 30, 'Mas toda oferta pelo pecado, cujo sangue for trazido para a Tenda do Encontro para propiciação no Lugar Santo, não será comida; terá que ser queimada".');
