INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2880, 'nvi', 'Levítico', 'lv', 7, 1, '"Esta é a regulamentação da oferta pela culpa, que é oferta santíssima:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2881, 'nvi', 'Levítico', 'lv', 7, 2, 'O animal da oferta pela culpa será morto no local onde são sacrificados os holocaustos, e seu sangue será derramado nos lados do altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2882, 'nvi', 'Levítico', 'lv', 7, 3, 'Toda a sua gordura será oferecida: a parte gorda da cauda e a gordura que cobre as vísceras,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2883, 'nvi', 'Levítico', 'lv', 7, 4, 'os dois rins com a gordura que os cobre e que está perto dos lombos, e o lóbulo do fígado, que será removido juntamente com os rins.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2884, 'nvi', 'Levítico', 'lv', 7, 5, 'O sacerdote os queimará no altar como oferta dedicada ao Senhor, preparada no fogo. É oferta pela culpa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2885, 'nvi', 'Levítico', 'lv', 7, 6, 'Somente os homens da família dos sacerdotes poderão comê-la, mas deve ser comida em lugar sagrado; é oferta santíssima.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2886, 'nvi', 'Levítico', 'lv', 7, 7, '"A mesma regulamentação aplica-se tanto à oferta pelo pecado quanto à oferta pela culpa: a carne pertence ao sacerdote que faz propiciação pela culpa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2887, 'nvi', 'Levítico', 'lv', 7, 8, 'O sacerdote que oferecer um holocausto por alguém ficará com o couro do animal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2888, 'nvi', 'Levítico', 'lv', 7, 9, 'Toda oferta de cereal, assada num forno ou cozida numa panela ou numa assadeira, pertence ao sacerdote que a oferecer,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2889, 'nvi', 'Levítico', 'lv', 7, 10, 'e toda oferta de cereal, amassada com óleo ou não, pertence igualmente aos descendentes de Arão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2890, 'nvi', 'Levítico', 'lv', 7, 11, '"Esta é a regulamentação da oferta de comunhão que pode ser apresentada ao Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2891, 'nvi', 'Levítico', 'lv', 7, 12, '"Se alguém a fizer por gratidão, então, junto com sua oferta de gratidão, terá que oferecer bolos sem fermento e amassados com óleo, pães finos sem fermento e untados com óleo, e bolos da melhor farinha bem amassados e misturados com óleo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2892, 'nvi', 'Levítico', 'lv', 7, 13, 'Juntamente com sua oferta de comunhão por gratidão, apresentará uma oferta que inclua bolos com fermento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2893, 'nvi', 'Levítico', 'lv', 7, 14, 'De cada oferta trará uma contribuição ao Senhor que será dada ao sacerdote que asperge o sangue das ofertas de comunhão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2894, 'nvi', 'Levítico', 'lv', 7, 15, 'A carne da sua oferta de comunhão por gratidão será comida no dia em que for oferecida; nada poderá sobrar até o amanhecer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2895, 'nvi', 'Levítico', 'lv', 7, 16, '"Se, contudo, sua oferta for resultado de um voto ou for uma oferta voluntária, a carne do sacrifício será comida no dia em que for oferecida, e o que sobrar poderá ser comido no dia seguinte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2896, 'nvi', 'Levítico', 'lv', 7, 17, 'Mas a carne que sobrar do sacrifício até o terceiro dia será queimada no fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2897, 'nvi', 'Levítico', 'lv', 7, 18, 'Se a carne da oferta de comunhão for comida ao terceiro dia, ela não será aceita. A oferta não será atribuída àquele que a ofereceu, pois a carne estará estragada; e quem dela comer sofrerá as conseqüências da sua iniqüidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2898, 'nvi', 'Levítico', 'lv', 7, 19, '"A carne que tocar em qualquer coisa impura não será comida; será queimada no fogo. A carne do sacrifício, porém, poderá ser comida por quem estiver puro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2899, 'nvi', 'Levítico', 'lv', 7, 20, 'Mas se alguém que está impuro comer da carne da oferta de comunhão que pertence ao Senhor, será eliminado do meio do seu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2900, 'nvi', 'Levítico', 'lv', 7, 21, 'Se alguém tocar em alguma coisa impura, seja impureza humana, seja de animal, seja qualquer outra coisa impura e proibida, e comer da carne da oferta de comunhão que pertence ao Senhor, será eliminado do meio do seu povo. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2901, 'nvi', 'Levítico', 'lv', 7, 22, 'E disse o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2902, 'nvi', 'Levítico', 'lv', 7, 23, '"Diga aos israelitas: Não comam gordura alguma de boi, carneiro ou cabrito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2903, 'nvi', 'Levítico', 'lv', 7, 24, 'A gordura de um animal encontrado morto ou despedaçado por animais selvagens pode ser usada para qualquer outra finalidade, mas nunca poderá ser comida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2904, 'nvi', 'Levítico', 'lv', 7, 25, 'Quem comer a gordura de um animal, dedicado ao Senhor numa oferta preparada no fogo, será eliminado do meio do seu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2905, 'nvi', 'Levítico', 'lv', 7, 26, 'Onde quer que vocês vivam, não comam o sangue de nenhuma ave nem de animal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2906, 'nvi', 'Levítico', 'lv', 7, 27, 'Quem comer sangue será eliminado do meio do seu povo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2907, 'nvi', 'Levítico', 'lv', 7, 28, 'Disse mais o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2908, 'nvi', 'Levítico', 'lv', 7, 29, '"Diga aos israelitas: Todo aquele que trouxer sacrifício de comunhão ao Senhor terá que dedicar parte dele ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2909, 'nvi', 'Levítico', 'lv', 7, 30, 'Com suas próprias mãos trará ao Senhor as ofertas preparadas no fogo; trará a gordura juntamente com o peito, e o moverá perante o Senhor como gesto ritual de apresentação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2910, 'nvi', 'Levítico', 'lv', 7, 31, 'O sacerdote queimará a gordura no altar, mas o peito pertence a Arão e a seus descendentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2911, 'nvi', 'Levítico', 'lv', 7, 32, 'Vocês deverão dar a coxa direita das ofertas de comunhão ao sacerdote como contribuição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2912, 'nvi', 'Levítico', 'lv', 7, 33, 'O descendente de Arão que oferecer o sangue e a gordura da oferta de comunhão receberá a coxa direita como porção.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2913, 'nvi', 'Levítico', 'lv', 7, 34, 'Das ofertas de comunhão dos israelitas, tomei o peito que é movido ritualmente e a coxa que é ofertada, e os dei ao sacerdote Arão e a seus descendentes por decreto perpétuo para os israelitas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2914, 'nvi', 'Levítico', 'lv', 7, 35, 'Essa é a parte das ofertas dedicadas ao Senhor, preparadas no fogo, designada a Arão e a seus filhos no dia em que foram apresentados para servirem ao Senhor como sacerdotes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2915, 'nvi', 'Levítico', 'lv', 7, 36, 'Foi isso que o Senhor ordenou dar a eles, no dia em que foram ungidos dentre os israelitas. É um decreto perpétuo para as suas gerações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2916, 'nvi', 'Levítico', 'lv', 7, 37, 'Essa é a regulamentação acerca do holocausto, da oferta de cereal, da oferta pelo pecado, da oferta pela culpa, da oferta de ordenação e da oferta de comunhão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2917, 'nvi', 'Levítico', 'lv', 7, 38, 'O Senhor entregou-a a Moisés no monte Sinai, no dia em que ordenou aos israelitas que trouxessem suas ofertas ao Senhor, no deserto do Sinai.');
