INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2918, 'nvi', 'Levítico', 'lv', 8, 1, 'O Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2919, 'nvi', 'Levítico', 'lv', 8, 2, '"Traga Arão e seus filhos, suas vestes, o óleo da unção, o novilho para a oferta pelo pecado, os dois carneiros e o cesto de pães sem fermento;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2920, 'nvi', 'Levítico', 'lv', 8, 3, 'e reúna toda a comunidade à entrada da Tenda do Encontro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2921, 'nvi', 'Levítico', 'lv', 8, 4, 'Moisés fez como o Senhor lhe ordenou, e a comunidade reuniu-se à entrada da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2922, 'nvi', 'Levítico', 'lv', 8, 5, 'Então Moisés disse à comunidade: "Foi isto que o Senhor mandou fazer";');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2923, 'nvi', 'Levítico', 'lv', 8, 6, 'e levou Arão e seus filhos à frente e mandou-os banhar-se com água;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2924, 'nvi', 'Levítico', 'lv', 8, 7, 'pôs a túnica em Arão, colocou-lhe o cinto e o manto, e pôs sobre ele o colete sacerdotal; depois a ele prendeu o manto sacerdotal com o cinturão;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2925, 'nvi', 'Levítico', 'lv', 8, 8, 'colocou também o peitoral, e nele pôs o Urim e o Tumim;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2926, 'nvi', 'Levítico', 'lv', 8, 9, 'e colocou o turbante na cabeça de Arão com a lâmina de ouro, isto é, a coroa sagrada, na frente do turbante, conforme o Senhor tinha ordenado a Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2927, 'nvi', 'Levítico', 'lv', 8, 10, 'Depois Moisés pegou o óleo da unção e ungiu o tabernáculo e tudo o que nele havia, e assim os consagrou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2928, 'nvi', 'Levítico', 'lv', 8, 11, 'Aspergiu sete vezes o óleo sobre o altar, ungindo o altar e todos os seus utensílios e a bacia com o seu suporte, para consagrá-los.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2929, 'nvi', 'Levítico', 'lv', 8, 12, 'Derramou o óleo da unção sobre a cabeça de Arão para ungi-lo e consagrá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2930, 'nvi', 'Levítico', 'lv', 8, 13, 'Trouxe então os filhos de Arão à frente, vestiu-os com suas túnicas e cintos, e colocou-lhes gorros, conforme o Senhor lhe havia ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2931, 'nvi', 'Levítico', 'lv', 8, 14, 'Em seguida trouxe o novilho para a oferta pelo pecado, e Arão e seus filhos puseram-lhe as mãos sobre a cabeça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2932, 'nvi', 'Levítico', 'lv', 8, 15, 'Moisés sacrificou o novilho, e com o dedo pôs um pouco do sangue em todas as pontas do altar para purificá-lo. Derramou o restante do sangue na base do altar e assim o consagrou para fazer propiciação por ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2933, 'nvi', 'Levítico', 'lv', 8, 16, 'Moisés pegou também toda a gordura que cobre as vísceras, o lóbulo do fígado e os dois rins com a gordura que os cobre, e os queimou no altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2934, 'nvi', 'Levítico', 'lv', 8, 17, 'Mas o novilho com o seu couro, a sua carne e o seu excremento, ele os queimou fora do acampamento, conforme o Senhor lhe havia ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2935, 'nvi', 'Levítico', 'lv', 8, 18, 'Mandou trazer então o carneiro para o holocausto, e Arão e seus filhos puseram as mãos sobre a cabeça do carneiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2936, 'nvi', 'Levítico', 'lv', 8, 19, 'A seguir Moisés sacrificou o carneiro e derramou o sangue nos lados do altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2937, 'nvi', 'Levítico', 'lv', 8, 20, 'Depois, cortou o carneiro em pedaços; queimou a cabeça, os pedaços e a gordura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2938, 'nvi', 'Levítico', 'lv', 8, 21, 'Lavou as vísceras e as pernas, e queimou o carneiro inteiro sobre o altar, como holocausto, oferta de aroma agradável ao Senhor, preparado no fogo, conforme o Senhor lhe havia ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2939, 'nvi', 'Levítico', 'lv', 8, 22, 'Depois mandou trazer o outro carneiro, o carneiro para a oferta de ordenação, e Arão e seus filhos colocaram as mãos sobre a cabeça do carneiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2940, 'nvi', 'Levítico', 'lv', 8, 23, 'Moisés sacrificou o carneiro e pôs um pouco do sangue na ponta da orelha direita de Arão, no polegar da sua mão direita e no polegar do seu pé direito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2941, 'nvi', 'Levítico', 'lv', 8, 24, 'Moisés também mandou que os filhos de Arão se aproximassem, e sobre cada um pôs um pouco do sangue na ponta da orelha direita, no polegar da mão direita e no polegar do pé direito; e derramou o restante do sangue nos lados do altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2942, 'nvi', 'Levítico', 'lv', 8, 25, 'Apanhou a gordura, a cauda gorda, toda a gordura que cobre as vísceras, o lóbulo do fígado, os dois rins e a gordura que os cobre e a coxa direita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2943, 'nvi', 'Levítico', 'lv', 8, 26, 'Então, do cesto de pães sem fermento, que estava perante o Senhor, apanhou um pão comum, outro feito com óleo e um pão fino, colocando-os sobre as porções de gordura e sobre a coxa direita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2944, 'nvi', 'Levítico', 'lv', 8, 27, 'Pôs tudo nas mãos de Arão e de seus filhos e os moveu perante o Senhor como gesto ritual de apresentação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2945, 'nvi', 'Levítico', 'lv', 8, 28, 'Depois Moisés pegou de volta das mãos deles e queimou tudo no altar, em cima do holocausto, como uma oferta de ordenação, preparada no fogo, de aroma agradável ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2946, 'nvi', 'Levítico', 'lv', 8, 29, 'Moisés também pegou o peito e a sua própria porção do carneiro da ordenação, e o moveu perante o Senhor como gesto ritual de apresentação, como o Senhor lhe havia ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2947, 'nvi', 'Levítico', 'lv', 8, 30, 'Então pegou um pouco do óleo da unção e um pouco do sangue que estava no altar e os aspergiu sobre Arão e suas vestes, bem como sobre seus filhos e suas vestes. Assim consagrou Arão e suas vestes, e seus filhos e suas vestes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2948, 'nvi', 'Levítico', 'lv', 8, 31, 'Moisés então disse a Arão e a seus filhos: "Cozinhem a carne na entrada da Tenda do Encontro, onde a deverão comer com o pão do cesto das ofertas de ordenação, conforme me foi ordenado: ‘Arão e seus filhos deverão comê-la’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2949, 'nvi', 'Levítico', 'lv', 8, 32, 'Depois queimem o restante da carne e do pão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2950, 'nvi', 'Levítico', 'lv', 8, 33, 'Não saiam da entrada da Tenda do Encontro por sete dias, até que se completem os dias da ordenação de vocês, pois essa cerimônia de ordenação durará sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2951, 'nvi', 'Levítico', 'lv', 8, 34, 'O que se fez hoje foi ordenado pelo Senhor para fazer propiciação por vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2952, 'nvi', 'Levítico', 'lv', 8, 35, 'Vocês terão que permanecer dia e noite à entrada da Tenda do Encontro por sete dias e obedecer às exigências do Senhor, para que não morram; pois isso me foi ordenado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2953, 'nvi', 'Levítico', 'lv', 8, 36, 'Arão e seus filhos fizeram tudo o que o Senhor tinha ordenado por meio de Moisés.');
