INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2954, 'nvi', 'Levítico', 'lv', 9, 1, 'Oito dias depois Moisés convocou Arão e seus filhos e as autoridades de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2955, 'nvi', 'Levítico', 'lv', 9, 2, 'E disse a Arão: "Traga um bezerro para a oferta pelo pecado e um carneiro para o holocausto, ambos sem defeito, e apresente-os ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2956, 'nvi', 'Levítico', 'lv', 9, 3, 'Depois diga aos israelitas: Tragam um bode para oferta pelo pecado, um bezerro e um cordeiro, ambos de um ano de idade e sem defeito, para holocausto;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2957, 'nvi', 'Levítico', 'lv', 9, 4, 'e um boi e um carneiro para oferta de comunhão, para os sacrificar perante o Senhor, juntamente com a oferta de cereal amassada com óleo; pois hoje o Senhor aparecerá a vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2958, 'nvi', 'Levítico', 'lv', 9, 5, 'Levaram então tudo o que Moisés determinou para a frente da Tenda do Encontro, e a comunidade inteira aproximou-se e ficou de pé perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2959, 'nvi', 'Levítico', 'lv', 9, 6, 'Disse-lhes Moisés: "Foi isso que o Senhor ordenou que façam, para que a glória do Senhor apareça a vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2960, 'nvi', 'Levítico', 'lv', 9, 7, 'Disse Moisés a Arão: "Venha até o altar e ofereça o seu sacrifício pelo pecado e o seu holocausto, e faça propiciação por você mesmo e pelo povo; ofereça o sacrifício pelo povo e faça propiciação por ele, conforme o Senhor ordenou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2961, 'nvi', 'Levítico', 'lv', 9, 8, 'Arão foi até o altar e ofereceu o bezerro como sacrifício pelo pecado por si mesmo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2962, 'nvi', 'Levítico', 'lv', 9, 9, 'Seus filhos levaram-lhe o sangue, e ele molhou o dedo no sangue e o pôs nas pontas do altar; então derramou o restante do sangue na base do altar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2963, 'nvi', 'Levítico', 'lv', 9, 10, 'onde queimou a gordura, os rins e o lóbulo do fígado da oferta pelo pecado, conforme o Senhor tinha ordenado a Moisés;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2964, 'nvi', 'Levítico', 'lv', 9, 11, 'a carne e o couro, porém, queimou fora do acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2965, 'nvi', 'Levítico', 'lv', 9, 12, 'Depois sacrificou o holocausto. Seus filhos lhe entregaram o sangue, e ele o derramou nos lados do altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2966, 'nvi', 'Levítico', 'lv', 9, 13, 'Entregaram-lhe em seguida o holocausto pedaço por pedaço, inclusive a cabeça, e ele os queimou no altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2967, 'nvi', 'Levítico', 'lv', 9, 14, 'Lavou as vísceras e as pernas e as queimou em cima do holocausto sobre o altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2968, 'nvi', 'Levítico', 'lv', 9, 15, 'Depois Arão apresentou a oferta pelo povo. Pegou o bode para a oferta pelo pecado do povo e o ofereceu como sacrifício pelo pecado, como fizera com o primeiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2969, 'nvi', 'Levítico', 'lv', 9, 16, 'Apresentou o holocausto e ofereceu-o conforme fora prescrito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2970, 'nvi', 'Levítico', 'lv', 9, 17, 'Também apresentou a oferta de cereal, pegou um punhado dela e a queimou no altar, além do holocausto da manhã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2971, 'nvi', 'Levítico', 'lv', 9, 18, 'Matou o boi e o carneiro como sacrifício de comunhão pelo povo. Seus filhos levaram-lhe o sangue, e ele o derramou nos lados do altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2972, 'nvi', 'Levítico', 'lv', 9, 19, 'Mas as porções de gordura do boi e do carneiro, a cauda gorda, a gordura que cobre as vísceras, os rins e o lóbulo do fígado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2973, 'nvi', 'Levítico', 'lv', 9, 20, 'puseram em cima do peito; e Arão as queimou no altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2974, 'nvi', 'Levítico', 'lv', 9, 21, 'Em seguida, Arão moveu o peito e a coxa direita do animal perante o Senhor como gesto ritual de apresentação, conforme Moisés tinha ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2975, 'nvi', 'Levítico', 'lv', 9, 22, 'Depois Arão ergueu as mãos em direção ao povo e o abençoou. E, tendo oferecido o sacrifício pelo pecado, o holocausto e o sacrifício de comunhão, desceu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2976, 'nvi', 'Levítico', 'lv', 9, 23, 'Assim Moisés e Arão entraram na Tenda do Encontro. Quando saíram, abençoaram o povo; e a glória do Senhor apareceu a todo o povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2977, 'nvi', 'Levítico', 'lv', 9, 24, 'Saiu fogo da presença do Senhor e consumiu o holocausto e as porções de gordura sobre o altar. E, quando todo o povo viu isso, gritou de alegria e prostrou-se rosto em terra.');
