INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24217, 'nvi', 'Marcos', 'mc', 1, 1, 'Princípio do evangelho de Jesus Cristo, o Filho de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24218, 'nvi', 'Marcos', 'mc', 1, 2, 'Conforme está escrito no profeta Isaías: "Enviarei à tua frente o meu mensageiro; ele preparará o teu caminho"—');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24219, 'nvi', 'Marcos', 'mc', 1, 3, '"voz do que clama no deserto: ‘Preparem o caminho para o Senhor, façam veredas retas para ele’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24220, 'nvi', 'Marcos', 'mc', 1, 4, 'Assim surgiu João, batizando no deserto e pregando um batismo de arrependimento para o perdão dos pecados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24221, 'nvi', 'Marcos', 'mc', 1, 5, 'A ele vinha toda a região da Judéia e todo o povo de Jerusalém. Confessando os seus pecados, eram batizados por ele no rio Jordão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24222, 'nvi', 'Marcos', 'mc', 1, 6, 'João vestia roupas feitas de pêlos de camelo, usava um cinto de couro e comia gafanhotos e mel silvestre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24223, 'nvi', 'Marcos', 'mc', 1, 7, 'E esta era a sua mensagem: "Depois de mim vem alguém mais poderoso do que eu, tanto que não sou digno nem de curvar-me e desamarrar as correias das suas sandálias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24224, 'nvi', 'Marcos', 'mc', 1, 8, 'Eu os batizo com água, mas ele os batizará com o Espírito Santo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24225, 'nvi', 'Marcos', 'mc', 1, 9, 'Naquela ocasião Jesus veio de Nazaré da Galiléia e foi batizado por João no Jordão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24226, 'nvi', 'Marcos', 'mc', 1, 10, 'Assim que saiu da água, Jesus viu os céus se abrindo, e o Espírito descendo como pomba sobre ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24227, 'nvi', 'Marcos', 'mc', 1, 11, 'Então veio dos céus uma voz: "Tu és o meu Filho amado; em ti me agrado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24228, 'nvi', 'Marcos', 'mc', 1, 12, 'Logo após, o Espírito o impeliu para o deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24229, 'nvi', 'Marcos', 'mc', 1, 13, 'Ali esteve quarenta dias, sendo tentado por Satanás. Estava com os animais selvagens, e os anjos o serviam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24230, 'nvi', 'Marcos', 'mc', 1, 14, 'Depois que João foi preso, Jesus foi para a Galiléia, proclamando as boas novas de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24231, 'nvi', 'Marcos', 'mc', 1, 15, '"O tempo é chegado", dizia ele. "O Reino de Deus está próximo. Arrependam-se e creiam nas boas novas! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24232, 'nvi', 'Marcos', 'mc', 1, 16, 'Andando à beira do mar da Galiléia, Jesus viu Simão e seu irmão André lançando redes ao mar, pois eram pescadores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24233, 'nvi', 'Marcos', 'mc', 1, 17, 'E disse Jesus: "Sigam-me, e eu os farei pescadores de homens".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24234, 'nvi', 'Marcos', 'mc', 1, 18, 'No mesmo instante eles deixaram as suas redes e o seguiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24235, 'nvi', 'Marcos', 'mc', 1, 19, 'Indo um pouco mais adiante, viu num barco Tiago, filho de Zebedeu, e João, seu irmão, preparando as suas redes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24236, 'nvi', 'Marcos', 'mc', 1, 20, 'Logo os chamou, e eles o seguiram, deixando Zebedeu, seu pai, com os empregados no barco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24237, 'nvi', 'Marcos', 'mc', 1, 21, 'Eles foram para Cafarnaum e, assim que chegou o sábado, Jesus entrou na sinagoga e começou a ensinar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24238, 'nvi', 'Marcos', 'mc', 1, 22, 'Todos ficavam maravilhados com o seu ensino, porque lhes ensinava como alguém que tem autoridade e não como os mestres da lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24239, 'nvi', 'Marcos', 'mc', 1, 23, 'Justamente naquela hora, na sinagoga, um homem possesso de um espírito imundo gritou:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24240, 'nvi', 'Marcos', 'mc', 1, 24, '"O que queres conosco, Jesus de Nazaré? Vieste para nos destruir? Sei quem tu és: o Santo de Deus! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24241, 'nvi', 'Marcos', 'mc', 1, 25, '"Cale-se e saia dele! ", repreendeu-o Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24242, 'nvi', 'Marcos', 'mc', 1, 26, 'O espírito imundo sacudiu o homem violentamente e saiu dele gritando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24243, 'nvi', 'Marcos', 'mc', 1, 27, 'Todos ficaram tão admirados que perguntavam uns aos outros: "O que é isto? Um novo ensino — e com autoridade! Até aos espíritos imundos ele dá ordens, e eles lhe obedecem! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24244, 'nvi', 'Marcos', 'mc', 1, 28, 'As notícias a seu respeito se espalharam rapidamente por toda a região da Galiléia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24245, 'nvi', 'Marcos', 'mc', 1, 29, 'Logo que saíram da sinagoga, foram com Tiago e João à casa de Simão e André.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24246, 'nvi', 'Marcos', 'mc', 1, 30, 'A sogra de Simão estava de cama, com febre, e falaram a respeito dela a Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24247, 'nvi', 'Marcos', 'mc', 1, 31, 'Então ele se aproximou dela, tomou-a pela mão e ajudou-a a levantar-se. A febre a deixou, e ela começou a servi-los.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24248, 'nvi', 'Marcos', 'mc', 1, 32, 'Ao anoitecer, depois do pôr-do-sol, o povo levou a Jesus todos os doentes e os endemoninhados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24249, 'nvi', 'Marcos', 'mc', 1, 33, 'Toda a cidade se reuniu à porta da casa,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24250, 'nvi', 'Marcos', 'mc', 1, 34, 'e Jesus curou muitos que sofriam de várias doenças. Também expulsou muitos demônios; não permitia, porém, que estes falassem, porque sabiam quem ele era.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24251, 'nvi', 'Marcos', 'mc', 1, 35, 'De madrugada, quando ainda estava escuro, Jesus levantou-se, saiu de casa e foi para um lugar deserto, onde ficou orando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24252, 'nvi', 'Marcos', 'mc', 1, 36, 'Simão e seus companheiros foram procurá-lo');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24253, 'nvi', 'Marcos', 'mc', 1, 37, 'e, ao encontrá-lo, disseram: "Todos estão te procurando! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24254, 'nvi', 'Marcos', 'mc', 1, 38, 'Jesus respondeu: "Vamos para outro lugar, para os povoados vizinhos, para que também lá eu pregue. Foi para isso que eu vim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24255, 'nvi', 'Marcos', 'mc', 1, 39, 'Então ele percorreu toda a Galiléia, pregando nas sinagogas e expulsando os demônios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24256, 'nvi', 'Marcos', 'mc', 1, 40, 'Um leproso aproximou-se dele e suplicou-lhe de joelhos: "Se quiseres, podes purificar-me! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24257, 'nvi', 'Marcos', 'mc', 1, 41, 'Cheio de compaixão, Jesus estendeu a mão, tocou nele e disse: "Quero. Seja purificado! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24258, 'nvi', 'Marcos', 'mc', 1, 42, 'Imediatamente a lepra o deixou, e ele foi purificado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24259, 'nvi', 'Marcos', 'mc', 1, 43, 'Em seguida Jesus o despediu, com uma severa advertência:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24260, 'nvi', 'Marcos', 'mc', 1, 44, '"Olhe, não conte isso a ninguém. Mas vá mostrar-se ao sacerdote e ofereça pela sua purificação os sacrifícios que Moisés ordenou, para que sirva de testemunho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24261, 'nvi', 'Marcos', 'mc', 1, 45, 'Ele, porém, saiu e começou a tornar público o fato, espalhando a notícia. Por isso Jesus não podia mais entrar publicamente em nenhuma cidade, mas ficava fora, em lugares solitários. Todavia, assim mesmo vinha a ele gente de todas as partes.');
