INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24590, 'nvi', 'Marcos', 'mc', 10, 1, 'Então Jesus saiu dali e foi para a região da Judéia e para o outro lado do Jordão. Novamente uma multidão veio a ele e, segundo o seu costume, ele a ensinava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24591, 'nvi', 'Marcos', 'mc', 10, 2, 'Alguns fariseus aproximaram-se dele para pô-lo à prova, perguntando: "É permitido ao homem divorciar-se de sua mulher? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24592, 'nvi', 'Marcos', 'mc', 10, 3, '"O que Moisés lhes ordenou? ", perguntou ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24593, 'nvi', 'Marcos', 'mc', 10, 4, 'Eles disseram: "Moisés permitiu que o homem desse uma certidão de divórcio e a mandasse embora".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24594, 'nvi', 'Marcos', 'mc', 10, 5, 'Respondeu Jesus: "Moisés escreveu essa lei por causa da dureza de coração de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24595, 'nvi', 'Marcos', 'mc', 10, 6, 'Mas no princípio da criação Deus ‘os fez homem e mulher’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24596, 'nvi', 'Marcos', 'mc', 10, 7, '‘Por esta razão, o homem deixará pai e mãe e se unirá à sua mulher,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24597, 'nvi', 'Marcos', 'mc', 10, 8, 'e os dois se tornarão uma só carne’. Assim, eles já não são dois, mas sim uma só carne.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24598, 'nvi', 'Marcos', 'mc', 10, 9, 'Portanto, o que Deus uniu, ninguém o separe".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24599, 'nvi', 'Marcos', 'mc', 10, 10, 'Quando estava em casa novamente, os discípulos interrogaram Jesus sobre o mesmo assunto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24600, 'nvi', 'Marcos', 'mc', 10, 11, 'Ele respondeu: "Todo aquele que se divorciar de sua mulher e se casar com outra mulher, estará cometendo adultério contra ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24601, 'nvi', 'Marcos', 'mc', 10, 12, 'E se ela se divorciar de seu marido e se casar com outro homem, estará cometendo adultério".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24602, 'nvi', 'Marcos', 'mc', 10, 13, 'Alguns traziam crianças a Jesus para que ele tocasse nelas, mas os discípulos os repreendiam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24603, 'nvi', 'Marcos', 'mc', 10, 14, 'Quando Jesus viu isso, ficou indignado e lhes disse: "Deixem vir a mim as crianças, não as impeçam; pois o Reino de Deus pertence aos que são semelhantes a elas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24604, 'nvi', 'Marcos', 'mc', 10, 15, 'Digo-lhes a verdade: Quem não receber o Reino de Deus como uma criança, nunca entrará nele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24605, 'nvi', 'Marcos', 'mc', 10, 16, 'Em seguida, tomou as crianças nos braços, impôs-lhes as mãos e as abençoou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24606, 'nvi', 'Marcos', 'mc', 10, 17, 'Quando Jesus ia saindo, um homem correu em sua direção, pôs-se de joelhos diante dele e lhe perguntou: "Bom mestre, que farei para herdar a vida eterna? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24607, 'nvi', 'Marcos', 'mc', 10, 18, 'Respondeu-lhe Jesus: "Por que você me chama bom? Ninguém é bom, a não ser um, que é Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24608, 'nvi', 'Marcos', 'mc', 10, 19, 'Você conhece os mandamentos: ‘não matarás, não adulterarás, não furtarás, não darás falso testemunho, não enganarás ninguém, honra teu pai e tua mãe’".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24609, 'nvi', 'Marcos', 'mc', 10, 20, 'E ele declarou: "Mestre, a tudo isso tenho obedecido desde a minha adolescência".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24610, 'nvi', 'Marcos', 'mc', 10, 21, 'Jesus olhou para ele e o amou. "Falta-lhe uma coisa", disse ele. "Vá, venda tudo o que você possui e dê o dinheiro aos pobres, e você terá um tesouro no céu. Depois, venha e siga-me".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24611, 'nvi', 'Marcos', 'mc', 10, 22, 'Diante disso ele ficou abatido e afastou-se triste, porque tinha muitas riquezas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24612, 'nvi', 'Marcos', 'mc', 10, 23, 'Jesus olhou ao redor e disse aos seus discípulos: "Como é difícil aos ricos entrar no Reino de Deus! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24613, 'nvi', 'Marcos', 'mc', 10, 24, 'Os discípulos ficaram admirados com essas palavras. Mas Jesus repetiu: "Filhos, como é difícil entrar no Reino de Deus!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24614, 'nvi', 'Marcos', 'mc', 10, 25, 'É mais fácil passar um camelo pelo fundo de uma agulha do que um rico entrar no Reino de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24615, 'nvi', 'Marcos', 'mc', 10, 26, 'Os discípulos ficaram perplexos, e perguntavam uns aos outros: "Neste caso, quem pode ser salvo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24616, 'nvi', 'Marcos', 'mc', 10, 27, 'Jesus olhou para eles e respondeu: "Para o homem é impossível, mas para Deus não; todas as coisas são possíveis para Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24617, 'nvi', 'Marcos', 'mc', 10, 28, 'Então Pedro começou a dizer-lhe: "Nós deixamos tudo para seguir-te".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24618, 'nvi', 'Marcos', 'mc', 10, 29, 'Respondeu Jesus: "Digo-lhes a verdade: Ninguém que tenha deixado casa, irmãos, irmãs, mãe, pai, filhos, ou campos, por causa de mim e do evangelho,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24619, 'nvi', 'Marcos', 'mc', 10, 30, 'deixará de receber cem vezes mais já no tempo presente casas, irmãos, irmãs, mães, filhos e campos, e com eles perseguição; e, na era futura, a vida eterna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24620, 'nvi', 'Marcos', 'mc', 10, 31, 'Contudo, muitos primeiros serão últimos, e os últimos serão primeiros".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24621, 'nvi', 'Marcos', 'mc', 10, 32, 'Eles estavam subindo para Jerusalém, e Jesus ia à frente. Os discípulos estavam admirados, enquanto os que o seguiam estavam com medo. Novamente ele chamou à parte os Doze e lhes disse o que haveria de lhe acontecer:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24622, 'nvi', 'Marcos', 'mc', 10, 33, '"Estamos subindo para Jerusalém e o Filho do homem será entregue aos chefes dos sacerdotes e aos mestres da lei. Eles o condenarão à morte e o entregarão aos gentios,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24623, 'nvi', 'Marcos', 'mc', 10, 34, 'que zombarão dele, cuspirão nele, o açoitarão e o matarão. Três dias depois ele ressuscitará".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24624, 'nvi', 'Marcos', 'mc', 10, 35, 'Nisso Tiago e João, filhos de Zebedeu, aproximaram-se dele e disseram: "Mestre, queremos que nos faças o que vamos te pedir".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24625, 'nvi', 'Marcos', 'mc', 10, 36, '"O que vocês querem que eu lhes faça? ", perguntou ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24626, 'nvi', 'Marcos', 'mc', 10, 37, 'Eles responderam: "Permite que, na tua glória, nos assentemos um à tua direita e o outro à tua esquerda".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24627, 'nvi', 'Marcos', 'mc', 10, 38, 'Disse-lhes Jesus: "Vocês não sabem o que estão pedindo. Podem vocês beber o cálice que eu estou bebendo ou ser batizados com o batismo com que estou sendo batizado? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24628, 'nvi', 'Marcos', 'mc', 10, 39, '"Podemos", responderam eles. Jesus lhes disse: "Vocês beberão o cálice que estou bebendo e serão batizados com o batismo com que estou sendo batizado;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24629, 'nvi', 'Marcos', 'mc', 10, 40, 'mas o assentar-se à minha direita ou à minha esquerda não cabe a mim conceder. Esses lugares pertencem àqueles para quem foram preparados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24630, 'nvi', 'Marcos', 'mc', 10, 41, 'Quando os outros dez ouviram essas coisas, ficaram indignados com Tiago e João.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24631, 'nvi', 'Marcos', 'mc', 10, 42, 'Jesus os chamou e disse: "Vocês sabem que aqueles que são considerados governantes das nações as dominam, e as pessoas importantes exercem poder sobre elas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24632, 'nvi', 'Marcos', 'mc', 10, 43, 'Não será assim entre vocês. Pelo contrário, quem quiser tornar-se importante entre vocês deverá ser servo;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24633, 'nvi', 'Marcos', 'mc', 10, 44, 'e quem quiser ser o primeiro deverá ser escravo de todos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24634, 'nvi', 'Marcos', 'mc', 10, 45, 'Pois nem mesmo o Filho do homem veio para ser servido, mas para servir e dar a sua vida em resgate por muitos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24635, 'nvi', 'Marcos', 'mc', 10, 46, 'Então chegaram a Jericó. Quando Jesus e seus discípulos, juntamente com uma grande multidão, estavam saindo da cidade, o filho de Timeu, Bartimeu, que era cego, estava sentado à beira do caminho pedindo esmolas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24636, 'nvi', 'Marcos', 'mc', 10, 47, 'Quando ouviu que era Jesus de Nazaré, começou a gritar: "Jesus, Filho de Davi, tem misericórdia de mim! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24637, 'nvi', 'Marcos', 'mc', 10, 48, 'Muitos o repreendiam para que ficasse quieto, mas ele gritava ainda mais: "Filho de Davi, tem misericórdia de mim! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24638, 'nvi', 'Marcos', 'mc', 10, 49, 'Jesus parou e disse: "Chamem-no". E chamaram o cego: "Ânimo! Levante-se! Ele o está chamando".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24639, 'nvi', 'Marcos', 'mc', 10, 50, 'Lançando sua capa para o lado, de um salto, pôs-se de pé e dirigiu-se a Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24640, 'nvi', 'Marcos', 'mc', 10, 51, '"O que você quer que eu lhe faça? ", perguntou-lhe Jesus. O cego respondeu: "Mestre, eu quero ver! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24641, 'nvi', 'Marcos', 'mc', 10, 52, '"Vá", disse Jesus, "a sua fé o curou". Imediatamente ele recuperou a visão e seguia a Jesus pelo caminho.');
