INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24642, 'nvi', 'Marcos', 'mc', 11, 1, 'Quando se aproximaram de Jerusalém e chegaram a Betfagé e Betânia, perto do monte das Oliveiras, Jesus enviou dois de seus discípulos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24643, 'nvi', 'Marcos', 'mc', 11, 2, 'dizendo-lhes: "Vão ao povoado que está adiante de vocês; logo que entrarem, encontrarão um jumentinho amarrado, no qual ninguém jamais montou. Desamarrem-no e tragam-no aqui.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24644, 'nvi', 'Marcos', 'mc', 11, 3, 'Se alguém lhes perguntar: ‘Por que vocês estão fazendo isso? ’ digam-lhe: ‘O Senhor precisa dele e logo o devolverá’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24645, 'nvi', 'Marcos', 'mc', 11, 4, 'Eles foram e encontraram um jumentinho, na rua, amarrado a um portão. Enquanto o desamarravam,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24646, 'nvi', 'Marcos', 'mc', 11, 5, 'alguns dos que ali estavam lhes perguntaram: "O que vocês estão fazendo, desamarrando esse jumentinho? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24647, 'nvi', 'Marcos', 'mc', 11, 6, 'Os discípulos responderam como Jesus lhes tinha dito, e eles os deixaram ir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24648, 'nvi', 'Marcos', 'mc', 11, 7, 'Trouxeram o jumentinho a Jesus, puseram sobre ele os seus mantos; e Jesus montou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24649, 'nvi', 'Marcos', 'mc', 11, 8, 'Muitos estenderam seus mantos pelo caminho, outros espalharam ramos que haviam cortado nos campos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24650, 'nvi', 'Marcos', 'mc', 11, 9, 'Os que iam adiante dele e os que o seguiam gritavam: "Hosana! " "Bendito é o que vem em nome do Senhor! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24651, 'nvi', 'Marcos', 'mc', 11, 10, '"Bendito é o Reino vindouro de nosso pai Davi! " "Hosana nas alturas! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24652, 'nvi', 'Marcos', 'mc', 11, 11, 'Jesus entrou em Jerusalém e dirigiu-se ao templo. Observou tudo à sua volta e, como já era tarde, foi para Betânia com os Doze.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24653, 'nvi', 'Marcos', 'mc', 11, 12, 'No dia seguinte, quando estavam saindo de Betânia, Jesus teve fome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24654, 'nvi', 'Marcos', 'mc', 11, 13, 'Vendo à distância uma figueira com folhas, foi ver se encontraria nela algum fruto. Aproximando-se dela, nada encontrou, a não ser folhas, porque não era tempo de figos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24655, 'nvi', 'Marcos', 'mc', 11, 14, 'Então lhe disse: "Ninguém mais coma de seu fruto". E os seus discípulos ouviram-no dizer isso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24656, 'nvi', 'Marcos', 'mc', 11, 15, 'Chegando a Jerusalém, Jesus entrou no templo e ali começou a expulsar os que estavam comprando e vendendo. Derrubou as mesas dos cambistas e as cadeiras dos que vendiam pombas');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24657, 'nvi', 'Marcos', 'mc', 11, 16, 'e não permitia que ninguém carregasse mercadorias pelo templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24658, 'nvi', 'Marcos', 'mc', 11, 17, 'E os ensinava, dizendo: "Não está escrito: ‘A minha casa será chamada casa de oração para todos os povos’? Mas vocês fizeram dela um covil de ladrões".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24659, 'nvi', 'Marcos', 'mc', 11, 18, 'Os chefes dos sacerdotes e os mestres da lei ouviram essas palavras e começaram a procurar uma forma de matá-lo, pois o temiam, visto que toda a multidão estava maravilhada com o seu ensino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24660, 'nvi', 'Marcos', 'mc', 11, 19, 'Ao cair da tarde, eles saíram da cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24661, 'nvi', 'Marcos', 'mc', 11, 20, 'De manhã, ao passarem, viram a figueira seca desde as raízes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24662, 'nvi', 'Marcos', 'mc', 11, 21, 'Lembrando-se Pedro, disse a Jesus: "Mestre! Vê! A figueira que amaldiçoaste secou! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24663, 'nvi', 'Marcos', 'mc', 11, 22, 'Respondeu Jesus: "Tenham fé em Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24664, 'nvi', 'Marcos', 'mc', 11, 23, 'Eu lhes asseguro que se alguém disser a este monte: ‘Levante-se e atire-se no mar’, e não duvidar em seu coração, mas crer que acontecerá o que diz, assim lhe será feito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24665, 'nvi', 'Marcos', 'mc', 11, 24, 'Portanto, eu lhes digo: tudo o que vocês pedirem em oração, creiam que já o receberam, e assim lhes sucederá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24666, 'nvi', 'Marcos', 'mc', 11, 25, 'E quando estiverem orando, se tiverem alguma coisa contra alguém, perdoem-no, para que também o Pai celestial lhes perdoe os seus pecados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24667, 'nvi', 'Marcos', 'mc', 11, 26, 'Mas se vocês não perdoarem, também o seu Pai que está no céu não perdoará os seus pecados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24668, 'nvi', 'Marcos', 'mc', 11, 27, 'Chegaram novamente a Jerusalém e, quando Jesus estava passando pelo templo, aproximaram-se dele os chefes dos sacerdotes, os mestres da lei e os líderes religiosos e perguntaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24669, 'nvi', 'Marcos', 'mc', 11, 28, '"Com que autoridade estás fazendo estas coisas? Quem te deu autoridade para fazê-las? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24670, 'nvi', 'Marcos', 'mc', 11, 29, 'Respondeu Jesus: "Eu lhes farei uma pergunta. Respondam-me, e eu lhes direi com que autoridade estou fazendo estas coisas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24671, 'nvi', 'Marcos', 'mc', 11, 30, 'O batismo de João era do céu ou dos homens? Digam-me! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24672, 'nvi', 'Marcos', 'mc', 11, 31, 'Eles discutiam entre si, dizendo: "Se dissermos: ‘do céu’, ele perguntará: ‘Então por que vocês não creram nele? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24673, 'nvi', 'Marcos', 'mc', 11, 32, 'Mas se dissermos: ‘dos homens’... " Eles temiam o povo, pois todos realmente consideravam João um profeta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24674, 'nvi', 'Marcos', 'mc', 11, 33, 'Eles responderam a Jesus: "Não sabemos". Disse então Jesus: "Tampouco lhes direi com que autoridade estou fazendo estas coisas".');
