INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24675, 'nvi', 'Marcos', 'mc', 12, 1, 'Então Jesus começou a lhes falar por parábolas: "Certo homem plantou uma vinha, colocou uma cerca ao redor dela, cavou um tanque para prensar as uvas e construiu uma torre. Depois arrendou a vinha a alguns lavradores e foi fazer uma viagem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24676, 'nvi', 'Marcos', 'mc', 12, 2, 'Na época da colheita, enviou um servo aos lavradores, para receber deles parte do fruto da vinha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24677, 'nvi', 'Marcos', 'mc', 12, 3, 'Mas eles o agarraram e espancaram, e o mandaram embora de mãos vazias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24678, 'nvi', 'Marcos', 'mc', 12, 4, 'Então enviou-lhes outro servo; e lhe bateram na cabeça e o humilharam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24679, 'nvi', 'Marcos', 'mc', 12, 5, 'E enviou ainda outro, o qual mataram. Enviou muitos outros; em alguns bateram, a outros mataram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24680, 'nvi', 'Marcos', 'mc', 12, 6, '"Faltava-lhe ainda um para enviar: seu filho amado. Por fim o enviou, dizendo: ‘A meu filho respeitarão’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24681, 'nvi', 'Marcos', 'mc', 12, 7, '"Mas os lavradores disseram uns aos outros: ‘Este é o herdeiro. Venham, vamos matá-lo, e a herança será nossa’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24682, 'nvi', 'Marcos', 'mc', 12, 8, 'Assim eles o agarraram, e o mataram, e o lançaram para fora da vinha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24683, 'nvi', 'Marcos', 'mc', 12, 9, '"O que fará então o dono da vinha? Virá e matará aqueles lavradores e dará a vinha a outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24684, 'nvi', 'Marcos', 'mc', 12, 10, 'Vocês nunca leram esta passagem das Escrituras? ‘A pedra que os construtores rejeitaram tornou-se a pedra angular;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24685, 'nvi', 'Marcos', 'mc', 12, 11, 'isso vem do Senhor, e é algo maravilhoso para nós’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24686, 'nvi', 'Marcos', 'mc', 12, 12, 'Então começaram a procurar um meio de prendê-lo, pois perceberam que era contra eles que ele havia contado aquela parábola. Mas tinham medo da multidão; por isso o deixaram e foram embora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24687, 'nvi', 'Marcos', 'mc', 12, 13, 'Mais tarde enviaram a Jesus alguns dos fariseus e herodianos para o apanharem em alguma coisa que ele dissesse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24688, 'nvi', 'Marcos', 'mc', 12, 14, 'Estes se aproximaram dele e disseram: "Mestre, sabemos que és íntegro e que não te deixas influenciar por ninguém, porque não te prendes à aparência dos homens, mas ensinas o caminho de Deus conforme a verdade. É certo pagar imposto a César ou não?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24689, 'nvi', 'Marcos', 'mc', 12, 15, 'Devemos pagar ou não? " Mas Jesus, percebendo a hipocrisia deles, perguntou: "Por que vocês estão me pondo à prova? Tragam-me um denário para que eu o veja".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24690, 'nvi', 'Marcos', 'mc', 12, 16, 'Eles lhe trouxeram a moeda, e ele lhes perguntou: "De quem é esta imagem e esta inscrição? " "De César", responderam eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24691, 'nvi', 'Marcos', 'mc', 12, 17, 'Então Jesus lhes disse: "Dêem a César o que é de César e a Deus o que é de Deus". E ficaram admirados com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24692, 'nvi', 'Marcos', 'mc', 12, 18, 'Depois os saduceus, que dizem que não há ressurreição, aproximaram-se dele com a seguinte questão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24693, 'nvi', 'Marcos', 'mc', 12, 19, '"Mestre, Moisés nos deixou escrito que, se o irmão de um homem morrer e deixar mulher sem filhos, este deverá casar-se com a viúva e ter filhos para seu irmão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24694, 'nvi', 'Marcos', 'mc', 12, 20, 'Havia sete irmãos. O primeiro casou-se e morreu sem deixar filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24695, 'nvi', 'Marcos', 'mc', 12, 21, 'O segundo casou-se com a viúva, mas também morreu sem deixar filhos. O mesmo aconteceu com o terceiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24696, 'nvi', 'Marcos', 'mc', 12, 22, 'Nenhum dos sete deixou filhos. Finalmente, morreu também a mulher.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24697, 'nvi', 'Marcos', 'mc', 12, 23, 'Na ressurreição, de quem ela será esposa, visto que os sete foram casados com ela? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24698, 'nvi', 'Marcos', 'mc', 12, 24, 'Jesus respondeu: "Vocês estão enganados! Pois não conhecem as Escrituras nem o poder de Deus!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24699, 'nvi', 'Marcos', 'mc', 12, 25, 'Quando os mortos ressuscitam, não se casam nem são dados em casamento, mas são como os anjos nos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24700, 'nvi', 'Marcos', 'mc', 12, 26, 'Quanto à ressurreição dos mortos, vocês não leram no livro de Moisés, no relato da sarça, como Deus lhe disse: ‘Eu sou o Deus de Abraão, o Deus de Isaque e o Deus de Jacó’?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24701, 'nvi', 'Marcos', 'mc', 12, 27, 'Ele não é Deus de mortos, mas de vivos. Vocês estão muito enganados! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24702, 'nvi', 'Marcos', 'mc', 12, 28, 'Um dos mestres da lei aproximou-se e os ouviu discutindo. Notando que Jesus lhes dera uma boa resposta, perguntou-lhe: "De todos os mandamentos, qual é o mais importante? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24703, 'nvi', 'Marcos', 'mc', 12, 29, 'Respondeu Jesus: "O mais importante é este: ‘Ouve, ó Israel, o Senhor, o nosso Deus, o Senhor é o único Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24704, 'nvi', 'Marcos', 'mc', 12, 30, 'Ame o Senhor, o seu Deus de todo o seu coração, de toda a sua alma, de todo o seu entendimento e de todas as suas forças’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24705, 'nvi', 'Marcos', 'mc', 12, 31, 'O segundo é este: ‘Ame o seu próximo como a si mesmo’. Não existe mandamento maior do que estes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24706, 'nvi', 'Marcos', 'mc', 12, 32, '"Muito bem, mestre", disse o homem. "Estás certo ao dizeres que Deus é único e que não existe outro além dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24707, 'nvi', 'Marcos', 'mc', 12, 33, 'Amá-lo de todo o coração, de todo o entendimento e de todas as forças, e amar ao próximo como a si mesmo é mais importante do que todos os sacrifícios e ofertas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24708, 'nvi', 'Marcos', 'mc', 12, 34, 'Vendo que ele tinha respondido sabiamente, Jesus lhe disse: "Você não está longe do Reino de Deus". Daí por diante ninguém mais ousava lhe fazer perguntas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24709, 'nvi', 'Marcos', 'mc', 12, 35, 'Ensinando no templo, Jesus perguntou: "Como os mestres da lei dizem que o Cristo é filho de Davi?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24710, 'nvi', 'Marcos', 'mc', 12, 36, 'O próprio Davi, falando pelo Espírito Santo, disse: ‘O Senhor disse ao meu Senhor: Senta-te à minha direita até que eu ponha os teus inimigos debaixo de teus pés’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24711, 'nvi', 'Marcos', 'mc', 12, 37, 'O próprio Davi o chama ‘Senhor’. Como pode, então, ser ele seu filho? " E a grande multidão o ouvia com prazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24712, 'nvi', 'Marcos', 'mc', 12, 38, 'Ao ensinar, Jesus dizia: "Cuidado com os mestres da lei. Eles fazem questão de andar com roupas especiais, de receber saudações nas praças');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24713, 'nvi', 'Marcos', 'mc', 12, 39, 'e de ocupar os lugares mais importantes nas sinagogas e os lugares de honra nos banquetes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24714, 'nvi', 'Marcos', 'mc', 12, 40, 'Eles devoram as casas das viúvas, e, para disfarçar, fazem longas orações. Esses receberão condenação mais severa! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24715, 'nvi', 'Marcos', 'mc', 12, 41, 'Jesus sentou-se em frente do lugar onde eram colocadas as contribuições, e observava a multidão colocando o dinheiro nas caixas de ofertas. Muitos ricos lançavam ali grandes quantias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24716, 'nvi', 'Marcos', 'mc', 12, 42, 'Então, uma viúva pobre chegou-se e colocou duas pequeninas moedas de cobre, de muito pouco valor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24717, 'nvi', 'Marcos', 'mc', 12, 43, 'Chamando a si os seus discípulos, Jesus declarou: "Afirmo-lhes que esta viúva pobre colocou na caixa de ofertas mais do que todos os outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24718, 'nvi', 'Marcos', 'mc', 12, 44, 'Todos deram do que lhes sobrava; mas ela, da sua pobreza, deu tudo o que possuía para viver".');
