INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24719, 'nvi', 'Marcos', 'mc', 13, 1, 'Quando ele estava saindo do templo, um de seus discípulos lhe disse: "Olha, Mestre! Que pedras enormes! Que construções magníficas! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24720, 'nvi', 'Marcos', 'mc', 13, 2, '"Você está vendo todas estas grandes construções? ", perguntou Jesus. "Aqui não ficará pedra sobre pedra; serão todas derrubadas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24721, 'nvi', 'Marcos', 'mc', 13, 3, 'Tendo Jesus se assentado no monte das Oliveiras, de frente para o templo, Pedro, Tiago, João e André lhe perguntaram em particular:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24722, 'nvi', 'Marcos', 'mc', 13, 4, '"Dize-nos, quando acontecerão essas coisas? E qual será o sinal de que tudo isso está prestes a cumprir-se? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24723, 'nvi', 'Marcos', 'mc', 13, 5, 'Jesus lhes disse: "Cuidado, que ninguém os engane.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24724, 'nvi', 'Marcos', 'mc', 13, 6, 'Muitos virão em meu nome, dizendo: ‘Sou eu! ’ e enganarão a muitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24725, 'nvi', 'Marcos', 'mc', 13, 7, 'Quando ouvirem falar de guerras e rumores de guerras, não tenham medo. É necessário que tais coisas aconteçam, mas ainda não é o fim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24726, 'nvi', 'Marcos', 'mc', 13, 8, 'Nação se levantará contra nação, e reino contra reino. Haverá terremotos em vários lugares e também fomes. Essas coisas são o início das dores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24727, 'nvi', 'Marcos', 'mc', 13, 9, '"Fiquem atentos, pois vocês serão entregues aos tribunais e serão açoitados nas sinagogas. Por minha causa vocês serão levados à presença de governadores e reis, como testemunho a eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24728, 'nvi', 'Marcos', 'mc', 13, 10, 'E é necessário que antes o evangelho seja pregado a todas as nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24729, 'nvi', 'Marcos', 'mc', 13, 11, 'Sempre que forem presos e levados a julgamento, não fiquem preocupados com o que vão dizer. Digam tão-somente o que lhes for dado naquela hora, pois não serão vocês que estarão falando, mas o Espírito Santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24730, 'nvi', 'Marcos', 'mc', 13, 12, '"O irmão trairá seu próprio irmão, entregando-o à morte, e o mesmo fará o pai a seu filho. Filhos se rebelarão contra seus pais e os matarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24731, 'nvi', 'Marcos', 'mc', 13, 13, 'Todos odiarão vocês por minha causa; mas aquele que perseverar até o fim será salvo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24732, 'nvi', 'Marcos', 'mc', 13, 14, '"Quando vocês virem ‘o sacrilégio terrível’ no lugar onde não deve estar — quem lê, entenda — então, os que estiverem na Judéia fujam para os montes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24733, 'nvi', 'Marcos', 'mc', 13, 15, 'Quem estiver no telhado de sua casa não desça nem entre em casa para tirar dela coisa alguma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24734, 'nvi', 'Marcos', 'mc', 13, 16, 'Quem estiver no campo não volte para pegar seu manto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24735, 'nvi', 'Marcos', 'mc', 13, 17, 'Como serão terríveis aqueles dias para as grávidas e para as que estiverem amamentando!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24736, 'nvi', 'Marcos', 'mc', 13, 18, 'Orem para que essas coisas não aconteçam no inverno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24737, 'nvi', 'Marcos', 'mc', 13, 19, 'Porque aqueles serão dias de tribulação como nunca houve desde que Deus criou o mundo até agora, nem jamais haverá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24738, 'nvi', 'Marcos', 'mc', 13, 20, 'Se o Senhor não tivesse abreviado tais dias, ninguém sobreviveria. Mas, por causa dos eleitos por ele escolhidos, ele os abreviou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24739, 'nvi', 'Marcos', 'mc', 13, 21, 'Se, então, alguém lhes disser: ‘Vejam, aqui está o Cristo! ’ ou: ‘Vejam, ali está ele! ’, não acreditem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24740, 'nvi', 'Marcos', 'mc', 13, 22, 'Pois aparecerão falsos cristos e falsos profetas que realizarão sinais e maravilhas para, se possível, enganar os eleitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24741, 'nvi', 'Marcos', 'mc', 13, 23, 'Por isso, fiquem atentos: avisei-os de tudo antecipadamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24742, 'nvi', 'Marcos', 'mc', 13, 24, '"Mas naqueles dias, após aquela tribulação, ‘o sol escurecerá e a lua não dará a sua luz;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24743, 'nvi', 'Marcos', 'mc', 13, 25, 'as estrelas cairão do céu e os poderes celestes serão abalados’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24744, 'nvi', 'Marcos', 'mc', 13, 26, '"Então se verá o Filho do homem vindo nas nuvens com grande poder e glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24745, 'nvi', 'Marcos', 'mc', 13, 27, 'E ele enviará os seus anjos e reunirá os seus eleitos dos quatro ventos, dos confins da terra até os confins do céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24746, 'nvi', 'Marcos', 'mc', 13, 28, '"Aprendam a lição da figueira: quando seus ramos se renovam e suas folhas começam a brotar, vocês sabem que o verão está próximo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24747, 'nvi', 'Marcos', 'mc', 13, 29, 'Assim também, quando virem estas coisas acontecendo, saibam que ele está próximo, às portas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24748, 'nvi', 'Marcos', 'mc', 13, 30, 'Eu lhes asseguro que não passará esta geração até que todas essas coisas aconteçam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24749, 'nvi', 'Marcos', 'mc', 13, 31, 'O céu e a terra passarão, mas as minhas palavras jamais passarão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24750, 'nvi', 'Marcos', 'mc', 13, 32, '"Quanto ao dia e à hora ninguém sabe, nem os anjos no céu, nem o Filho, senão somente o Pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24751, 'nvi', 'Marcos', 'mc', 13, 33, 'Fiquem atentos! Vigiem! Vocês não sabem quando virá esse tempo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24752, 'nvi', 'Marcos', 'mc', 13, 34, 'É como um homem que sai de viagem. Ele deixa sua casa, encarrega de tarefas cada um dos seus servos e ordena ao porteiro que vigie.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24753, 'nvi', 'Marcos', 'mc', 13, 35, 'Portanto, vigiem, porque vocês não sabem quando o dono da casa voltará: se à tarde, à meia-noite, ao cantar do galo ou ao amanhecer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24754, 'nvi', 'Marcos', 'mc', 13, 36, 'Se ele vier de repente, que não os encontre dormindo!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24755, 'nvi', 'Marcos', 'mc', 13, 37, 'O que lhes digo, digo a todos: Vigiem! "');
