INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24756, 'nvi', 'Marcos', 'mc', 14, 1, 'Faltavam apenas dois dias para a Páscoa e para a festa dos pães sem fermento. Os chefes dos sacerdotes e os mestres da lei estavam procurando um meio de flagrar Jesus em algum erro e matá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24757, 'nvi', 'Marcos', 'mc', 14, 2, 'Mas diziam: "Não durante a festa, para que não haja tumulto entre o povo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24758, 'nvi', 'Marcos', 'mc', 14, 3, 'Estando Jesus em Betânia, reclinado à mesa na casa de um homem conhecido como Simão, o leproso, aproximou-se dele certa mulher com um frasco de alabastro contendo um perfume muito caro, feito de nardo puro. Ela quebrou o frasco e derramou o perfume sobre a cabeça de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24759, 'nvi', 'Marcos', 'mc', 14, 4, 'Alguns dos presentes começaram a dizer uns aos outros, indignados: "Por que este desperdício de perfume?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24760, 'nvi', 'Marcos', 'mc', 14, 5, 'Ele poderia ser vendido por trezentos denários, e o dinheiro dado aos pobres". E a repreendiam severamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24761, 'nvi', 'Marcos', 'mc', 14, 6, '"Deixem-na em paz", disse Jesus. "Por que a estão perturbando? Ela praticou uma boa ação para comigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24762, 'nvi', 'Marcos', 'mc', 14, 7, 'Pois os pobres vocês sempre terão consigo, e poderão ajudá-los sempre que o desejarem. Mas a mim vocês nem sempre terão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24763, 'nvi', 'Marcos', 'mc', 14, 8, 'Ela fez o que pôde. Derramou o perfume em meu corpo antecipadamente, preparando-o para o sepultamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24764, 'nvi', 'Marcos', 'mc', 14, 9, 'Eu lhes asseguro que onde quer que o evangelho for anunciado, em todo o mundo, também o que ela fez será contado em sua memória".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24765, 'nvi', 'Marcos', 'mc', 14, 10, 'Então Judas Iscariotes, um dos Doze, dirigiu-se aos chefes dos sacerdotes a fim de lhes entregar Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24766, 'nvi', 'Marcos', 'mc', 14, 11, 'A proposta muito os alegrou, e lhe prometeram dinheiro. Assim, ele procurava uma oportunidade para entregá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24767, 'nvi', 'Marcos', 'mc', 14, 12, 'No primeiro dia da festa dos pães sem fermento, quando se costumava sacrificar o cordeiro pascal, os discípulos de Jesus lhe perguntaram: "Aonde queres que vamos e te preparemos a refeição da Páscoa? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24768, 'nvi', 'Marcos', 'mc', 14, 13, 'Então ele enviou dois de seus discípulos, dizendo-lhes: "Entrem na cidade, e um homem carregando um pote de água virá ao encontro de vocês. Sigam-no');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24769, 'nvi', 'Marcos', 'mc', 14, 14, 'e digam ao dono da casa em que ele entrar: ‘O Mestre pergunta: Onde é o meu salão de hóspedes, no qual poderei comer a Páscoa com meus discípulos? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24770, 'nvi', 'Marcos', 'mc', 14, 15, 'Ele lhes mostrará uma ampla sala no andar superior, mobiliada e pronta. Façam ali os preparativos para nós".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24771, 'nvi', 'Marcos', 'mc', 14, 16, 'Os discípulos se retiraram, entraram na cidade e encontraram tudo como Jesus lhes tinha dito. E prepararam a Páscoa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24772, 'nvi', 'Marcos', 'mc', 14, 17, 'Ao anoitecer, Jesus chegou com os Doze.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24773, 'nvi', 'Marcos', 'mc', 14, 18, 'Quando estavam comendo, reclinados à mesa, Jesus disse: "Digo-lhes que certamente um de vocês me trairá, alguém que está comendo comigo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24774, 'nvi', 'Marcos', 'mc', 14, 19, 'Eles ficaram tristes e, um por um, lhe disseram: "Com certeza não sou eu! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24775, 'nvi', 'Marcos', 'mc', 14, 20, 'Afirmou Jesus: "É um dos Doze, alguém que come comigo do mesmo prato.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24776, 'nvi', 'Marcos', 'mc', 14, 21, 'O Filho do homem vai, como está escrito a seu respeito. Mas ai daquele que trai o Filho do homem! Melhor lhe seria não haver nascido".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24777, 'nvi', 'Marcos', 'mc', 14, 22, 'Enquanto comiam, Jesus tomou o pão, deu graças, partiu-o, e o deu aos discípulos, dizendo: "Tomem; isto é o meu corpo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24778, 'nvi', 'Marcos', 'mc', 14, 23, 'Em seguida tomou o cálice, deu graças, ofereceu-o aos discípulos, e todos beberam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24779, 'nvi', 'Marcos', 'mc', 14, 24, 'E lhes disse: "Isto é o meu sangue da aliança, que é derramado em favor de muitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24780, 'nvi', 'Marcos', 'mc', 14, 25, 'Eu lhes afirmo que não beberei outra vez do fruto da videira, até aquele dia em que beberei o vinho novo no Reino de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24781, 'nvi', 'Marcos', 'mc', 14, 26, 'Depois de terem cantado um hino, saíram para o monte das Oliveiras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24782, 'nvi', 'Marcos', 'mc', 14, 27, 'Disse-lhes Jesus: "Vocês todos me abandonarão. Pois está escrito: ‘Ferirei o pastor, e as ovelhas serão dispersas’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24783, 'nvi', 'Marcos', 'mc', 14, 28, 'Mas, depois de ressuscitar, irei adiante de vocês para a Galiléia".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24784, 'nvi', 'Marcos', 'mc', 14, 29, 'Pedro declarou: "Ainda que todos te abandonem, eu não te abandonarei! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24785, 'nvi', 'Marcos', 'mc', 14, 30, 'Respondeu Jesus: "Asseguro-lhe que ainda hoje, esta noite, antes que duas vezes cante o galo, três vezes você me negará".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24786, 'nvi', 'Marcos', 'mc', 14, 31, 'Mas Pedro insistia ainda mais: "Mesmo que seja preciso que eu morra contigo, nunca te negarei". E todos os outros disseram o mesmo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24787, 'nvi', 'Marcos', 'mc', 14, 32, 'Então foram para um lugar chamado Getsêmani, e Jesus disse aos seus discípulos: "Sentem-se aqui enquanto vou orar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24788, 'nvi', 'Marcos', 'mc', 14, 33, 'Levou consigo Pedro, Tiago e João, e começou a ficar aflito e angustiado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24789, 'nvi', 'Marcos', 'mc', 14, 34, 'E lhes disse: "A minha alma está profundamente triste, numa tristeza mortal. Fiquem aqui e vigiem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24790, 'nvi', 'Marcos', 'mc', 14, 35, 'Indo um pouco mais adiante, prostrou-se e orava para que, se possível, fosse afastada dele aquela hora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24791, 'nvi', 'Marcos', 'mc', 14, 36, 'E dizia: "Aba, Pai, tudo te é possível. Afasta de mim este cálice; contudo, não seja o que eu quero, mas sim o que tu queres".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24792, 'nvi', 'Marcos', 'mc', 14, 37, 'Então, voltou aos seus discípulos e os encontrou dormindo. "Simão", disse ele a Pedro, "você está dormindo? Não pôde vigiar nem por uma hora?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24793, 'nvi', 'Marcos', 'mc', 14, 38, 'Vigiem e orem para que não caiam em tentação. O espírito está pronto, mas a carne é fraca. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24794, 'nvi', 'Marcos', 'mc', 14, 39, 'Mais uma vez ele se afastou e orou, repetindo as mesmas palavras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24795, 'nvi', 'Marcos', 'mc', 14, 40, 'Quando voltou, de novo os encontrou dormindo, porque seus olhos estavam pesados. Eles não sabiam o que lhe dizer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24796, 'nvi', 'Marcos', 'mc', 14, 41, 'Voltando pela terceira vez, ele lhes disse: "Vocês ainda dormem e descansam? Basta! Chegou a hora! Eis que o Filho do homem está sendo entregue nas mãos dos pecadores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24797, 'nvi', 'Marcos', 'mc', 14, 42, 'Levantem-se e vamos! Aí vem aquele que me trai! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24798, 'nvi', 'Marcos', 'mc', 14, 43, 'Enquanto ele ainda falava, apareceu Judas, um dos Doze. Com ele estava uma multidão armada de espadas e varas, enviada pelos chefes dos sacerdotes, mestres da lei e líderes religiosos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24799, 'nvi', 'Marcos', 'mc', 14, 44, 'O traidor havia combinado um sinal com eles: "Aquele a quem eu saudar com um beijo, é ele: prendam-no e levem-no em segurança".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24800, 'nvi', 'Marcos', 'mc', 14, 45, 'Dirigindo-se imediatamente a Jesus, Judas disse: "Mestre! ", e o beijou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24801, 'nvi', 'Marcos', 'mc', 14, 46, 'Os homens agarraram Jesus e o prenderam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24802, 'nvi', 'Marcos', 'mc', 14, 47, 'Então, um dos que estavam por perto puxou a espada e feriu o servo do sumo sacerdote, decepando-lhe a orelha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24803, 'nvi', 'Marcos', 'mc', 14, 48, 'Disse Jesus: "Estou eu chefiando alguma rebelião, para que vocês venham me prender com espadas e varas?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24804, 'nvi', 'Marcos', 'mc', 14, 49, 'Todos os dias eu estava com vocês, ensinando no templo, e vocês não me prenderam. Mas as Escrituras precisam ser cumpridas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24805, 'nvi', 'Marcos', 'mc', 14, 50, 'Então todos o abandonaram e fugiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24806, 'nvi', 'Marcos', 'mc', 14, 51, 'Um jovem, vestindo apenas um lençol de linho, estava seguindo a Jesus. Quando tentaram prendê-lo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24807, 'nvi', 'Marcos', 'mc', 14, 52, 'ele fugiu nu, deixando o lençol para trás.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24808, 'nvi', 'Marcos', 'mc', 14, 53, 'Levaram Jesus ao sumo sacerdote; e então se reuniram todos os chefes dos sacerdotes, os líderes religiosos e os mestres da lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24809, 'nvi', 'Marcos', 'mc', 14, 54, 'Pedro o seguiu de longe até o pátio do sumo sacerdote. Sentando-se ali com os guardas, esquentava-se junto ao fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24810, 'nvi', 'Marcos', 'mc', 14, 55, 'Os chefes dos sacerdotes e todo o Sinédrio estavam procurando depoimentos contra Jesus, para que pudessem condená-lo à morte, mas não encontravam nenhum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24811, 'nvi', 'Marcos', 'mc', 14, 56, 'Muitos testemunharam falsamente contra ele, mas as declarações deles não eram coerentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24812, 'nvi', 'Marcos', 'mc', 14, 57, 'Então se levantaram alguns e declararam falsamente contra ele:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24813, 'nvi', 'Marcos', 'mc', 14, 58, '"Nós o ouvimos dizer: ‘Destruirei este templo feito por mãos humanas e em três dias construirei outro, não feito por mãos de homens’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24814, 'nvi', 'Marcos', 'mc', 14, 59, 'Mas, nem mesmo assim, o depoimento deles era coerente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24815, 'nvi', 'Marcos', 'mc', 14, 60, 'Depois o sumo sacerdote levantou-se diante deles e perguntou a Jesus: "Você não vai responder à acusação que estes lhe fazem? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24816, 'nvi', 'Marcos', 'mc', 14, 61, 'Mas Jesus permaneceu em silêncio e nada respondeu. Outra vez o sumo sacerdote lhe perguntou: "Você é o Cristo, o Filho do Deus Bendito? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24817, 'nvi', 'Marcos', 'mc', 14, 62, '"Sou", disse Jesus. "E vereis o Filho do homem assentado à direita do Poderoso vindo com as nuvens do céu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24818, 'nvi', 'Marcos', 'mc', 14, 63, 'O sumo sacerdote, rasgando as próprias vestes, perguntou: "Por que precisamos de mais testemunhas?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24819, 'nvi', 'Marcos', 'mc', 14, 64, 'Vocês ouviram a blasfêmia. Que acham? " Todos o julgaram digno de morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24820, 'nvi', 'Marcos', 'mc', 14, 65, 'Então alguns começaram a cuspir nele; vendaram-lhe os olhos e, dando-lhe murros, diziam: "Profetize! " E os guardas o levaram, dando-lhe tapas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24821, 'nvi', 'Marcos', 'mc', 14, 66, 'Estando Pedro em baixo, no pátio, uma das criadas do sumo sacerdote passou por ali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24822, 'nvi', 'Marcos', 'mc', 14, 67, 'Vendo Pedro a aquecer-se, olhou bem para ele e disse: "Você também estava com Jesus, o Nazareno".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24823, 'nvi', 'Marcos', 'mc', 14, 68, 'Contudo ele o negou, dizendo: "Não o conheço, nem sei do que você está falando". E saiu para o alpendre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24824, 'nvi', 'Marcos', 'mc', 14, 69, 'Quando a criada o viu lá, disse novamente aos que estavam por perto: "Esse aí é um deles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24825, 'nvi', 'Marcos', 'mc', 14, 70, 'De novo ele negou. Pouco tempo depois, os que estavam sentados ali perto disseram a Pedro: "Certamente você é um deles. Você é galileu! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24826, 'nvi', 'Marcos', 'mc', 14, 71, 'Ele começou a se amaldiçoar e a jurar: "Não conheço o homem de quem vocês estão falando! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24827, 'nvi', 'Marcos', 'mc', 14, 72, 'E logo o galo cantou pela segunda vez. Então Pedro se lembrou da palavra que Jesus lhe tinha dito: "Antes que duas vezes cante o galo, você me negará três vezes". E se pôs a chorar.');
