INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24828, 'nvi', 'Marcos', 'mc', 15, 1, 'De manhã bem cedo, os chefes dos sacerdotes com os líderes religiosos, os mestres da lei e todo o Sinédrio chegaram a uma decisão. Amarrando Jesus, levaram-no e o entregaram a Pilatos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24829, 'nvi', 'Marcos', 'mc', 15, 2, '"Você é o rei dos judeus? ", perguntou Pilatos. "Tu o dizes", respondeu Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24830, 'nvi', 'Marcos', 'mc', 15, 3, 'Os chefes dos sacerdotes o acusavam de muitas coisas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24831, 'nvi', 'Marcos', 'mc', 15, 4, 'Então Pilatos lhe perguntou novamente: "Você não vai responder? Veja de quantas coisas o estão acusando".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24832, 'nvi', 'Marcos', 'mc', 15, 5, 'Mas Jesus não respondeu nada, e Pilatos ficou impressionado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24833, 'nvi', 'Marcos', 'mc', 15, 6, 'Por ocasião da festa, era costume soltar um prisioneiro que o povo pedisse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24834, 'nvi', 'Marcos', 'mc', 15, 7, 'Um homem chamado Barrabás estava na prisão com os rebeldes que haviam cometido assassinato durante uma rebelião.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24835, 'nvi', 'Marcos', 'mc', 15, 8, 'A multidão chegou e pediu a Pilatos que lhe fizesse o que costumava fazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24836, 'nvi', 'Marcos', 'mc', 15, 9, '"Vocês querem que eu lhes solte o rei dos judeus? ", perguntou Pilatos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24837, 'nvi', 'Marcos', 'mc', 15, 10, 'sabendo que fora por inveja que os chefes dos sacerdotes lhe haviam entregado Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24838, 'nvi', 'Marcos', 'mc', 15, 11, 'Mas os chefes dos sacerdotes incitaram a multidão a pedir que Pilatos, ao contrário, soltasse Barrabás.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24839, 'nvi', 'Marcos', 'mc', 15, 12, '"Então, que farei com aquele a quem vocês chamam rei dos judeus? ", perguntou-lhes Pilatos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24840, 'nvi', 'Marcos', 'mc', 15, 13, '"Crucifica-o", gritaram eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24841, 'nvi', 'Marcos', 'mc', 15, 14, '"Por quê? Que crime ele cometeu? ", perguntou Pilatos. Mas eles gritavam ainda mais: "Crucifica-o! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24842, 'nvi', 'Marcos', 'mc', 15, 15, 'Desejando agradar a multidão, Pilatos soltou-lhes Barrabás, mandou açoitar Jesus e o entregou para ser crucificado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24843, 'nvi', 'Marcos', 'mc', 15, 16, 'Os soldados levaram Jesus para dentro do palácio, isto é, ao Pretório e reuniram toda a tropa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24844, 'nvi', 'Marcos', 'mc', 15, 17, 'Vestiram-no com um manto de púrpura, depois fizeram uma coroa de espinhos e a colocaram nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24845, 'nvi', 'Marcos', 'mc', 15, 18, 'E começaram a saudá-lo: "Salve, rei dos judeus! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24846, 'nvi', 'Marcos', 'mc', 15, 19, 'Batiam-lhe na cabeça com uma vara e cuspiam nele. Ajoelhavam-se e lhe prestavam adoração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24847, 'nvi', 'Marcos', 'mc', 15, 20, 'Depois de terem zombado dele, tiraram-lhe o manto de púrpura e vestiram-lhe suas próprias roupas. Então o levaram para fora, a fim de crucificá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24848, 'nvi', 'Marcos', 'mc', 15, 21, 'Certo homem de Cirene, chamado Simão, pai de Alexandre e de Rufo, passava por ali, chegando do campo. Eles o forçaram a carregar a cruz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24849, 'nvi', 'Marcos', 'mc', 15, 22, 'Levaram Jesus ao lugar chamado Gólgota, que quer dizer Lugar da Caveira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24850, 'nvi', 'Marcos', 'mc', 15, 23, 'Então lhe deram vinho misturado com mirra, mas ele não o bebeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24851, 'nvi', 'Marcos', 'mc', 15, 24, 'E o crucificaram. Dividindo as roupas dele, tiraram sortes para saber com o que cada um iria ficar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24852, 'nvi', 'Marcos', 'mc', 15, 25, 'Eram nove horas da manhã quando o crucificaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24853, 'nvi', 'Marcos', 'mc', 15, 26, 'E assim estava escrito na acusação contra ele: O REI DOS JUDEUS.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24854, 'nvi', 'Marcos', 'mc', 15, 27, 'Com ele crucificaram dois ladrões, um à sua direita e outro à sua esquerda,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24855, 'nvi', 'Marcos', 'mc', 15, 28, 'e cumpriu-se a Escritura que diz: "Ele foi contado entre os transgressores".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24856, 'nvi', 'Marcos', 'mc', 15, 29, 'Os que passavam lançavam-lhe insultos, balançando a cabeça e dizendo: "Ora, você que destrói o templo e o reedifica em três dias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24857, 'nvi', 'Marcos', 'mc', 15, 30, 'desça da cruz e salve-se a si mesmo! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24858, 'nvi', 'Marcos', 'mc', 15, 31, 'Da mesma forma, os chefes dos sacerdotes e os mestres da lei zombavam dele entre si, dizendo: "Salvou os outros, mas não é capaz de salvar a si mesmo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24859, 'nvi', 'Marcos', 'mc', 15, 32, 'O Cristo, o Rei de Israel... Desça da cruz, para que o vejamos e creiamos! " Os que foram crucificados com ele também o insultavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24860, 'nvi', 'Marcos', 'mc', 15, 33, 'E houve trevas sobre toda a terra, do meio dia às três horas da tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24861, 'nvi', 'Marcos', 'mc', 15, 34, 'Por volta das três horas da tarde, Jesus bradou em alta voz: "Eloí, Eloí, lamá sabactâni? " que significa: "Meu Deus! Meu Deus! Por que me abandonaste? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24862, 'nvi', 'Marcos', 'mc', 15, 35, 'Quando alguns dos que estavam presentes ouviram isso, disseram: "Ouçam! Ele está chamando Elias".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24863, 'nvi', 'Marcos', 'mc', 15, 36, 'Um deles correu, embebeu uma esponja em vinagre, colocou-a na ponta de uma vara e deu-a a Jesus para beber. E disse: "Deixem-no. Vejamos se Elias vem tirá-lo daí".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24864, 'nvi', 'Marcos', 'mc', 15, 37, 'Mas Jesus, com um alto brado, expirou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24865, 'nvi', 'Marcos', 'mc', 15, 38, 'E o véu do santuário rasgou-se em duas partes, de alto a baixo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24866, 'nvi', 'Marcos', 'mc', 15, 39, 'Quando o centurião que estava em frente de Jesus ouviu o seu brado e viu como ele morreu, disse: "Realmente este homem era o Filho de Deus! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24867, 'nvi', 'Marcos', 'mc', 15, 40, 'Algumas mulheres estavam observando de longe. Entre elas estavam Maria Madalena, Salomé e Maria, mãe de Tiago, o mais jovem, e de José.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24868, 'nvi', 'Marcos', 'mc', 15, 41, 'Na Galiléia elas tinham seguido e servido a Jesus. Muitas outras mulheres que tinham subido com ele para Jerusalém também estavam ali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24869, 'nvi', 'Marcos', 'mc', 15, 42, 'Era o Dia da Preparação, isto é, a véspera do sábado. Ao cair da tarde,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24870, 'nvi', 'Marcos', 'mc', 15, 43, 'José de Arimatéia, membro de destaque do Sinédrio, que também esperava o Reino de Deus, dirigiu-se corajosamente a Pilatos e pediu o corpo de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24871, 'nvi', 'Marcos', 'mc', 15, 44, 'Pilatos ficou surpreso ao ouvir que ele já tinha morrido. Chamando o centurião, perguntou-lhe se Jesus já tinha morrido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24872, 'nvi', 'Marcos', 'mc', 15, 45, 'Sendo informado pelo centurião, entregou o corpo a José.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24873, 'nvi', 'Marcos', 'mc', 15, 46, 'Então José comprou um lençol de linho, baixou o corpo da cruz, envolveu-o no lençol e o colocou num sepulcro cavado na rocha. Depois, fez rolar uma pedra sobre a entrada do sepulcro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24874, 'nvi', 'Marcos', 'mc', 15, 47, 'Maria Madalena e Maria, mãe de José, viram onde ele fora colocado.');
