INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24262, 'nvi', 'Marcos', 'mc', 2, 1, 'Poucos dias depois, tendo Jesus entrado novamente em Cafarnaum, o povo ouviu falar que ele estava em casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24263, 'nvi', 'Marcos', 'mc', 2, 2, 'Então muita gente se reuniu ali, de forma que não havia lugar nem junto à porta; e ele lhes pregava a palavra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24264, 'nvi', 'Marcos', 'mc', 2, 3, 'Vieram alguns homens, trazendo-lhe um paralítico, carregado por quatro deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24265, 'nvi', 'Marcos', 'mc', 2, 4, 'Não podendo levá-lo até Jesus, por causa da multidão, removeram parte da cobertura do lugar onde Jesus estava e, através de uma abertura no teto, baixaram a maca em que estava deitado o paralítico.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24266, 'nvi', 'Marcos', 'mc', 2, 5, 'Vendo a fé que eles tinham, Jesus disse ao paralítico: "Filho, os seus pecados estão perdoados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24267, 'nvi', 'Marcos', 'mc', 2, 6, 'Estavam sentados ali alguns mestres da lei, raciocinando em seu íntimo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24268, 'nvi', 'Marcos', 'mc', 2, 7, '"Por que esse homem fala assim? Está blasfemando! Quem pode perdoar pecados, a não ser somente Deus? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24269, 'nvi', 'Marcos', 'mc', 2, 8, 'Jesus percebeu logo em seu espírito que era isso que eles estavam pensando e lhes disse: "Por que vocês estão remoendo essas coisas em seus corações?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24270, 'nvi', 'Marcos', 'mc', 2, 9, 'Que é mais fácil dizer ao paralítico: ‘Os seus pecados estão perdoados’, ou: ‘Levante-se, pegue a sua maca e ande’?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24271, 'nvi', 'Marcos', 'mc', 2, 10, 'Mas, para que vocês saibam que o Filho do homem tem na terra autoridade para perdoar pecados — disse ao paralítico —');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24272, 'nvi', 'Marcos', 'mc', 2, 11, 'eu lhe digo: Levante-se, pegue a sua maca e vá para casa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24273, 'nvi', 'Marcos', 'mc', 2, 12, 'Ele se levantou, pegou a maca e saiu à vista de todos. Estes ficaram atônitos e glorificaram a Deus, dizendo: "Nunca vimos nada igual! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24274, 'nvi', 'Marcos', 'mc', 2, 13, 'Jesus saiu outra vez para beira-mar. Uma grande multidão aproximou-se, e ele começou a ensiná-los.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24275, 'nvi', 'Marcos', 'mc', 2, 14, 'Passando por ali, viu Levi, filho de Alfeu, sentado na coletoria, e disse-lhe: "Siga-me". Levi levantou-se e o seguiu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24276, 'nvi', 'Marcos', 'mc', 2, 15, 'Durante uma refeição na casa de Levi, muitos publicanos e "pecadores" estavam comendo com Jesus e seus discípulos, pois havia muitos que o seguiam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24277, 'nvi', 'Marcos', 'mc', 2, 16, 'Quando os mestres da lei que eram fariseus o viram comendo com "pecadores" e publicanos, perguntaram aos discípulos de Jesus: "Por que ele come com publicanos e ‘pecadores’? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24278, 'nvi', 'Marcos', 'mc', 2, 17, 'Ouvindo isso, Jesus lhes disse: "Não são os que têm saúde que precisam de médico, mas sim os doentes. Eu não vim para chamar justos, mas pecadores".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24279, 'nvi', 'Marcos', 'mc', 2, 18, 'Os discípulos de João e os fariseus estavam jejuando. Algumas pessoas vieram a Jesus e lhe perguntaram: "Por que os discípulos de João e os dos fariseus jejuam, mas os teus não? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24280, 'nvi', 'Marcos', 'mc', 2, 19, 'Jesus respondeu: "Como podem os convidados do noivo jejuar enquanto este está com eles? Não podem, enquanto o têm consigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24281, 'nvi', 'Marcos', 'mc', 2, 20, 'Mas virão dias quando o noivo lhes será tirado; e nesse tempo jejuarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24282, 'nvi', 'Marcos', 'mc', 2, 21, '"Ninguém põe remendo de pano novo em roupa velha, pois o remendo forçará a roupa, tornando pior o rasgo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24283, 'nvi', 'Marcos', 'mc', 2, 22, 'E ninguém põe vinho novo em vasilhas de couro velhas; se o fizer, o vinho rebentará as vasilhas, e tanto o vinho quanto as vasilhas se estragarão. Pelo contrário, põe-se vinho novo em vasilhas de couro novas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24284, 'nvi', 'Marcos', 'mc', 2, 23, 'Certo sábado Jesus estava passando pelas lavouras de cereal. Enquanto caminhavam, seus discípulos começaram a colher espigas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24285, 'nvi', 'Marcos', 'mc', 2, 24, 'Os fariseus lhe perguntaram: "Olha, por que eles estão fazendo o que não é permitido no sábado? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24286, 'nvi', 'Marcos', 'mc', 2, 25, 'Ele respondeu: "Vocês nunca leram o que fez Davi quando ele e seus companheiros estavam necessitados e com fome?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24287, 'nvi', 'Marcos', 'mc', 2, 26, 'Nos dias de Abiatar, o sumo sacerdote, ele entrou na casa de Deus e comeu os pães da Presença, que apenas aos sacerdotes era permitido comer, e os deu também aos seus companheiros".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24288, 'nvi', 'Marcos', 'mc', 2, 27, 'E então lhes disse: "O sábado foi feito por causa do homem, e não o homem por causa do sábado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24289, 'nvi', 'Marcos', 'mc', 2, 28, 'Assim, pois, o Filho do homem é Senhor até mesmo do sábado".');
