INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24290, 'nvi', 'Marcos', 'mc', 3, 1, 'Noutra ocasião ele entrou na sinagoga, e estava ali um homem com uma das mãos atrofiada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24291, 'nvi', 'Marcos', 'mc', 3, 2, 'Alguns deles estavam procurando um motivo para acusar Jesus; por isso o observavam atentamente, para ver se ele iria curá-lo no sábado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24292, 'nvi', 'Marcos', 'mc', 3, 3, 'Jesus disse ao homem da mão atrofiada: "Levante-se e venha para o meio".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24293, 'nvi', 'Marcos', 'mc', 3, 4, 'Depois Jesus lhes perguntou: "O que é permitido fazer no sábado: o bem ou o mal, salvar a vida ou matar? " Mas eles permaneceram em silêncio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24294, 'nvi', 'Marcos', 'mc', 3, 5, 'Irado, olhou para os que estavam à sua volta e, profundamente entristecido por causa dos seus corações endurecidos, disse ao homem: "Estenda a mão". Ele a estendeu, e ela foi restaurada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24295, 'nvi', 'Marcos', 'mc', 3, 6, 'Então os fariseus saíram e começaram a conspirar com os herodianos contra Jesus, sobre como poderiam matá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24296, 'nvi', 'Marcos', 'mc', 3, 7, 'Jesus retirou-se com os seus discípulos para o mar, e uma grande multidão vinda da Galiléia o seguia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24297, 'nvi', 'Marcos', 'mc', 3, 8, 'Quando ouviram a respeito de tudo o que ele estava fazendo, muitas pessoas procedentes da Judéia, de Jerusalém, da Iduméia e das regiões do outro lado do Jordão e dos arredores de Tiro e de Sidom foram atrás dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24298, 'nvi', 'Marcos', 'mc', 3, 9, 'Por causa da multidão, ele disse aos discípulos que lhe preparassem um pequeno barco, para evitar que o comprimissem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24299, 'nvi', 'Marcos', 'mc', 3, 10, 'Pois ele havia curado a muitos, de modo que os que sofriam de doenças ficavam se empurrando para conseguir tocar nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24300, 'nvi', 'Marcos', 'mc', 3, 11, 'Sempre que os espíritos imundos o viam, prostravam-se diante dele e gritavam: "Tu és o Filho de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24301, 'nvi', 'Marcos', 'mc', 3, 12, 'Mas ele lhes dava ordens severas para que não dissessem quem ele era.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24302, 'nvi', 'Marcos', 'mc', 3, 13, 'Jesus subiu a um monte e chamou a si aqueles que ele quis, os quais vieram para junto dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24303, 'nvi', 'Marcos', 'mc', 3, 14, 'Escolheu doze, designando-os como apóstolos, para que estivessem com ele, os enviasse a pregar');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24304, 'nvi', 'Marcos', 'mc', 3, 15, 'e tivessem autoridade para expulsar demônios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24305, 'nvi', 'Marcos', 'mc', 3, 16, 'Estes são os doze que ele escolheu: Simão, a quem deu o nome de Pedro;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24306, 'nvi', 'Marcos', 'mc', 3, 17, 'Tiago, filho de Zebedeu, e João, seu irmão, aos quais deu o nome de Boanerges, que significa filhos do trovão;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24307, 'nvi', 'Marcos', 'mc', 3, 18, 'André; Filipe; Bartolomeu; Mateus; Tomé; Tiago, filho de Alfeu; Tadeu; Simão, o zelote,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24308, 'nvi', 'Marcos', 'mc', 3, 19, 'e Judas Iscariotes, que o traiu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24309, 'nvi', 'Marcos', 'mc', 3, 20, 'Então Jesus entrou numa casa, e novamente reuniu-se ali uma multidão, de modo que ele e os seus discípulos não conseguiam nem comer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24310, 'nvi', 'Marcos', 'mc', 3, 21, 'Quando seus familiares ouviram falar disso, saíram para apoderar-se dele, pois diziam: "Ele está fora de si".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24311, 'nvi', 'Marcos', 'mc', 3, 22, 'E os mestres da lei que haviam descido de Jerusalém diziam: "Ele está com Belzebu! Pelo príncipe dos demônios é que ele expulsa demônios".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24312, 'nvi', 'Marcos', 'mc', 3, 23, 'Então Jesus os chamou e lhes falou por parábolas: "Como pode Satanás expulsar Satanás?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24313, 'nvi', 'Marcos', 'mc', 3, 24, 'Se um reino estiver dividido contra si mesmo, não poderá subsistir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24314, 'nvi', 'Marcos', 'mc', 3, 25, 'Se uma casa estiver dividida contra si mesma, também não poderá subsistir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24315, 'nvi', 'Marcos', 'mc', 3, 26, 'E se Satanás se opuser a si mesmo e estiver dividido, não poderá subsistir; chegou o seu fim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24316, 'nvi', 'Marcos', 'mc', 3, 27, 'De fato, ninguém pode entrar na casa do homem forte e levar dali os seus bens, sem que antes o amarre. Só então poderá roubar a casa dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24317, 'nvi', 'Marcos', 'mc', 3, 28, 'Eu lhes asseguro que todos os pecados e blasfêmias dos homens lhes serão perdoados,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24318, 'nvi', 'Marcos', 'mc', 3, 29, 'mas quem blasfemar contra o Espírito Santo nunca terá perdão: é culpado de pecado eterno".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24319, 'nvi', 'Marcos', 'mc', 3, 30, 'Jesus falou isso porque eles estavam dizendo: "Ele está com um espírito imundo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24320, 'nvi', 'Marcos', 'mc', 3, 31, 'Então chegaram a mãe e os irmãos de Jesus. Ficando do lado de fora, mandaram alguém chamá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24321, 'nvi', 'Marcos', 'mc', 3, 32, 'Havia muita gente assentada ao seu redor; e lhe disseram: "Tua mãe e teus irmãos estão lá fora e te procuram".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24322, 'nvi', 'Marcos', 'mc', 3, 33, '"Quem é minha mãe, e quem são meus irmãos? ", perguntou ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24323, 'nvi', 'Marcos', 'mc', 3, 34, 'Então olhou para os que estavam assentados ao seu redor e disse: "Aqui estão minha mãe e meus irmãos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24324, 'nvi', 'Marcos', 'mc', 3, 35, 'Quem faz a vontade de Deus, este é meu irmão, minha irmã e minha mãe".');
