INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24325, 'nvi', 'Marcos', 'mc', 4, 1, 'Novamente Jesus começou a ensinar à beira-mar. Reuniu-se ao seu redor uma multidão tão grande que ele teve que entrar num barco e assentar-se nele. O barco estava no mar, enquanto todo o povo ficava na beira da praia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24326, 'nvi', 'Marcos', 'mc', 4, 2, 'Ele lhes ensinava muitas coisas por parábolas, dizendo em seu ensino:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24327, 'nvi', 'Marcos', 'mc', 4, 3, '"Ouçam! O semeador saiu a semear.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24328, 'nvi', 'Marcos', 'mc', 4, 4, 'Enquanto lançava a semente, parte dela caiu à beira do caminho, e as aves vieram e a comeram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24329, 'nvi', 'Marcos', 'mc', 4, 5, 'Parte dela caiu em terreno pedregoso, onde não havia muita terra; e logo brotou, porque a terra não era profunda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24330, 'nvi', 'Marcos', 'mc', 4, 6, 'Mas quando saiu o sol, as plantas se queimaram e secaram, porque não tinham raiz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24331, 'nvi', 'Marcos', 'mc', 4, 7, 'Outra parte caiu entre espinhos, que cresceram e sufocaram as plantas, de forma que ela não deu fruto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24332, 'nvi', 'Marcos', 'mc', 4, 8, 'Outra ainda caiu em boa terra, germinou, cresceu e deu boa colheita, a trinta, sessenta e até cem por um".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24333, 'nvi', 'Marcos', 'mc', 4, 9, 'A seguir Jesus acrescentou: "Aquele que tem ouvidos para ouvir, ouça! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24334, 'nvi', 'Marcos', 'mc', 4, 10, 'Quando ele ficou sozinho, os Doze e os outros que estavam ao seu redor lhe fizeram perguntas acerca das parábolas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24335, 'nvi', 'Marcos', 'mc', 4, 11, 'Ele lhes disse: "A vocês foi dado o mistério do Reino de Deus, mas aos que estão fora tudo é dito por parábolas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24336, 'nvi', 'Marcos', 'mc', 4, 12, 'a fim de que, ‘ainda que vejam, não percebam, ainda que ouçam, não entendam; de outro modo, poderiam converter-se e ser perdoados! ’"');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24337, 'nvi', 'Marcos', 'mc', 4, 13, 'Então Jesus lhes perguntou: "Vocês não entendem esta parábola? Como, então, compreenderão todas as outras parábolas?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24338, 'nvi', 'Marcos', 'mc', 4, 14, 'O semeador semeia a palavra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24339, 'nvi', 'Marcos', 'mc', 4, 15, 'Algumas pessoas são como a semente à beira do caminho, onde a palavra é semeada. Logo que a ouvem, Satanás vem e retira a palavra nelas semeada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24340, 'nvi', 'Marcos', 'mc', 4, 16, 'Outras, como a semente lançada em terreno pedregoso, ouvem a palavra e logo a recebem com alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24341, 'nvi', 'Marcos', 'mc', 4, 17, 'Todavia, visto que não têm raiz em si mesmas, permanecem por pouco tempo. Quando surge alguma tribulação ou perseguição por causa da palavra, logo a abandonam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24342, 'nvi', 'Marcos', 'mc', 4, 18, 'Outras ainda, como a semente lançada entre espinhos, ouvem a palavra;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24343, 'nvi', 'Marcos', 'mc', 4, 19, 'mas quando chegam as preocupações desta vida, o engano das riquezas e os anseios por outras coisas, sufocam a palavra, tornando-a infrutífera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24344, 'nvi', 'Marcos', 'mc', 4, 20, 'Outras pessoas são como a semente lançada em boa terra: ouvem a palavra, aceitam-na e dão uma colheita de trinta, sessenta e até cem por um".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24345, 'nvi', 'Marcos', 'mc', 4, 21, 'Ele lhes disse: "Quem traz uma candeia para ser colocada debaixo de uma vasilha ou de uma cama? Acaso não a coloca num lugar apropriado?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24346, 'nvi', 'Marcos', 'mc', 4, 22, 'Porque não há nada oculto, senão para ser revelado, e nada escondido senão para ser trazido à luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24347, 'nvi', 'Marcos', 'mc', 4, 23, 'Se alguém tem ouvidos para ouvir, ouça!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24348, 'nvi', 'Marcos', 'mc', 4, 24, '"Considerem atentamente o que vocês estão ouvindo", continuou ele. "Com a medida com que medirem, vocês serão medidos; e ainda mais lhes acrescentarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24349, 'nvi', 'Marcos', 'mc', 4, 25, 'A quem tiver, mais lhe será dado; de quem não tiver, até o que tem lhe será tirado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24350, 'nvi', 'Marcos', 'mc', 4, 26, 'Ele prosseguiu dizendo: "O Reino de Deus é semelhante a um homem que lança a semente sobre a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24351, 'nvi', 'Marcos', 'mc', 4, 27, 'Noite e dia, quer ele durma quer se levante, a semente germina e cresce, embora ele não saiba como.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24352, 'nvi', 'Marcos', 'mc', 4, 28, 'A terra por si própria produz o grão: primeiro o talo, depois a espiga e, então, o grão cheio na espiga.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24353, 'nvi', 'Marcos', 'mc', 4, 29, 'Logo que o grão fica maduro, o homem lhe passa a foice, porque chegou a colheita".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24354, 'nvi', 'Marcos', 'mc', 4, 30, 'Novamente ele disse: "Com que compararemos o Reino de Deus? Que parábola usaremos para descrevê-lo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24355, 'nvi', 'Marcos', 'mc', 4, 31, 'É como um grão de mostarda, que, quando plantada, é a menor semente de todas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24356, 'nvi', 'Marcos', 'mc', 4, 32, 'No entanto, plantada, ela cresce e se torna a maior de todas as hortaliças, com ramos tão grandes que as aves do céu podem abrigar-se à sua sombra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24357, 'nvi', 'Marcos', 'mc', 4, 33, 'Com muitas parábolas semelhantes Jesus lhes anunciava a palavra, tanto quanto podiam receber.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24358, 'nvi', 'Marcos', 'mc', 4, 34, 'Não lhes dizia nada sem usar alguma parábola. Quando, porém, estava a sós com os seus discípulos, explicava-lhes tudo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24359, 'nvi', 'Marcos', 'mc', 4, 35, 'Naquele dia, ao anoitecer, disse ele aos seus discípulos: "Vamos atravessar para o outro lado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24360, 'nvi', 'Marcos', 'mc', 4, 36, 'Deixando a multidão, eles o levaram no barco, assim como estava. Outros barcos também o acompanhavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24361, 'nvi', 'Marcos', 'mc', 4, 37, 'Levantou-se um forte vendaval, e as ondas se lançavam sobre o barco, de forma que este foi se enchendo de água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24362, 'nvi', 'Marcos', 'mc', 4, 38, 'Jesus estava na popa, dormindo com a cabeça sobre um travesseiro. Os discípulos o acordaram e clamaram: "Mestre, não te importas que morramos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24363, 'nvi', 'Marcos', 'mc', 4, 39, 'Ele se levantou, repreendeu o vento e disse ao mar: "Aquiete-se! Acalme-se! " O vento se aquietou, e fez-se completa bonança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24364, 'nvi', 'Marcos', 'mc', 4, 40, 'Então perguntou aos seus discípulos: "Por que vocês estão com tanto medo? Ainda não têm fé? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24365, 'nvi', 'Marcos', 'mc', 4, 41, 'Eles estavam apavorados e perguntavam uns aos outros: "Quem é este que até o vento e o mar lhe obedecem? "');
