INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24366, 'nvi', 'Marcos', 'mc', 5, 1, 'Eles atravessaram o mar e foram para a região dos gerasenos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24367, 'nvi', 'Marcos', 'mc', 5, 2, 'Quando Jesus desembarcou, um homem com um espírito imundo veio dos sepulcros ao seu encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24368, 'nvi', 'Marcos', 'mc', 5, 3, 'Esse homem vivia nos sepulcros, e ninguém conseguia prendê-lo, nem mesmo com correntes;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24369, 'nvi', 'Marcos', 'mc', 5, 4, 'pois muitas vezes lhe haviam sido acorrentados pés e mãos, mas ele arrebentara as correntes e quebrara os ferros de seus pés. Ninguém era suficientemente forte para dominá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24370, 'nvi', 'Marcos', 'mc', 5, 5, 'Noite e dia ele andava gritando e cortando-se com pedras entre os sepulcros e nas colinas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24371, 'nvi', 'Marcos', 'mc', 5, 6, 'Quando ele viu Jesus de longe, correu e prostrou-se diante dele,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24372, 'nvi', 'Marcos', 'mc', 5, 7, 'e gritou em alta voz: "Que queres comigo, Jesus, Filho do Deus Altíssimo? Rogo-te por Deus que não me atormentes! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24373, 'nvi', 'Marcos', 'mc', 5, 8, 'Pois Jesus lhe tinha dito: "Saia deste homem, espírito imundo! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24374, 'nvi', 'Marcos', 'mc', 5, 9, 'Então Jesus lhe perguntou: "Qual é o seu nome? " "Meu nome é Legião", respondeu ele, "porque somos muitos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24375, 'nvi', 'Marcos', 'mc', 5, 10, 'E implorava a Jesus, com insistência, que não os mandasse sair daquela região.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24376, 'nvi', 'Marcos', 'mc', 5, 11, 'Uma grande manada de porcos estava pastando numa colina próxima.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24377, 'nvi', 'Marcos', 'mc', 5, 12, 'Os demônios imploraram a Jesus: "Manda-nos para os porcos, para que entremos neles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24378, 'nvi', 'Marcos', 'mc', 5, 13, 'Ele lhes deu permissão, e os espíritos imundos saíram e entraram nos porcos. A manada de cerca de dois mil porcos atirou-se precipício abaixo, em direção ao mar, e nele se afogou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24379, 'nvi', 'Marcos', 'mc', 5, 14, 'Os que cuidavam dos porcos fugiram e contaram esses fatos na cidade e nos campos, e o povo foi ver o que havia acontecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24380, 'nvi', 'Marcos', 'mc', 5, 15, 'Quando se aproximaram de Jesus, viram ali o homem que fora possesso da legião de demônios, assentado, vestido e em perfeito juízo; e ficaram com medo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24381, 'nvi', 'Marcos', 'mc', 5, 16, 'Os que o tinham visto contaram ao povo o que acontecera ao endemoninhado, e falaram também sobre os porcos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24382, 'nvi', 'Marcos', 'mc', 5, 17, 'Então o povo começou a suplicar a Jesus que saísse do território deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24383, 'nvi', 'Marcos', 'mc', 5, 18, 'Quando Jesus estava entrando no barco, o homem que estivera endemoninhado suplicava-lhe que o deixasse ir com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24384, 'nvi', 'Marcos', 'mc', 5, 19, 'Jesus não o permitiu, mas disse: "Vá para casa, para a sua família e anuncie-lhes quanto o Senhor fez por você e como teve misericórdia de você".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24385, 'nvi', 'Marcos', 'mc', 5, 20, 'Então, aquele homem se foi e começou a anunciar em Decápolis quanto Jesus tinha feito por ele. Todos ficavam admirados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24386, 'nvi', 'Marcos', 'mc', 5, 21, 'Tendo Jesus voltado de barco para a outra margem, uma grande multidão se reuniu ao seu redor, enquanto ele estava à beira do mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24387, 'nvi', 'Marcos', 'mc', 5, 22, 'Então chegou ali um dos dirigentes da sinagoga, chamado Jairo. Vendo Jesus, prostrou-se aos seus pés');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24388, 'nvi', 'Marcos', 'mc', 5, 23, 'e lhe implorou insistentemente: "Minha filhinha está morrendo! Vem, por favor, e impõe as mãos sobre ela, para que seja curada e viva".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24389, 'nvi', 'Marcos', 'mc', 5, 24, 'Jesus foi com ele. Uma grande multidão o seguia e o comprimia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24390, 'nvi', 'Marcos', 'mc', 5, 25, 'E estava ali certa mulher que havia doze anos vinha sofrendo de uma hemorragia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24391, 'nvi', 'Marcos', 'mc', 5, 26, 'Ela padecera muito sob o cuidado de vários médicos e gastara tudo o que tinha, mas, em vez de melhorar, piorava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24392, 'nvi', 'Marcos', 'mc', 5, 27, 'Quando ouviu falar de Jesus, chegou-se por trás dele, no meio da multidão, e tocou em seu manto,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24393, 'nvi', 'Marcos', 'mc', 5, 28, 'porque pensava: "Se eu tão-somente tocar em seu manto, ficarei curada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24394, 'nvi', 'Marcos', 'mc', 5, 29, 'Imediatamente cessou sua hemorragia e ela sentiu em seu corpo que estava livre do seu sofrimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24395, 'nvi', 'Marcos', 'mc', 5, 30, 'No mesmo instante, Jesus percebeu que dele havia saído poder, virou-se para a multidão e perguntou: "Quem tocou em meu manto? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24396, 'nvi', 'Marcos', 'mc', 5, 31, 'Responderam os seus discípulos: "Vês a multidão aglomerada ao teu redor e ainda perguntas: ‘Quem tocou em mim? ’ "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24397, 'nvi', 'Marcos', 'mc', 5, 32, 'Mas Jesus continuou olhando ao seu redor para ver quem tinha feito aquilo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24398, 'nvi', 'Marcos', 'mc', 5, 33, 'Então a mulher, sabendo o que lhe tinha acontecido, aproximou-se, prostrou-se aos seus pés e, tremendo de medo, contou-lhe toda a verdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24399, 'nvi', 'Marcos', 'mc', 5, 34, 'Então ele lhe disse: "Filha, a sua fé a curou! Vá em paz e fique livre do seu sofrimento".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24400, 'nvi', 'Marcos', 'mc', 5, 35, 'Enquanto Jesus ainda estava falando, chegaram algumas pessoas da casa de Jairo, o dirigente da sinagoga. "Sua filha morreu", disseram eles. "Não precisa mais incomodar o mestre! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24401, 'nvi', 'Marcos', 'mc', 5, 36, 'Não fazendo caso do que eles disseram, Jesus disse ao dirigente da sinagoga: "Não tenha medo; tão-somente creia".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24402, 'nvi', 'Marcos', 'mc', 5, 37, 'E não deixou ninguém segui-lo, senão Pedro, Tiago e João, irmão de Tiago.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24403, 'nvi', 'Marcos', 'mc', 5, 38, 'Quando chegaram à casa do dirigente da sinagoga, Jesus viu um alvoroço, com gente chorando e se lamentando em alta voz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24404, 'nvi', 'Marcos', 'mc', 5, 39, 'Então entrou e lhes disse: "Por que todo este alvoroço e lamento? A criança não está morta, mas dorme".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24405, 'nvi', 'Marcos', 'mc', 5, 40, 'Mas todos começaram a rir de Jesus. Ele, porém, ordenou que eles saíssem, tomou consigo o pai e a mãe da criança e os discípulos que estavam com ele, e entrou onde se encontrava a criança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24406, 'nvi', 'Marcos', 'mc', 5, 41, 'Tomou-a pela mão e lhe disse: "Talita cumi! ", que significa: "Menina, eu lhe ordeno, levante-se! ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24407, 'nvi', 'Marcos', 'mc', 5, 42, 'Imediatamente a menina, que tinha doze anos de idade, levantou-se e começou a andar. Isso os deixou atônitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24408, 'nvi', 'Marcos', 'mc', 5, 43, 'Ele deu ordens expressas para que não dissessem nada a ninguém e mandou que dessem a ela alguma coisa para comer.');
