INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24409, 'nvi', 'Marcos', 'mc', 6, 1, 'Jesus saiu dali e foi para a sua cidade, acompanhado dos seus discípulos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24410, 'nvi', 'Marcos', 'mc', 6, 2, 'Quando chegou o sábado, começou a ensinar na sinagoga, e muitos dos que o ouviam ficavam admirados. "De onde lhe vêm estas coisas? ", perguntavam eles. "Que sabedoria é esta que lhe foi dada? E estes milagres que ele faz?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24411, 'nvi', 'Marcos', 'mc', 6, 3, 'Não é este o carpinteiro, filho de Maria e irmão de Tiago, José, Judas e Simão? Não estão aqui conosco as suas irmãs? " E ficavam escandalizados por causa dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24412, 'nvi', 'Marcos', 'mc', 6, 4, 'Jesus lhes disse: "Só em sua própria terra, entre seus parentes e em sua própria casa, é que um profeta não tem honra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24413, 'nvi', 'Marcos', 'mc', 6, 5, 'E não pôde fazer ali nenhum milagre, exceto impor as mãos sobre alguns doentes e curá-los.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24414, 'nvi', 'Marcos', 'mc', 6, 6, 'E ficou admirado com a incredulidade deles. Então Jesus passou a percorrer os povoados, ensinando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24415, 'nvi', 'Marcos', 'mc', 6, 7, 'Chamando os Doze para junto de si, enviou-os de dois em dois e deu-lhes autoridade sobre os espíritos imundos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24416, 'nvi', 'Marcos', 'mc', 6, 8, 'Estas foram as suas instruções: "Não levem nada pelo caminho, a não ser um bordão. Não levem pão, nem saco de viagem, nem dinheiro em seus cintos;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24417, 'nvi', 'Marcos', 'mc', 6, 9, 'calcem sandálias, mas não levem túnica extra;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24418, 'nvi', 'Marcos', 'mc', 6, 10, 'sempre que entrarem numa casa, fiquem ali até partirem;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24419, 'nvi', 'Marcos', 'mc', 6, 11, 'e, se algum povoado não os receber nem os ouvir, sacudam a poeira dos seus pés quando saírem de lá, como testemunho contra eles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24420, 'nvi', 'Marcos', 'mc', 6, 12, 'Eles saíram e pregaram ao povo que se arrependesse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24421, 'nvi', 'Marcos', 'mc', 6, 13, 'Expulsavam muitos demônios, ungiam muitos doentes com óleo e os curavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24422, 'nvi', 'Marcos', 'mc', 6, 14, 'O rei Herodes ouviu falar dessas coisas, pois o nome de Jesus havia se tornado bem conhecido. Algumas pessoas estavam dizendo: "João Batista ressuscitou dos mortos! Por isso estão operando nele poderes miraculosos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24423, 'nvi', 'Marcos', 'mc', 6, 15, 'Outros diziam: "Ele é Elias". E ainda outros afirmavam: "Ele é um profeta, como um dos antigos profetas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24424, 'nvi', 'Marcos', 'mc', 6, 16, 'Mas quando Herodes ouviu essas coisas, disse: "João, o homem a quem decapitei, ressuscitou dos mortos! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24425, 'nvi', 'Marcos', 'mc', 6, 17, 'Pois o próprio Herodes tinha dado ordens para que prendessem João, o amarrassem e o colocassem na prisão, por causa de Herodias, mulher de Filipe, seu irmão, com a qual se casara.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24426, 'nvi', 'Marcos', 'mc', 6, 18, 'Porquanto João dizia a Herodes: "Não te é permitido viver com a mulher do teu irmão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24427, 'nvi', 'Marcos', 'mc', 6, 19, 'Assim, Herodias o odiava e queria matá-lo. Mas não podia fazê-lo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24428, 'nvi', 'Marcos', 'mc', 6, 20, 'porque Herodes temia a João e o protegia, sabendo que ele era um homem justo e santo; e quando o ouvia, ficava perplexo. Mesmo assim gostava de ouvi-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24429, 'nvi', 'Marcos', 'mc', 6, 21, 'Finalmente chegou uma ocasião oportuna. No seu aniversário, Herodes ofereceu um banquete aos seus líderes mais importantes, aos comandantes militares e às principais personalidades da Galiléia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24430, 'nvi', 'Marcos', 'mc', 6, 22, 'Quando a filha de Herodias entrou e dançou, agradou a Herodes e aos convidados. O rei disse à jovem: "Peça-me qualquer coisa que você quiser, e eu lhe darei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24431, 'nvi', 'Marcos', 'mc', 6, 23, 'E prometeu-lhe sob juramento: "Seja o que for que me pedir, eu lhe darei, até a metade do meu reino".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24432, 'nvi', 'Marcos', 'mc', 6, 24, 'Ela saiu e disse à sua mãe: "Que pedirei? " "A cabeça de João Batista", respondeu ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24433, 'nvi', 'Marcos', 'mc', 6, 25, 'Imediatamente a jovem apressou-se em apresentar-se ao rei com o pedido: "Desejo que me dês agora mesmo a cabeça de João Batista num prato".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24434, 'nvi', 'Marcos', 'mc', 6, 26, 'O rei ficou muito aflito, mas por causa do seu juramento e dos convidados, não quis negar o pedido à jovem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24435, 'nvi', 'Marcos', 'mc', 6, 27, 'Assim enviou imediatamente um carrasco com ordens para trazer a cabeça de João. O homem foi, decapitou João na prisão');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24436, 'nvi', 'Marcos', 'mc', 6, 28, 'e trouxe sua cabeça num prato. Ele a entregou à jovem, e esta a deu à sua mãe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24437, 'nvi', 'Marcos', 'mc', 6, 29, 'Tendo ouvido isso, os discípulos de João vieram, levaram o seu corpo e o colocaram num túmulo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24438, 'nvi', 'Marcos', 'mc', 6, 30, 'Os apóstolos reuniram-se a Jesus e lhe relataram tudo o que tinham feito e ensinado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24439, 'nvi', 'Marcos', 'mc', 6, 31, 'Havia muita gente indo e vindo, a ponto de eles não terem tempo para comer. Jesus lhes disse: "Venham comigo para um lugar deserto e descansem um pouco".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24440, 'nvi', 'Marcos', 'mc', 6, 32, 'Assim, eles se afastaram num barco para um lugar deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24441, 'nvi', 'Marcos', 'mc', 6, 33, 'Mas muitos dos que os viram retirar-se, tendo-os reconhecido, correram a pé de todas as cidades e chegaram lá antes deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24442, 'nvi', 'Marcos', 'mc', 6, 34, 'Quando Jesus saiu do barco e viu uma grande multidão, teve compaixão deles, porque eram como ovelhas sem pastor. Então começou a ensinar-lhes muitas coisas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24443, 'nvi', 'Marcos', 'mc', 6, 35, 'Já era tarde e, por isso, os seus discípulos aproximaram-se dele e disseram: "Este é um lugar deserto, e já é tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24444, 'nvi', 'Marcos', 'mc', 6, 36, 'Manda embora o povo para que possa ir aos campos e povoados vizinhos comprar algo para comer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24445, 'nvi', 'Marcos', 'mc', 6, 37, 'Ele, porém, respondeu: "Dêem-lhes vocês algo para comer". Eles lhe disseram: "Isto exigiria duzentos denários! Devemos gastar tanto dinheiro em pão e dar-lhes de comer? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24446, 'nvi', 'Marcos', 'mc', 6, 38, 'Perguntou ele: "Quantos pães vocês têm? Verifiquem". Quando ficaram sabendo, disseram: "Cinco pães e dois peixes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24447, 'nvi', 'Marcos', 'mc', 6, 39, 'Então Jesus ordenou que fizessem todo o povo assentar-se em grupos na grama verde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24448, 'nvi', 'Marcos', 'mc', 6, 40, 'Assim, eles se assentaram em grupos de cem e de cinqüenta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24449, 'nvi', 'Marcos', 'mc', 6, 41, 'Tomando os cinco pães e os dois peixes e, olhando para o céu, deu graças e partiu os pães. Em seguida, entregou-os aos seus discípulos para que os servissem ao povo. E também dividiu os dois peixes entre todos eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24450, 'nvi', 'Marcos', 'mc', 6, 42, 'Todos comeram e ficaram satisfeitos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24451, 'nvi', 'Marcos', 'mc', 6, 43, 'e os discípulos recolheram doze cestos cheios de pedaços de pão e de peixe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24452, 'nvi', 'Marcos', 'mc', 6, 44, 'Os que comeram foram cinco mil homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24453, 'nvi', 'Marcos', 'mc', 6, 45, 'Logo em seguida, Jesus insistiu com os discípulos para que entrassem no barco e fossem adiante dele para Betsaida, enquanto ele despedia a multidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24454, 'nvi', 'Marcos', 'mc', 6, 46, 'Tendo-a despedido, subiu a um monte para orar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24455, 'nvi', 'Marcos', 'mc', 6, 47, 'Ao anoitecer, o barco estava no meio do mar, e Jesus se achava sozinho em terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24456, 'nvi', 'Marcos', 'mc', 6, 48, 'Ele viu os discípulos remando com dificuldade, porque o vento soprava contra eles. Alta madrugada, Jesus dirigiu-se a eles, andando sobre o mar; e estava já a ponto de passar por eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24457, 'nvi', 'Marcos', 'mc', 6, 49, 'Quando o viram andando sobre o mar, pensaram que fosse um fantasma. Então gritaram,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24458, 'nvi', 'Marcos', 'mc', 6, 50, 'pois todos o tinham visto e ficam aterrorizados. Mas Jesus imediatamente lhes disse: "Coragem! Sou eu! Não tenham medo! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24459, 'nvi', 'Marcos', 'mc', 6, 51, 'Então subiu no barco para junto deles, e o vento se acalmou; e eles ficaram atônitos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24460, 'nvi', 'Marcos', 'mc', 6, 52, 'pois não tinham entendido o milagre dos pães. Seus corações estavam endurecidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24461, 'nvi', 'Marcos', 'mc', 6, 53, 'Depois de atravessarem o mar, chegaram a Genesaré e ali amarraram o barco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24462, 'nvi', 'Marcos', 'mc', 6, 54, 'Logo que desembarcaram, o povo reconheceu Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24463, 'nvi', 'Marcos', 'mc', 6, 55, 'Eles percorriam toda aquela região e levavam os doentes em macas, para onde ouviam que ele estava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24464, 'nvi', 'Marcos', 'mc', 6, 56, 'E aonde quer que ele fosse, povoados, cidades ou campos, levavam os doentes para as praças. Suplicavam-lhe que pudessem pelo menos tocar na borda do seu manto; e todos os que nele tocavam eram curados.');
