INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24465, 'nvi', 'Marcos', 'mc', 7, 1, 'Os fariseus e alguns dos mestres da lei, vindos de Jerusalém, reuniram-se a Jesus e');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24466, 'nvi', 'Marcos', 'mc', 7, 2, 'viram alguns dos seus discípulos comerem com as mãos "impuras", isto é, por lavar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24467, 'nvi', 'Marcos', 'mc', 7, 3, '( Os fariseus e todos os judeus não comem sem lavar as mãos cerimonialmente, apegando-se, assim, à tradição dos líderes religiosos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24468, 'nvi', 'Marcos', 'mc', 7, 4, 'Quando chegam da rua, não comem sem antes se lavarem. E observam muitas outras tradições, tais como o lavar de copos, jarros e vasilhas de metal. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24469, 'nvi', 'Marcos', 'mc', 7, 5, 'Então os fariseus e os mestres da lei perguntaram a Jesus: "Por que os seus discípulos não vivem de acordo com a tradição dos líderes religiosos, em vez de comerem o alimento com as mãos ‘impuras’? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24470, 'nvi', 'Marcos', 'mc', 7, 6, 'Ele respondeu: "Bem profetizou Isaías acerca de vocês, hipócritas; como está escrito: ‘Este povo me honra com os lábios, mas o seu coração está longe de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24471, 'nvi', 'Marcos', 'mc', 7, 7, 'Em vão me adoram; seus ensinamentos não passam de regras ensinadas por homens’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24472, 'nvi', 'Marcos', 'mc', 7, 8, 'Vocês negligenciam os mandamentos de Deus e se apegam às tradições dos homens".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24473, 'nvi', 'Marcos', 'mc', 7, 9, 'E disse-lhes: "Vocês estão sempre encontrando uma boa maneira para pôr de lado os mandamentos de Deus, a fim de obedecer às suas tradições!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24474, 'nvi', 'Marcos', 'mc', 7, 10, 'Pois Moisés disse: ‘Honra teu pai e tua mãe’, e ‘quem amaldiçoar seu pai ou sua mãe terá que ser executado’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24475, 'nvi', 'Marcos', 'mc', 7, 11, 'Mas vocês afirmam que se alguém disser a seu pai ou a sua mãe: ‘Qualquer ajuda que vocês poderiam receber de mim é Corbã’, isto é, uma oferta dedicada a Deus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24476, 'nvi', 'Marcos', 'mc', 7, 12, 'vocês o desobrigam de qualquer dever para com seu pai ou sua mãe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24477, 'nvi', 'Marcos', 'mc', 7, 13, 'Assim vocês anulam a palavra de Deus, por meio da tradição que vocês mesmos transmitiram. E fazem muitas coisas como essa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24478, 'nvi', 'Marcos', 'mc', 7, 14, 'Jesus chamou novamente a multidão para junto de si e disse: "Ouçam-me todos e entendam isto:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24479, 'nvi', 'Marcos', 'mc', 7, 15, 'não há nada fora do homem que, nele entrando, possa torná-lo ‘impuro’. Pelo contrário, o que sai do homem é que o torna ‘impuro’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24480, 'nvi', 'Marcos', 'mc', 7, 16, 'Se alguém tem ouvidos para ouvir, ouça! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24481, 'nvi', 'Marcos', 'mc', 7, 17, 'Depois de deixar a multidão e entrar em casa, os discípulos lhe pediram explicação da parábola.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24482, 'nvi', 'Marcos', 'mc', 7, 18, '"Será que vocês também não conseguem entender? ", perguntou-lhes Jesus. "Não percebem que nada que entre no homem pode torná-lo ‘impuro’?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24483, 'nvi', 'Marcos', 'mc', 7, 19, 'Porque não entra em seu coração, mas em seu estômago, sendo depois eliminado". Ao dizer isto, Jesus declarou "puros" todos os alimentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24484, 'nvi', 'Marcos', 'mc', 7, 20, 'E continuou: "O que sai do homem é que o torna ‘impuro’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24485, 'nvi', 'Marcos', 'mc', 7, 21, 'Pois do interior do coração dos homens vêm os maus pensamentos, as imoralidades sexuais, os roubos, os homicídios, os adultérios,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24486, 'nvi', 'Marcos', 'mc', 7, 22, 'as cobiças, as maldades, o engano, a devassidão, a inveja, a calúnia, a arrogância e a insensatez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24487, 'nvi', 'Marcos', 'mc', 7, 23, 'Todos esses males vêm de dentro e tornam o homem ‘impuro’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24488, 'nvi', 'Marcos', 'mc', 7, 24, 'Jesus saiu daquele lugar e foi para os arredores de Tiro e de Sidom. Entrou numa casa e não queria que ninguém o soubesse; contudo, não conseguiu manter em segredo a sua presença.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24489, 'nvi', 'Marcos', 'mc', 7, 25, 'De fato, logo que ouviu falar dele, certa mulher, cuja filha estava com um espírito imundo, veio e lançou-se aos seus pés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24490, 'nvi', 'Marcos', 'mc', 7, 26, 'A mulher era grega, siro-fenícia de origem, e rogava a Jesus que expulsasse de sua filha o demônio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24491, 'nvi', 'Marcos', 'mc', 7, 27, 'Ele lhe disse: "Deixe que primeiro os filhos comam até se fartar; pois não é correto tirar o pão dos filhos e lançá-lo aos cachorrinhos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24492, 'nvi', 'Marcos', 'mc', 7, 28, 'Ela respondeu: "Sim, Senhor, mas até os cachorrinhos, debaixo da mesa, comem das migalhas das crianças".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24493, 'nvi', 'Marcos', 'mc', 7, 29, 'Então ele lhe disse: "Por causa desta resposta, você pode ir; o demônio já saiu da sua filha".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24494, 'nvi', 'Marcos', 'mc', 7, 30, 'Ela foi para casa e encontrou sua filha deitada na cama, e o demônio já a tinha deixado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24495, 'nvi', 'Marcos', 'mc', 7, 31, 'A seguir Jesus saiu dos arredores de Tiro e atravessou Sidom, até o mar da Galiléia e a região de Decápolis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24496, 'nvi', 'Marcos', 'mc', 7, 32, 'Ali algumas pessoas lhe trouxeram um homem que era surdo e mal podia falar, suplicando que lhe impusesse as mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24497, 'nvi', 'Marcos', 'mc', 7, 33, 'Depois de levá-lo à parte, longe da multidão, Jesus colocou os dedos nos ouvidos dele. Em seguida, cuspiu e tocou na língua do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24498, 'nvi', 'Marcos', 'mc', 7, 34, 'Então voltou os olhos para os céus e, com um profundo suspiro, disse-lhe: "Efatá! ", que significa: Abra-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24499, 'nvi', 'Marcos', 'mc', 7, 35, 'Com isso, os ouvidos do homem se abriram, sua língua ficou livre e ele começou a falar corretamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24500, 'nvi', 'Marcos', 'mc', 7, 36, 'Jesus ordenou-lhes que não o contassem a ninguém. Contudo, quanto mais ele os proibia, mais eles falavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24501, 'nvi', 'Marcos', 'mc', 7, 37, 'O povo ficava simplesmente maravilhado e dizia: "Ele faz tudo muito bem. Faz até o surdo ouvir e o mudo falar".');
