INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24540, 'nvi', 'Marcos', 'mc', 9, 1, 'E lhes disse: "Garanto-lhes que alguns dos que aqui estão de modo nenhum experimentarão a morte, antes de verem o Reino de Deus vindo com poder".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24541, 'nvi', 'Marcos', 'mc', 9, 2, 'Seis dias depois, Jesus tomou consigo Pedro, Tiago e João e os levou a um alto monte, onde ficaram a sós. Ali ele foi transfigurado diante deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24542, 'nvi', 'Marcos', 'mc', 9, 3, 'Suas roupas se tornaram brancas, de um branco resplandecente, como nenhum lavandeiro no mundo seria capaz de branqueá-las.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24543, 'nvi', 'Marcos', 'mc', 9, 4, 'E apareceram diante deles Elias e Moisés, os quais conversavam com Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24544, 'nvi', 'Marcos', 'mc', 9, 5, 'Então Pedro disse a Jesus: "Mestre, é bom estarmos aqui. Façamos três tendas: uma para ti, uma para Moisés e uma para Elias".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24545, 'nvi', 'Marcos', 'mc', 9, 6, 'Ele não sabia o que dizer, pois estavam apavorados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24546, 'nvi', 'Marcos', 'mc', 9, 7, 'A seguir apareceu uma nuvem e os envolveu, e dela saiu uma voz, que disse: "Este é o meu Filho amado. Ouçam-no! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24547, 'nvi', 'Marcos', 'mc', 9, 8, 'Repentinamente, quando olharam ao redor, não viram mais ninguém, a não ser Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24548, 'nvi', 'Marcos', 'mc', 9, 9, 'Enquanto desciam do monte, Jesus lhes ordenou que não contassem a ninguém o que tinham visto, até que o Filho do homem tivesse ressuscitado dos mortos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24549, 'nvi', 'Marcos', 'mc', 9, 10, 'Eles guardaram o assunto apenas entre si, discutindo o que significaria "ressuscitar dos mortos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24550, 'nvi', 'Marcos', 'mc', 9, 11, 'E lhe perguntaram: "Por que os mestres da lei dizem que é necessário que Elias venha primeiro? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24551, 'nvi', 'Marcos', 'mc', 9, 12, 'Jesus respondeu: "De fato, Elias vem primeiro e restaura todas as coisas. Então, por que está escrito que é necessário que o Filho do homem sofra muito e seja rejeitado com desprezo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24552, 'nvi', 'Marcos', 'mc', 9, 13, 'Mas eu lhes digo: Elias já veio, e fizeram com ele tudo o que quiseram, como está escrito a seu respeito".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24553, 'nvi', 'Marcos', 'mc', 9, 14, 'Quando chegaram onde estavam os outros discípulos, viram uma grande multidão ao redor deles e os mestres da lei discutindo com eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24554, 'nvi', 'Marcos', 'mc', 9, 15, 'Logo que todo o povo viu Jesus, ficou muito surpreso e correu para saudá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24555, 'nvi', 'Marcos', 'mc', 9, 16, 'Perguntou Jesus: "O que vocês estão discutindo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24556, 'nvi', 'Marcos', 'mc', 9, 17, 'Um homem, no meio da multidão, respondeu: "Mestre, eu te trouxe o meu filho, que está com um espírito que o impede de falar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24557, 'nvi', 'Marcos', 'mc', 9, 18, 'Onde quer que o apanhe, joga-o no chão. Ele espuma pela boca, range os dentes e fica rígido. Pedi aos teus discípulos que expulsassem o espírito, mas eles não conseguiram".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24558, 'nvi', 'Marcos', 'mc', 9, 19, 'Respondeu Jesus: "Ó geração incrédula, até quando estarei com vocês? Até quando terei que suportá-los? Tragam-me o menino".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24559, 'nvi', 'Marcos', 'mc', 9, 20, 'Então, eles o trouxeram. Quando o espírito viu Jesus, imediatamente causou uma convulsão no menino. Este caiu no chão e começou a rolar, espumando pela boca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24560, 'nvi', 'Marcos', 'mc', 9, 21, 'Jesus perguntou ao pai do menino: "Há quanto tempo ele está assim? " "Desde a infância", respondeu ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24561, 'nvi', 'Marcos', 'mc', 9, 22, '"Muitas vezes o tem lançado no fogo e na água para matá-lo. Mas, se podes fazer alguma coisa, tem compaixão de nós e ajuda-nos. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24562, 'nvi', 'Marcos', 'mc', 9, 23, '"Se podes? ", disse Jesus. "Tudo é possível àquele que crê. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24563, 'nvi', 'Marcos', 'mc', 9, 24, 'Imediatamente o pai do menino exclamou: "Creio, ajuda-me a vencer a minha incredulidade! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24564, 'nvi', 'Marcos', 'mc', 9, 25, 'Quando Jesus viu que uma multidão estava se ajuntando, repreendeu o espírito imundo, dizendo: "Espírito mudo e surdo, eu ordeno que o deixe e nunca mais entre nele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24565, 'nvi', 'Marcos', 'mc', 9, 26, 'O espírito gritou, agitou-o violentamente e saiu. O menino ficou como morto, a ponto de muitos dizerem: "Ele morreu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24566, 'nvi', 'Marcos', 'mc', 9, 27, 'Mas Jesus tomou-o pela mão e o levantou, e ele ficou em pé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24567, 'nvi', 'Marcos', 'mc', 9, 28, 'Depois de Jesus ter entrado em casa, seus discípulos lhe perguntaram em particular: "Por que não conseguimos expulsá-lo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24568, 'nvi', 'Marcos', 'mc', 9, 29, 'Ele respondeu: "Essa espécie só sai pela oração e pelo jejum".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24569, 'nvi', 'Marcos', 'mc', 9, 30, 'Eles saíram daquele lugar e atravessaram a Galiléia. Jesus não queria que ninguém soubesse onde eles estavam,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24570, 'nvi', 'Marcos', 'mc', 9, 31, 'porque estava ensinando os seus discípulos. E lhes dizia: "O Filho do homem está para ser entregue nas mãos dos homens. Eles o matarão, e três dias depois ele ressuscitará".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24571, 'nvi', 'Marcos', 'mc', 9, 32, 'Mas eles não entendiam o que ele queria dizer e tinham receio de perguntar-lhe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24572, 'nvi', 'Marcos', 'mc', 9, 33, 'E chegaram a Cafarnaum. Quando ele estava em casa, perguntou-lhes: "O que vocês estavam discutindo no caminho? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24573, 'nvi', 'Marcos', 'mc', 9, 34, 'Mas eles guardaram silêncio, porque no caminho haviam discutido sobre quem era o maior.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24574, 'nvi', 'Marcos', 'mc', 9, 35, 'Assentando-se, Jesus chamou os Doze e disse: "Se alguém quiser ser o primeiro, será o último, e servo de todos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24575, 'nvi', 'Marcos', 'mc', 9, 36, 'E, tomando uma criança, colocou-a no meio deles. Pegando-a nos braços, disse-lhes:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24576, 'nvi', 'Marcos', 'mc', 9, 37, '"Quem recebe uma destas crianças em meu nome, está me recebendo; e quem me recebe, não está apenas me recebendo, mas também àquele que me enviou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24577, 'nvi', 'Marcos', 'mc', 9, 38, '"Mestre", disse João, "vimos um homem expulsando demônios em teu nome e procuramos impedi-lo, porque ele não era um dos nossos. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24578, 'nvi', 'Marcos', 'mc', 9, 39, '"Não o impeçam", disse Jesus. "Ninguém que faça um milagre em meu nome, pode falar mal de mim logo em seguida,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24579, 'nvi', 'Marcos', 'mc', 9, 40, 'pois quem não é contra nós está a nosso favor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24580, 'nvi', 'Marcos', 'mc', 9, 41, 'Eu lhes digo a verdade: Quem lhes der um copo de água em meu nome, por vocês pertencerem a Cristo, de modo nenhum perderá a sua recompensa. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24581, 'nvi', 'Marcos', 'mc', 9, 42, '"Se alguém fizer tropeçar um destes pequeninos que crêem em mim, seria melhor que fosse lançado no mar com uma grande pedra amarrada no pescoço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24582, 'nvi', 'Marcos', 'mc', 9, 43, 'Se a sua mão o fizer tropeçar, corte-a. É melhor entrar na vida mutilado do que, tendo as duas mãos, ir para o inferno, onde o fogo nunca se apaga,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24583, 'nvi', 'Marcos', 'mc', 9, 44, 'onde o seu verme não morre, e o fogo não se apaga.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24584, 'nvi', 'Marcos', 'mc', 9, 45, 'E se o seu pé o fizer tropeçar, corte-o. É melhor entrar na vida aleijado do que, tendo os dois pés, ser lançado no inferno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24585, 'nvi', 'Marcos', 'mc', 9, 46, 'onde o seu verme não morre, e o fogo não se apaga.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24586, 'nvi', 'Marcos', 'mc', 9, 47, 'E se o seu olho o fizer tropeçar, arranque-o. É melhor entrar no Reino de Deus com um só olho do que, tendo os dois olhos, ser lançado no inferno,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24587, 'nvi', 'Marcos', 'mc', 9, 48, 'onde ‘o seu verme não morre, e o fogo não se apaga’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24588, 'nvi', 'Marcos', 'mc', 9, 49, 'Cada um será salgado com fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24589, 'nvi', 'Marcos', 'mc', 9, 50, '"O sal é bom, mas se deixar de ser salgado, como restaurar o seu sabor? Tenham sal em vocês mesmos e vivam em paz uns com os outros".');
