INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23140, 'nvi', 'Malaquias', 'ml', 4, 1, '"Pois certamente vem o dia, ardente como uma fornalha. Todos os arrogantes e todos os malfeitores serão como palha, e aquele dia, que está chegando, ateará fogo neles", diz o Senhor dos Exércitos. "Nem raiz nem galho algum sobrará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23141, 'nvi', 'Malaquias', 'ml', 4, 2, 'Mas para vocês que reverenciam o meu nome, o sol da justiça se levantará trazendo cura em suas asas. E vocês sairão e saltarão como bezerros soltos do curral.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23142, 'nvi', 'Malaquias', 'ml', 4, 3, 'Depois esmagarão os ímpios, que serão como pó sob as solas dos seus pés no dia em que eu agir", diz o Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23143, 'nvi', 'Malaquias', 'ml', 4, 4, '"Lembrem-se da lei do meu servo Moisés, dos decretos e das ordenanças que lhe dei em Horebe para todo o povo de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23144, 'nvi', 'Malaquias', 'ml', 4, 5, '"Vejam, eu enviarei a vocês o profeta Elias antes do grande e terrível dia do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23145, 'nvi', 'Malaquias', 'ml', 4, 6, 'Ele fará com que os corações dos pais se voltem para seus filhos, e os corações dos filhos para seus pais; do contrário eu virei e castigarei a terra com maldição. "');
