INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22666, 'nvi', 'Miquéias', 'mq', 7, 1, 'Que desgraça a minha! Sou como quem colhe frutos de verão na respiga da vinha; não há nenhum cacho de uvas para provar, nenhum figo novo que eu tanto desejo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22667, 'nvi', 'Miquéias', 'mq', 7, 2, 'Os piedosos desapareceram do país; não há um justo sequer. Todos estão à espreita para derramar sangue; cada um caça seu irmão com um laço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22668, 'nvi', 'Miquéias', 'mq', 7, 3, 'Com as mãos prontas para fazer o mal, o governante exige presentes, o juiz aceita suborno, os poderosos impõem o que querem; todos tramam em conjunto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22669, 'nvi', 'Miquéias', 'mq', 7, 4, 'O melhor deles é como espinheiro, e o mais correto é pior que uma cerca de espinhos. Chegou o dia anunciado pelas suas sentinelas, o dia do castigo de Deus. Agora reinará a confusão entre eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22670, 'nvi', 'Miquéias', 'mq', 7, 5, 'Não confiem nos vizinhos; nem acreditem nos amigos. Até com aquela que o abraça tenha cada um cuidado com o que diz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22671, 'nvi', 'Miquéias', 'mq', 7, 6, 'Pois o filho despreza o pai, a filha se rebela contra a mãe, a nora, contra a sogra; os inimigos do homem são os seus próprios familiares.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22672, 'nvi', 'Miquéias', 'mq', 7, 7, 'Mas, quanto a mim, ficarei atento ao Senhor, esperando em Deus, o meu Salvador, pois o meu Deus me ouvirá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22673, 'nvi', 'Miquéias', 'mq', 7, 8, 'Não se alegre a minha inimiga com a minha desgraça. Embora eu tenha caído, eu me levantarei. Embora eu esteja morando nas trevas, o Senhor será a minha luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22674, 'nvi', 'Miquéias', 'mq', 7, 9, 'Por eu ter pecado contra o Senhor, suportarei a sua ira, até que ele apresente a minha defesa e estabeleça o meu direito. Ele me fará sair para a luz; contemplarei a sua justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22675, 'nvi', 'Miquéias', 'mq', 7, 10, 'Então a minha inimiga o verá e ficará coberta de vergonha, ela que me disse: "Onde está o Senhor, o seu Deus? " Meus olhos verão a sua queda; ela será pisada como o barro das ruas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22676, 'nvi', 'Miquéias', 'mq', 7, 11, 'O dia da reconstrução dos seus muros chegará, o dia em que se ampliarão as suas fronteiras virá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22677, 'nvi', 'Miquéias', 'mq', 7, 12, 'Naquele dia virá a você gente desde a Assíria até o Egito, e desde o Egito até o Eufrates, de mar a mar e de montanha a montanha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22678, 'nvi', 'Miquéias', 'mq', 7, 13, 'Mas a terra será desolada por causa dos seus habitantes, em conseqüência de suas ações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22679, 'nvi', 'Miquéias', 'mq', 7, 14, 'Pastoreia o teu povo com o teu cajado, o rebanho da tua herança, que vive à parte numa floresta, em férteis pastagens. Deixa-os pastar em Basã e em Gileade, como antigamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22680, 'nvi', 'Miquéias', 'mq', 7, 15, '"Como nos dias em que você saiu do Egito, ali mostrarei as minhas maravilhas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22681, 'nvi', 'Miquéias', 'mq', 7, 16, 'As nações verão isso e se envergonharão, despojadas de todo o seu poder. Porão a mão na boca, e taparão os ouvidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22682, 'nvi', 'Miquéias', 'mq', 7, 17, 'Lamberão o pó como a serpente, como animais que se arrastam no chão. Sairão tremendo das suas fortalezas; com temor se voltarão para o Senhor, o nosso Deus, e terão medo de ti.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22683, 'nvi', 'Miquéias', 'mq', 7, 18, 'Quem é comparável a ti, ó Deus, que perdoas o pecado e esqueces a transgressão do remanescente da sua herança? Tu que não permaneces irado para sempre, mas tens prazer em mostrar amor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22684, 'nvi', 'Miquéias', 'mq', 7, 19, 'De novo terás compaixão de nós; pisarás as nossas maldades e atirarás todos os nossos pecados nas profundezas do mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22685, 'nvi', 'Miquéias', 'mq', 7, 20, 'Mostrarás fidelidade a Jacó, e bondade a Abraão, conforme prometeste sob juramento aos nossos antepassados, na antigüidade.');
