INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23419, 'nvi', 'Mateus', 'mt', 10, 1, 'Chamando seus doze discípulos, deu-lhes autoridade para expulsar espíritos imundos e curar todas as doenças e enfermidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23420, 'nvi', 'Mateus', 'mt', 10, 2, 'Estes são os nomes dos doze apóstolos: primeiro, Simão, chamado Pedro, e André, seu irmão; Tiago, filho de Zebedeu, e João, seu irmão;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23421, 'nvi', 'Mateus', 'mt', 10, 3, 'Filipe e Bartolomeu; Tomé e Mateus, o publicano; Tiago, filho de Alfeu, e Tadeu;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23422, 'nvi', 'Mateus', 'mt', 10, 4, 'Simão, o zelote, e Judas Iscariotes, que o traiu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23423, 'nvi', 'Mateus', 'mt', 10, 5, 'Jesus enviou estes doze com as seguintes instruções: "Não se dirijam aos gentios, nem entrem em cidade alguma dos samaritanos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23424, 'nvi', 'Mateus', 'mt', 10, 6, 'Antes, dirijam-se às ovelhas perdidas de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23425, 'nvi', 'Mateus', 'mt', 10, 7, 'Por onde forem, preguem esta mensagem: ‘O Reino dos céus está próximo’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23426, 'nvi', 'Mateus', 'mt', 10, 8, 'Curem os enfermos, ressuscitem os mortos, purifiquem os leprosos, expulsem os demônios. Vocês receberam de graça; dêem também de graça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23427, 'nvi', 'Mateus', 'mt', 10, 9, 'Não levem nem ouro, nem prata, nem cobre em seus cintos;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23428, 'nvi', 'Mateus', 'mt', 10, 10, 'não levem nenhum saco de viagem, nem túnica extra, nem sandálias, nem bordão; pois o trabalhador é digno do seu sustento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23429, 'nvi', 'Mateus', 'mt', 10, 11, '"Na cidade ou povoado em que entrarem, procurem alguém digno de recebê-los, e fiquem em sua casa até partirem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23430, 'nvi', 'Mateus', 'mt', 10, 12, 'Ao entrarem na casa, saúdem-na.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23431, 'nvi', 'Mateus', 'mt', 10, 13, 'Se a casa for digna, que a paz de vocês repouse sobre ela; se não for, que a paz retorne para vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23432, 'nvi', 'Mateus', 'mt', 10, 14, 'Se alguém não os receber nem ouvir suas palavras, sacudam a poeira dos pés, quando saírem daquela casa ou cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23433, 'nvi', 'Mateus', 'mt', 10, 15, 'Eu lhes digo a verdade: No dia do juízo haverá menor rigor para Sodoma e Gomorra do que para aquela cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23434, 'nvi', 'Mateus', 'mt', 10, 16, 'Eu os estou enviando como ovelhas entre lobos. Portanto, sejam prudentes como as serpentes e simples como as pombas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23435, 'nvi', 'Mateus', 'mt', 10, 17, '"Tenham cuidado, pois os homens os entregarão aos tribunais e os açoitarão nas sinagogas deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23436, 'nvi', 'Mateus', 'mt', 10, 18, 'Por minha causa vocês serão levados à presença de governadores e reis como testemunhas a eles e aos gentios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23437, 'nvi', 'Mateus', 'mt', 10, 19, 'Mas quando os prenderem, não se preocupem quanto ao que dizer, ou como dizer. Naquela hora lhes será dado o que dizer,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23438, 'nvi', 'Mateus', 'mt', 10, 20, 'pois não serão vocês que estarão falando, mas o Espírito do Pai de vocês falará por intermédio de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23439, 'nvi', 'Mateus', 'mt', 10, 21, '"O irmão entregará à morte o seu irmão, e o pai o seu filho; filhos se rebelarão contra seus pais e os matarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23440, 'nvi', 'Mateus', 'mt', 10, 22, 'Todos odiarão vocês por minha causa, mas aquele que perseverar até o fim será salvo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23441, 'nvi', 'Mateus', 'mt', 10, 23, 'Quando forem perseguidos num lugar, fujam para outro. Eu lhes garanto que vocês não terão percorrido todas as cidades de Israel antes que venha o Filho do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23442, 'nvi', 'Mateus', 'mt', 10, 24, '"O discípulo não está acima do seu mestre, nem o servo acima do seu senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23443, 'nvi', 'Mateus', 'mt', 10, 25, 'Basta ao discípulo ser como o seu mestre, e ao servo, como o seu senhor. Se o dono da casa foi chamado Belzebu, quanto mais os membros da sua família!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23444, 'nvi', 'Mateus', 'mt', 10, 26, '"Portanto, não tenham medo deles. Não há nada escondido que não venha a ser revelado, nem oculto que não venha a se tornar conhecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23445, 'nvi', 'Mateus', 'mt', 10, 27, 'O que eu lhes digo na escuridão, falem à luz do dia; o que é sussurrado em seus ouvidos, proclamem dos telhados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23446, 'nvi', 'Mateus', 'mt', 10, 28, 'Não tenham medo dos que matam o corpo, mas não podem matar a alma. Antes, tenham medo daquele que pode destruir tanto a alma como o corpo no inferno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23447, 'nvi', 'Mateus', 'mt', 10, 29, 'Não se vendem dois pardais por uma moedinha? Contudo, nenhum deles cai no chão sem o consentimento do Pai de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23448, 'nvi', 'Mateus', 'mt', 10, 30, 'Até os cabelos da cabeça de vocês estão todos contados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23449, 'nvi', 'Mateus', 'mt', 10, 31, 'Portanto, não tenham medo; vocês valem mais do que muitos pardais!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23450, 'nvi', 'Mateus', 'mt', 10, 32, '"Quem, pois, me confessar diante dos homens, eu também o confessarei diante do meu Pai que está nos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23451, 'nvi', 'Mateus', 'mt', 10, 33, 'Mas aquele que me negar diante dos homens, eu também o negarei diante do meu Pai que está nos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23452, 'nvi', 'Mateus', 'mt', 10, 34, '"Não pensem que vim trazer paz à terra; não vim trazer paz, mas espada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23453, 'nvi', 'Mateus', 'mt', 10, 35, 'Pois vim para fazer que ‘o homem fique contra seu pai, a filha contra sua mãe, a nora contra sua sogra;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23454, 'nvi', 'Mateus', 'mt', 10, 36, 'os inimigos do homem serão os da sua própria família’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23455, 'nvi', 'Mateus', 'mt', 10, 37, '"Quem ama seu pai ou sua mãe mais do que a mim não é digno de mim; quem ama seu filho ou sua filha mais do que a mim não é digno de mim;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23456, 'nvi', 'Mateus', 'mt', 10, 38, 'e quem não toma a sua cruz e não me segue, não é digno de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23457, 'nvi', 'Mateus', 'mt', 10, 39, 'Quem acha a sua vida a perderá, e quem perde a sua vida por minha causa a encontrará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23458, 'nvi', 'Mateus', 'mt', 10, 40, '"Quem recebe vocês, recebe a mim; e quem me recebe, recebe aquele que me enviou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23459, 'nvi', 'Mateus', 'mt', 10, 41, 'Quem recebe um profeta, porque ele é profeta, receberá a recompensa de profeta, e quem recebe um justo, porque ele é justo, receberá a recompensa de justo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23460, 'nvi', 'Mateus', 'mt', 10, 42, 'E se alguém der mesmo que seja apenas um copo de água fria a um destes pequeninos, porque ele é meu discípulo, eu lhes asseguro que não perderá a sua recompensa".');
