INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23461, 'nvi', 'Mateus', 'mt', 11, 1, 'Depois que terminou de instruir seus doze discípulos, Jesus saiu para ensinar e pregar nas cidades da Galiléia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23462, 'nvi', 'Mateus', 'mt', 11, 2, 'João, ao ouvir na prisão o que Cristo estava fazendo, enviou seus discípulos para lhe perguntarem:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23463, 'nvi', 'Mateus', 'mt', 11, 3, '"És tu aquele que haveria de vir ou devemos esperar algum outro? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23464, 'nvi', 'Mateus', 'mt', 11, 4, 'Jesus respondeu: "Voltem e anunciem a João o que vocês estão ouvindo e vendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23465, 'nvi', 'Mateus', 'mt', 11, 5, 'os cegos vêem, os mancos andam, os leprosos são purificados, os surdos ouvem, os mortos são ressuscitados, e as boas novas são pregadas aos pobres;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23466, 'nvi', 'Mateus', 'mt', 11, 6, 'e feliz é aquele que não se escandaliza por minha causa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23467, 'nvi', 'Mateus', 'mt', 11, 7, 'Enquanto saíam os discípulos de João, Jesus começou a falar à multidão a respeito de João: "O que vocês foram ver no deserto? Um caniço agitado pelo vento?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23468, 'nvi', 'Mateus', 'mt', 11, 8, 'Ou, o que foram ver? Um homem vestido de roupas finas? Ora, os que usam roupas finas estão nos palácios reais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23469, 'nvi', 'Mateus', 'mt', 11, 9, 'Afinal, o que foram ver? Um profeta? Sim, eu lhes digo, e mais que profeta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23470, 'nvi', 'Mateus', 'mt', 11, 10, 'Este é aquele a respeito de quem está escrito: ‘Enviarei o meu mensageiro à tua frente; ele preparará o teu caminho diante de ti’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23471, 'nvi', 'Mateus', 'mt', 11, 11, 'Digo-lhes a verdade: Entre os nascidos de mulher não surgiu ninguém maior do que João Batista; todavia, o menor no Reino dos céus é maior do que ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23472, 'nvi', 'Mateus', 'mt', 11, 12, 'Desde os dias de João Batista até agora, o Reino dos céus é tomado à força, e os que usam de força se apoderam dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23473, 'nvi', 'Mateus', 'mt', 11, 13, 'Pois todos os Profetas e a Lei profetizaram até João.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23474, 'nvi', 'Mateus', 'mt', 11, 14, 'E se vocês quiserem aceitar, este é o Elias que havia de vir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23475, 'nvi', 'Mateus', 'mt', 11, 15, 'Aquele que tem ouvidos, ouça!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23476, 'nvi', 'Mateus', 'mt', 11, 16, '"A que posso comparar esta geração? São como crianças que ficam sentadas nas praças e gritam umas às outras:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23477, 'nvi', 'Mateus', 'mt', 11, 17, '‘Nós lhes tocamos flauta, mas vocês não dançaram; cantamos um lamento, mas vocês não se entristeceram’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23478, 'nvi', 'Mateus', 'mt', 11, 18, 'Pois veio João, que jejua e não bebe vinho, e dizem: ‘Ele tem demônio’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23479, 'nvi', 'Mateus', 'mt', 11, 19, 'Veio o Filho do homem comendo e bebendo, e dizem: ‘Aí está um comilão e beberrão, amigo de publicanos e "pecadores" ’. Mas a sabedoria é comprovada pelas obras que a acompanham".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23480, 'nvi', 'Mateus', 'mt', 11, 20, 'Então Jesus começou a denunciar as cidades em que havia sido realizada a maioria dos seus milagres, porque não se arrependeram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23481, 'nvi', 'Mateus', 'mt', 11, 21, '"Ai de você, Corazim! Ai de você, Betsaida! Porque se os milagres que foram realizados entre vocês tivessem sido realizados em Tiro e Sidom, há muito tempo elas se teriam arrependido, vestindo roupas de saco e cobrindo-se de cinzas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23482, 'nvi', 'Mateus', 'mt', 11, 22, 'Mas eu lhes afirmo que no dia do juízo haverá menor rigor para Tiro e Sidom do que para vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23483, 'nvi', 'Mateus', 'mt', 11, 23, 'E você, Cafarnaum: será elevada até o céu? Não, você descerá até ao Hades! Se os milagres que em você foram realizados tivessem sido realizados em Sodoma, ela teria permanecido até hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23484, 'nvi', 'Mateus', 'mt', 11, 24, 'Mas eu lhes afirmo que no dia do juízo haverá menor rigor para Sodoma do que para você".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23485, 'nvi', 'Mateus', 'mt', 11, 25, 'Naquela ocasião Jesus disse: "Eu te louvo, Pai, Senhor dos céus e da terra, porque escondeste estas coisas dos sábios e cultos, e as revelaste aos pequeninos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23486, 'nvi', 'Mateus', 'mt', 11, 26, 'Sim, Pai, pois assim foi do teu agrado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23487, 'nvi', 'Mateus', 'mt', 11, 27, '"Todas as coisas me foram entregues por meu Pai. Ninguém conhece o Filho a não ser o Pai, e ninguém conhece o Pai a não ser o Filho e aqueles a quem o Filho o quiser revelar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23488, 'nvi', 'Mateus', 'mt', 11, 28, '"Venham a mim, todos os que estão cansados e sobrecarregados, e eu lhes darei descanso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23489, 'nvi', 'Mateus', 'mt', 11, 29, 'Tomem sobre vocês o meu jugo e aprendam de mim, pois sou manso e humilde de coração, e vocês encontrarão descanso para as suas almas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23490, 'nvi', 'Mateus', 'mt', 11, 30, 'Pois o meu jugo é suave e o meu fardo é leve".');
