INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23541, 'nvi', 'Mateus', 'mt', 13, 1, 'Naquele mesmo dia Jesus saiu de casa e assentou-se à beira-mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23542, 'nvi', 'Mateus', 'mt', 13, 2, 'Reuniu-se ao seu redor uma multidão tão grande que ele teve que entrar num barco e assentar-se nele, enquanto todo o povo ficou na praia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23543, 'nvi', 'Mateus', 'mt', 13, 3, 'Então lhes falou muitas coisas por parábolas, dizendo: "O semeador saiu a semear.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23544, 'nvi', 'Mateus', 'mt', 13, 4, 'Enquanto lançava a semente, parte dela caiu à beira do caminho, e as aves vieram e a comeram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23545, 'nvi', 'Mateus', 'mt', 13, 5, 'Parte dela caiu em terreno pedregoso, onde não havia muita terra; e logo brotou, porque a terra não era profunda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23546, 'nvi', 'Mateus', 'mt', 13, 6, 'Mas quando saiu o sol, as plantas se queimaram e secaram, porque não tinham raiz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23547, 'nvi', 'Mateus', 'mt', 13, 7, 'Outra parte caiu entre espinhos, que cresceram e sufocaram as plantas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23548, 'nvi', 'Mateus', 'mt', 13, 8, 'Outra ainda caiu em boa terra, deu boa colheita, a cem, sessenta e trinta por um.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23549, 'nvi', 'Mateus', 'mt', 13, 9, 'Aquele que tem ouvidos para ouvir, ouça! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23550, 'nvi', 'Mateus', 'mt', 13, 10, 'Os discípulos aproximaram-se dele e perguntaram: "Por que falas ao povo por parábolas? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23551, 'nvi', 'Mateus', 'mt', 13, 11, 'Ele respondeu: "A vocês foi dado o conhecimento dos mistérios do Reino dos céus, mas a eles não.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23552, 'nvi', 'Mateus', 'mt', 13, 12, 'A quem tem será dado, e este terá em grande quantidade. De quem não tem, até o que tem lhe será tirado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23553, 'nvi', 'Mateus', 'mt', 13, 13, 'Por essa razão eu lhes falo por parábolas: ‘Porque vendo, eles não vêem e, ouvindo, não ouvem nem entendem’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23554, 'nvi', 'Mateus', 'mt', 13, 14, 'Neles se cumpre a profecia de Isaías: ‘Ainda que estejam sempre ouvindo, vocês nunca entenderão; ainda que estejam sempre vendo, jamais perceberão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23555, 'nvi', 'Mateus', 'mt', 13, 15, 'Pois o coração deste povo se tornou insensível; de má vontade ouviram com os seus ouvidos, e fecharam os seus olhos. Se assim não fosse, poderiam ver com os olhos, ouvir com os ouvidos, entender com o coração e converter-se, e eu os curaria’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23556, 'nvi', 'Mateus', 'mt', 13, 16, 'Mas, felizes são os olhos de vocês, porque vêem; e os ouvidos de vocês, porque ouvem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23557, 'nvi', 'Mateus', 'mt', 13, 17, 'Pois eu lhes digo a verdade: Muitos profetas e justos desejaram ver o que vocês estão vendo, mas não viram, e ouvir o que vocês estão ouvindo, mas não ouviram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23558, 'nvi', 'Mateus', 'mt', 13, 18, '"Portanto, ouçam o que significa a parábola do semeador:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23559, 'nvi', 'Mateus', 'mt', 13, 19, 'Quando alguém ouve a mensagem do Reino e não a entende, o Maligno vem e lhe arranca o que foi semeado em seu coração. Este é o que foi semeado à beira do caminho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23560, 'nvi', 'Mateus', 'mt', 13, 20, 'Quanto ao que foi semeado em terreno pedregoso, este é aquele que ouve a palavra e logo a recebe com alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23561, 'nvi', 'Mateus', 'mt', 13, 21, 'Todavia, visto que não tem raiz em si mesmo, permanece por pouco tempo. Quando surge alguma tribulação ou perseguição por causa da palavra, logo a abandona.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23562, 'nvi', 'Mateus', 'mt', 13, 22, 'Quanto ao que foi semeado entre os espinhos, este é aquele que ouve a palavra, mas a preocupação desta vida e o engano das riquezas a sufocam, tornando-a infrutífera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23563, 'nvi', 'Mateus', 'mt', 13, 23, 'E, finalmente, o que foi semeado em boa terra: este é aquele que ouve a palavra e a entende, e dá uma colheita de cem, sessenta e trinta por um".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23564, 'nvi', 'Mateus', 'mt', 13, 24, 'Jesus lhes contou outra parábola, dizendo: "O Reino dos céus é como um homem que semeou boa semente em seu campo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23565, 'nvi', 'Mateus', 'mt', 13, 25, 'Mas enquanto todos dormiam, veio o seu inimigo e semeou o joio no meio do trigo e se foi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23566, 'nvi', 'Mateus', 'mt', 13, 26, 'Quando o trigo brotou e formou espigas, o joio também apareceu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23567, 'nvi', 'Mateus', 'mt', 13, 27, '"Os servos do dono do campo dirigiram-se a ele e disseram: ‘O senhor não semeou boa semente em seu campo? Então, de onde veio o joio? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23568, 'nvi', 'Mateus', 'mt', 13, 28, '" ‘Um inimigo fez isso’, respondeu ele. "Os servos lhe perguntaram: ‘O senhor quer que vamos tirá-lo? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23569, 'nvi', 'Mateus', 'mt', 13, 29, '"Ele respondeu: ‘Não, porque, ao tirar o joio, vocês poderão arrancar com ele o trigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23570, 'nvi', 'Mateus', 'mt', 13, 30, 'Deixem que cresçam juntos até à colheita. Então direi aos encarregados da colheita: Juntem primeiro o joio e amarrem-no em feixes para ser queimado; depois juntem o trigo e guardem-no no meu celeiro’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23571, 'nvi', 'Mateus', 'mt', 13, 31, 'E contou-lhes outra parábola: "O Reino dos céus é como um grão de mostarda que um homem plantou em seu campo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23572, 'nvi', 'Mateus', 'mt', 13, 32, 'Embora seja a menor dentre todas as sementes, quando cresce torna-se a maior das hortaliças e se transforma numa árvore, de modo que as aves do céu vêm fazer os seus ninhos em seus ramos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23573, 'nvi', 'Mateus', 'mt', 13, 33, 'E contou-lhes ainda outra parábola: "O Reino dos céus é como o fermento que uma mulher tomou e misturou com uma grande quantidade de farinha, e toda a massa ficou fermentada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23574, 'nvi', 'Mateus', 'mt', 13, 34, 'Jesus falou todas estas coisas à multidão por parábolas. Nada lhes dizia sem usar alguma parábola,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23575, 'nvi', 'Mateus', 'mt', 13, 35, 'cumprindo-se, assim, o que fora dito pelo profeta: "Abrirei minha boca em parábolas, Proclamarei coisas ocultas Desde a criação do mundo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23576, 'nvi', 'Mateus', 'mt', 13, 36, 'Então ele deixou a multidão e foi para casa. Seus discípulos aproximaram-se dele e disseram: "Explica-nos a parábola do joio no campo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23577, 'nvi', 'Mateus', 'mt', 13, 37, 'Ele respondeu: "Aquele que semeou a boa semente é o Filho do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23578, 'nvi', 'Mateus', 'mt', 13, 38, 'O campo é o mundo, e a boa semente são os filhos do Reino. O joio são os filhos do Maligno,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23579, 'nvi', 'Mateus', 'mt', 13, 39, 'e o inimigo que o semeia é o diabo. A colheita é o fim desta era, e os encarregados da colheita são anjos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23580, 'nvi', 'Mateus', 'mt', 13, 40, '"Assim como o joio é colhido e queimado no fogo, assim também acontecerá no fim desta era.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23581, 'nvi', 'Mateus', 'mt', 13, 41, 'O Filho do homem enviará os seus anjos, e eles tirarão do seu Reino tudo o que faz tropeçar e todos os que praticam o mal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23582, 'nvi', 'Mateus', 'mt', 13, 42, 'Eles os lançarão na fornalha ardente, onde haverá choro e ranger de dentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23583, 'nvi', 'Mateus', 'mt', 13, 43, 'Então os justos brilharão como o sol no Reino do seu Pai. Aquele que tem ouvidos, ouça".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23584, 'nvi', 'Mateus', 'mt', 13, 44, '"O Reino dos céus é como um tesouro escondido num campo. Certo homem, tendo-o encontrado, escondeu-o de novo e, então, cheio de alegria, foi, vendeu tudo o que tinha e comprou aquele campo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23585, 'nvi', 'Mateus', 'mt', 13, 45, '"O Reino dos céus também é como um negociante que procura pérolas preciosas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23586, 'nvi', 'Mateus', 'mt', 13, 46, 'Encontrando uma pérola de grande valor, foi, vendeu tudo o que tinha e a comprou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23587, 'nvi', 'Mateus', 'mt', 13, 47, '"O Reino dos céus é ainda como uma rede que é lançada ao mar e apanha toda sorte de peixes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23588, 'nvi', 'Mateus', 'mt', 13, 48, 'Quando está cheia, os pescadores a puxam para a praia. Então se assentam e juntam os peixes bons em cestos, mas jogam fora os ruins.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23589, 'nvi', 'Mateus', 'mt', 13, 49, 'Assim acontecerá no fim desta era. Os anjos virão, separarão os perversos dos justos');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23590, 'nvi', 'Mateus', 'mt', 13, 50, 'e lançarão aqueles na fornalha ardente, onde haverá choro e ranger de dentes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23591, 'nvi', 'Mateus', 'mt', 13, 51, 'Então perguntou Jesus: "Vocês entenderam todas essas coisas? " "Sim", responderam eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23592, 'nvi', 'Mateus', 'mt', 13, 52, 'Ele lhes disse: "Por isso, todo mestre da lei instruído quanto ao Reino dos céus é como o dono de uma casa que tira do seu tesouro coisas novas e coisas velhas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23593, 'nvi', 'Mateus', 'mt', 13, 53, 'Tendo terminado de contar estas parábolas, Jesus saiu dali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23594, 'nvi', 'Mateus', 'mt', 13, 54, 'Chegando à sua cidade, começou a ensinar o povo na sinagoga. Todos ficaram admirados e perguntavam: "De onde lhe vêm esta sabedoria e estes poderes miraculosos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23595, 'nvi', 'Mateus', 'mt', 13, 55, 'Não é este o filho do carpinteiro? O nome de sua mãe não é Maria, e não são seus irmãos Tiago, José, Simão e Judas?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23596, 'nvi', 'Mateus', 'mt', 13, 56, 'Não estão conosco todas as suas irmãs? De onde, pois, ele obteve todas essas coisas? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23597, 'nvi', 'Mateus', 'mt', 13, 57, 'E ficavam escandalizados por causa dele. Mas Jesus lhes disse: "Só em sua própria terra e em sua própria casa é que um profeta não tem honra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23598, 'nvi', 'Mateus', 'mt', 13, 58, 'E não realizou muitos milagres ali, por causa da incredulidade deles.');
