INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23599, 'nvi', 'Mateus', 'mt', 14, 1, 'Por aquele tempo Herodes, o tetrarca, ouviu os relatos a respeito de Jesus');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23600, 'nvi', 'Mateus', 'mt', 14, 2, 'e disse aos que o serviam: "Este é João Batista; ele ressuscitou dos mortos! Por isso estão operando nele poderes miraculosos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23601, 'nvi', 'Mateus', 'mt', 14, 3, 'Pois Herodes havia prendido e amarrado João, colocando-o na prisão por causa de Herodias, mulher de Filipe, seu irmão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23602, 'nvi', 'Mateus', 'mt', 14, 4, 'porquanto João lhe dizia: "Não te é permitido viver com ela".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23603, 'nvi', 'Mateus', 'mt', 14, 5, 'Herodes queria matá-lo, mas tinha medo do povo, porque este o considerava profeta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23604, 'nvi', 'Mateus', 'mt', 14, 6, 'No aniversário de Herodes, a filha de Herodias dançou diante de todos, e agradou tanto a Herodes');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23605, 'nvi', 'Mateus', 'mt', 14, 7, 'que ele prometeu sob juramento dar-lhe o que ela pedisse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23606, 'nvi', 'Mateus', 'mt', 14, 8, 'Influenciada por sua mãe, ela disse: "Dá-me aqui, num prato, a cabeça de João Batista".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23607, 'nvi', 'Mateus', 'mt', 14, 9, 'O rei ficou aflito, mas, por causa dos juramento e dos convidados, ordenou que lhe fosse dado o que ela pedia');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23608, 'nvi', 'Mateus', 'mt', 14, 10, 'e mandou decapitar João na prisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23609, 'nvi', 'Mateus', 'mt', 14, 11, 'Sua cabeça foi levada num prato e entregue à jovem, que a levou à sua mãe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23610, 'nvi', 'Mateus', 'mt', 14, 12, 'Os discípulos de João vieram, levaram o seu corpo e o sepultaram. Depois foram contar isso a Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23611, 'nvi', 'Mateus', 'mt', 14, 13, 'Ouvindo o que havia ocorrido, Jesus retirou-se de barco em particular para um lugar deserto. As multidões, ao ouvirem falar disso, saíram das cidades e o seguiram a pé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23612, 'nvi', 'Mateus', 'mt', 14, 14, 'Quando Jesus saiu do barco e viu tão grande multidão, teve compaixão deles e curou os seus doentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23613, 'nvi', 'Mateus', 'mt', 14, 15, 'Ao cair da tarde, os discípulos aproximaram-se dele e disseram: "Este é um lugar deserto, e já está ficando tarde. Manda embora a multidão para que possam ir aos povoados comprar comida".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23614, 'nvi', 'Mateus', 'mt', 14, 16, 'Respondeu Jesus: "Eles não precisam ir. Dêem-lhes vocês algo para comer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23615, 'nvi', 'Mateus', 'mt', 14, 17, 'Eles lhe disseram: "Tudo o que temos aqui são cinco pães e dois peixes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23616, 'nvi', 'Mateus', 'mt', 14, 18, '"Tragam-nos aqui para mim", disse ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23617, 'nvi', 'Mateus', 'mt', 14, 19, 'E ordenou que a multidão se assentasse na grama. Tomando os cinco pães e os dois peixes e, olhando para o céu, deu graças e partiu os pães. Em seguida, deu-os aos discípulos, e estes à multidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23618, 'nvi', 'Mateus', 'mt', 14, 20, 'Todos comeram e ficaram satisfeitos, e os discípulos recolheram doze cestos cheios de pedaços que sobraram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23619, 'nvi', 'Mateus', 'mt', 14, 21, 'Os que comeram foram cerca de cinco mil homens, sem contar mulheres e crianças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23620, 'nvi', 'Mateus', 'mt', 14, 22, 'Logo em seguida, Jesus insistiu com os discípulos para que entrassem no barco e fossem adiante dele para o outro lado, enquanto ele despedia a multidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23621, 'nvi', 'Mateus', 'mt', 14, 23, 'Tendo despedido a multidão, subiu sozinho a um monte para orar. Ao anoitecer, ele estava ali sozinho,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23622, 'nvi', 'Mateus', 'mt', 14, 24, 'mas o barco já estava a considerável distância da terra, fustigado pelas ondas, porque o vento soprava contra ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23623, 'nvi', 'Mateus', 'mt', 14, 25, 'Alta madrugada, Jesus dirigiu-se a eles, andando sobre o mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23624, 'nvi', 'Mateus', 'mt', 14, 26, 'Quando o viram andando sobre o mar, ficaram aterrorizados e disseram: "É um fantasma! " E gritaram de medo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23625, 'nvi', 'Mateus', 'mt', 14, 27, 'Mas Jesus imediatamente lhes disse: "Coragem! Sou eu. Não tenham medo! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23626, 'nvi', 'Mateus', 'mt', 14, 28, '"Senhor", disse Pedro, "se és tu, manda-me ir ao teu encontro por sobre as águas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23627, 'nvi', 'Mateus', 'mt', 14, 29, '"Venha", respondeu ele. Então Pedro saiu do barco, andou sobre a água e foi na direção de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23628, 'nvi', 'Mateus', 'mt', 14, 30, 'Mas, quando reparou no vento, ficou com medo e, começando a afundar, gritou: "Senhor, salva-me! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23629, 'nvi', 'Mateus', 'mt', 14, 31, 'Imediatamente Jesus estendeu a mão e o segurou. E disse: "Homem de pequena fé, porque você duvidou? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23630, 'nvi', 'Mateus', 'mt', 14, 32, 'Quando entraram no barco, o vento cessou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23631, 'nvi', 'Mateus', 'mt', 14, 33, 'Então os que estavam no barco o adoraram, dizendo: "Verdadeiramente tu és o Filho de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23632, 'nvi', 'Mateus', 'mt', 14, 34, 'Depois de atravessarem o mar, chegaram a Genesaré.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23633, 'nvi', 'Mateus', 'mt', 14, 35, 'Quando os homens daquele lugar reconheceram Jesus, espalharam a notícia em toda aquela região e lhe trouxeram os seus doentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23634, 'nvi', 'Mateus', 'mt', 14, 36, 'Suplicavam-lhe que apenas pudessem tocar na borda do seu manto; e todos os que nele tocaram foram curados.');
