INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23635, 'nvi', 'Mateus', 'mt', 15, 1, 'Então alguns fariseus e mestres da lei, vindos de Jerusalém, foram a Jesus e perguntaram:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23636, 'nvi', 'Mateus', 'mt', 15, 2, '"Por que os seus discípulos transgridem a tradição dos líderes religiosos? Pois não lavam as mãos antes de comer! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23637, 'nvi', 'Mateus', 'mt', 15, 3, 'Respondeu Jesus: "E por que vocês transgridem o mandamento de Deus por causa da tradição de vocês?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23638, 'nvi', 'Mateus', 'mt', 15, 4, 'Pois Deus disse: ‘Honra teu pai e tua mãe’ e ‘quem amaldiçoar seu pai ou sua mãe terá que ser executado’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23639, 'nvi', 'Mateus', 'mt', 15, 5, 'Mas vocês afirmam que se alguém disser a seu pai ou a sua mãe: ‘Qualquer ajuda que vocês poderiam receber de mim é uma oferta dedicada a Deus’,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23640, 'nvi', 'Mateus', 'mt', 15, 6, 'ele não é obrigado a ‘honrar seu pai’ dessa forma. Assim vocês anulam a palavra de Deus por causa da tradição de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23641, 'nvi', 'Mateus', 'mt', 15, 7, 'Hipócritas! Bem profetizou Isaías acerca de vocês, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23642, 'nvi', 'Mateus', 'mt', 15, 8, '‘Este povo me honra com os lábios, mas o seu coração está longe de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23643, 'nvi', 'Mateus', 'mt', 15, 9, 'Em vão me adoram; seus ensinamentos não passam de regras ensinadas por homens’".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23644, 'nvi', 'Mateus', 'mt', 15, 10, 'Jesus chamou para junto de si a multidão e disse: "Ouçam e entendam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23645, 'nvi', 'Mateus', 'mt', 15, 11, 'O que entra pela boca não torna o homem ‘impuro’; mas o que sai de sua boca, isto o torna ‘impuro’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23646, 'nvi', 'Mateus', 'mt', 15, 12, 'Então os discípulos se aproximaram dele e perguntaram: "Sabes que os fariseus ficaram ofendidos quando ouviram isso? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23647, 'nvi', 'Mateus', 'mt', 15, 13, 'Ele respondeu: "Toda planta que meu Pai celestial não plantou será arrancada pelas raízes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23648, 'nvi', 'Mateus', 'mt', 15, 14, 'Deixem-nos; eles são guias cegos. Se um cego conduzir outro cego, ambos cairão num buraco".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23649, 'nvi', 'Mateus', 'mt', 15, 15, 'Então Pedro pediu-lhe: "Explica-nos a parábola".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23650, 'nvi', 'Mateus', 'mt', 15, 16, '"Será que vocês ainda não conseguem entender? ", perguntou Jesus:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23651, 'nvi', 'Mateus', 'mt', 15, 17, '"Não percebem que o que entra pela boca vai para o estômago e mais tarde é expelido?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23652, 'nvi', 'Mateus', 'mt', 15, 18, 'Mas as coisas que saem da boca vêm do coração, e são essas que tornam o homem ‘impuro’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23653, 'nvi', 'Mateus', 'mt', 15, 19, 'Pois do coração saem os maus pensamentos, os homicídios, os adultérios, as imoralidades sexuais, os roubos, os falsos testemunhos e as calúnias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23654, 'nvi', 'Mateus', 'mt', 15, 20, 'Essas coisas tornam o homem ‘impuro’; mas o comer sem lavar as mãos não o torna ‘impuro’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23655, 'nvi', 'Mateus', 'mt', 15, 21, 'Saindo daquele lugar, Jesus retirou-se para a região de Tiro e de Sidom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23656, 'nvi', 'Mateus', 'mt', 15, 22, 'Uma mulher cananéia, natural dali, veio a ele, gritando: "Senhor, Filho de Davi, tem misericórdia de mim! Minha filha está endemoninhada e está sofrendo muito".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23657, 'nvi', 'Mateus', 'mt', 15, 23, 'Mas Jesus não lhe respondeu palavra. Então seus discípulos se aproximaram dele e pediram: "Manda-a embora, pois vem gritando atrás de nós".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23658, 'nvi', 'Mateus', 'mt', 15, 24, 'Ele respondeu: "Eu fui enviado apenas às ovelhas perdidas de Israel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23659, 'nvi', 'Mateus', 'mt', 15, 25, 'A mulher veio, adorou-o de joelhos e disse: "Senhor, ajuda-me! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23660, 'nvi', 'Mateus', 'mt', 15, 26, 'Ele respondeu: "Não é certo tirar o pão dos filhos e lançá-lo aos cachorrinhos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23661, 'nvi', 'Mateus', 'mt', 15, 27, 'Disse ela, porém: "Sim, Senhor, mas até os cachorrinhos comem das migalhas que caem da mesa dos seus donos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23662, 'nvi', 'Mateus', 'mt', 15, 28, 'Jesus respondeu: "Mulher, grande é a sua fé! Seja conforme você deseja". E naquele mesmo instante a sua filha foi curada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23663, 'nvi', 'Mateus', 'mt', 15, 29, 'Jesus saiu dali e foi para a beira do mar da Galiléia. Depois subiu a um monte e se assentou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23664, 'nvi', 'Mateus', 'mt', 15, 30, 'Uma grande multidão dirigiu-se a ele, levando-lhe os mancos, os aleijados, os cegos, os mudos e muitos outros, e os colocaram aos seus pés; e ele os curou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23665, 'nvi', 'Mateus', 'mt', 15, 31, 'O povo ficou admirado quando viu os mudos falando, os aleijados curados, os mancos andando e os cegos vendo. E louvaram o Deus de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23666, 'nvi', 'Mateus', 'mt', 15, 32, 'Jesus chamou os seus discípulos e disse: "Tenho compaixão desta multidão; já faz três dias que eles estão comigo e nada têm para comer. Não quero mandá-los embora com fome, porque podem desfalecer no caminho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23667, 'nvi', 'Mateus', 'mt', 15, 33, 'Os seus discípulos responderam: "Onde poderíamos encontrar, neste lugar deserto, pão suficiente para alimentar tanta gente? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23668, 'nvi', 'Mateus', 'mt', 15, 34, '"Quantos pães vocês têm? ", perguntou Jesus. "Sete", responderam eles, "e alguns peixinhos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23669, 'nvi', 'Mateus', 'mt', 15, 35, 'Ele ordenou à multidão que se assentasse no chão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23670, 'nvi', 'Mateus', 'mt', 15, 36, 'Depois de tomar os sete pães e os peixes e dar graças, partiu-os e os entregou aos discípulos, e os discípulos à multidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23671, 'nvi', 'Mateus', 'mt', 15, 37, 'Todos comeram até se fartar. E ajuntaram sete cestos cheios de pedaços que sobraram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23672, 'nvi', 'Mateus', 'mt', 15, 38, 'Os que comeram foram quatro mil homens, sem contar mulheres e crianças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23673, 'nvi', 'Mateus', 'mt', 15, 39, 'E, havendo despedido a multidão, Jesus entrou no barco e foi para a região de Magadã.');
