INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23674, 'nvi', 'Mateus', 'mt', 16, 1, 'Os fariseus e os saduceus aproximaram-se de Jesus e o puseram à prova, pedindo-lhe que lhes mostrasse um sinal do céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23675, 'nvi', 'Mateus', 'mt', 16, 2, 'Ele respondeu: "Quando a tarde vem, vocês dizem: ‘Vai fazer bom tempo, porque o céu está vermelho’,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23676, 'nvi', 'Mateus', 'mt', 16, 3, 'e de manhã: ‘Hoje haverá tempestade, porque o céu está vermelho e nublado’. Vocês sabem interpretar o aspecto do céu, mas não sabem interpretar os sinais dos tempos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23677, 'nvi', 'Mateus', 'mt', 16, 4, 'Uma geração perversa e adúltera pede um sinal miraculoso, mas nenhum sinal lhe será dado, a não ser o sinal de Jonas". Então Jesus os deixou e retirou-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23678, 'nvi', 'Mateus', 'mt', 16, 5, 'Indo os discípulos para o outro lado do mar, esqueceram-se de levar pão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23679, 'nvi', 'Mateus', 'mt', 16, 6, 'Disse-lhes Jesus: "Estejam atentos e tenham cuidado com o fermento dos fariseus e dos saduceus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23680, 'nvi', 'Mateus', 'mt', 16, 7, 'E eles discutiam entre si, dizendo: "É porque não trouxemos pão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23681, 'nvi', 'Mateus', 'mt', 16, 8, 'Percebendo a discussão, Jesus lhes perguntou: "Homens de pequena fé, por que vocês estão discutindo entre si sobre não terem pão?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23682, 'nvi', 'Mateus', 'mt', 16, 9, 'Ainda não compreendem? Não se lembram dos cinco pães para os cinco mil e de quantos cestos vocês recolheram?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23683, 'nvi', 'Mateus', 'mt', 16, 10, 'Nem dos sete pães para os quatro mil e de quantos cestos recolheram?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23684, 'nvi', 'Mateus', 'mt', 16, 11, 'Como é que vocês não entendem que não era de pão que eu estava lhes falando? Mas tomem cuidado com o fermento dos fariseus e dos saduceus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23685, 'nvi', 'Mateus', 'mt', 16, 12, 'Então entenderam que não estava lhes dizendo que tomassem cuidado com o fermento de pão, mas com o ensino dos fariseus e dos saduceus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23686, 'nvi', 'Mateus', 'mt', 16, 13, 'Chegando Jesus à região de Cesaréia de Filipe, perguntou aos seus discípulos: "Quem os homens dizem que o Filho do homem é? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23687, 'nvi', 'Mateus', 'mt', 16, 14, 'Eles responderam: "Alguns dizem que é João Batista; outros, Elias; e, ainda outros, Jeremias ou um dos profetas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23688, 'nvi', 'Mateus', 'mt', 16, 15, '"E vocês? ", perguntou ele. "Quem vocês dizem que eu sou? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23689, 'nvi', 'Mateus', 'mt', 16, 16, 'Simão Pedro respondeu: "Tu és o Cristo, o Filho do Deus vivo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23690, 'nvi', 'Mateus', 'mt', 16, 17, 'Respondeu Jesus: "Feliz é você, Simão, filho de Jonas! Porque isto não lhe foi revelado por carne ou sangue, mas por meu Pai que está nos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23691, 'nvi', 'Mateus', 'mt', 16, 18, 'E eu lhe digo que você é Pedro, e sobre esta pedra edificarei a minha igreja, e as portas do Hades não poderão vencê-la.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23692, 'nvi', 'Mateus', 'mt', 16, 19, 'Eu lhe darei as chaves do Reino dos céus; o que você ligar na terra terá sido ligado nos céus, e o que você desligar na terra terá sido desligado nos céus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23693, 'nvi', 'Mateus', 'mt', 16, 20, 'Então advertiu a seus discípulos que não contassem a ninguém que ele era o Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23694, 'nvi', 'Mateus', 'mt', 16, 21, 'Desde aquele momento Jesus começou a explicar aos seus discípulos que era necessário que ele fosse para Jerusalém e sofresse muitas coisas nas mãos dos líderes religiosos, dos chefes dos sacerdotes e dos mestres da lei, e fosse morto e ressuscitasse no terceiro dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23695, 'nvi', 'Mateus', 'mt', 16, 22, 'Então Pedro, chamando-o à parte, começou a repreendê-lo, dizendo: "Nunca, Senhor! Isso nunca te acontecerá! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23696, 'nvi', 'Mateus', 'mt', 16, 23, 'Jesus virou-se e disse a Pedro: "Para trás de mim, Satanás! Você é uma pedra de tropeço para mim, e não pensa nas coisas de Deus, mas nas dos homens".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23697, 'nvi', 'Mateus', 'mt', 16, 24, 'Então Jesus disse aos seus discípulos: "Se alguém quiser acompanhar-me, negue-se a si mesmo, tome a sua cruz e siga-me.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23698, 'nvi', 'Mateus', 'mt', 16, 25, 'Pois quem quiser salvar a sua vida, a perderá, mas quem perder a vida por minha causa, a encontrará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23699, 'nvi', 'Mateus', 'mt', 16, 26, 'Pois, que adiantará ao homem ganhar o mundo inteiro e perder a sua alma? Ou, o que o homem poderá dar em troca de sua alma?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23700, 'nvi', 'Mateus', 'mt', 16, 27, 'Pois o Filho do homem virá na glória de seu Pai, com os seus anjos, e então recompensará a cada um de acordo com o que tenha feito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23701, 'nvi', 'Mateus', 'mt', 16, 28, 'Garanto-lhes que alguns dos que aqui se acham não experimentarão a morte antes de verem o Filho do homem vindo em seu Reino".');
