INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23702, 'nvi', 'Mateus', 'mt', 17, 1, 'Seis dias depois, Jesus tomou consigo Pedro, Tiago e João, irmão de Tiago, e os levou, em particular, a um alto monte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23703, 'nvi', 'Mateus', 'mt', 17, 2, 'Ali ele foi transfigurado diante deles. Sua face brilhou como o sol, e suas roupas se tornaram brancas como a luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23704, 'nvi', 'Mateus', 'mt', 17, 3, 'Naquele mesmo momento apareceram diante deles Moisés e Elias, conversando com Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23705, 'nvi', 'Mateus', 'mt', 17, 4, 'Então Pedro disse a Jesus: "Senhor, é bom estarmos aqui. Se quiseres, farei três tendas: uma para ti, uma para Moisés e outra para Elias".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23706, 'nvi', 'Mateus', 'mt', 17, 5, 'Enquanto ele ainda estava falando, uma nuvem resplandecente os envolveu, e dela saiu uma voz, que dizia: "Este é o meu Filho amado em quem me agrado. Ouçam-no! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23707, 'nvi', 'Mateus', 'mt', 17, 6, 'Ouvindo isso, os discípulos prostraram-se com o rosto em terra e ficaram aterrorizados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23708, 'nvi', 'Mateus', 'mt', 17, 7, 'Mas Jesus se aproximou, tocou neles e disse: "Levantem-se! Não tenham medo! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23709, 'nvi', 'Mateus', 'mt', 17, 8, 'E erguendo eles os olhos, não viram mais ninguém a não ser Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23710, 'nvi', 'Mateus', 'mt', 17, 9, 'Enquanto desciam do monte, Jesus lhes ordenou: "Não contem a ninguém o que vocês viram, até que o Filho do homem tenha sido ressuscitado dos mortos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23711, 'nvi', 'Mateus', 'mt', 17, 10, 'Os discípulos lhe perguntaram: "Então, por que os mestres da lei dizem que é necessário que Elias venha primeiro? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23712, 'nvi', 'Mateus', 'mt', 17, 11, 'Jesus respondeu: "De fato, Elias vem e restaurará todas as coisas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23713, 'nvi', 'Mateus', 'mt', 17, 12, 'Mas eu lhes digo: Elias já veio, e eles não o reconheceram, mas fizeram com ele tudo o que quiseram. Da mesma forma o Filho do homem será maltratado por eles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23714, 'nvi', 'Mateus', 'mt', 17, 13, 'Então os discípulos entenderam que era de João Batista que ele tinha falado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23715, 'nvi', 'Mateus', 'mt', 17, 14, 'Quando chegaram onde estava a multidão, um homem aproximou-se de Jesus, ajoelhou-se diante dele e disse:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23716, 'nvi', 'Mateus', 'mt', 17, 15, '"Senhor, tem misericórdia do meu filho. Ele tem ataques e está sofrendo muito. Muitas vezes cai no fogo ou na água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23717, 'nvi', 'Mateus', 'mt', 17, 16, 'Eu o trouxe aos teus discípulos, mas eles não puderam curá-lo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23718, 'nvi', 'Mateus', 'mt', 17, 17, 'Respondeu Jesus: "Ó geração incrédula e perversa, até quando estarei com vocês? Até quando terei que suportá-los? Tragam-me o menino".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23719, 'nvi', 'Mateus', 'mt', 17, 18, 'Jesus repreendeu o demônio; este saiu do menino e, desde aquele momento, ele ficou curado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23720, 'nvi', 'Mateus', 'mt', 17, 19, 'Então os discípulos aproximaram-se de Jesus em particular e perguntaram: "Por que não conseguimos expulsá-lo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23721, 'nvi', 'Mateus', 'mt', 17, 20, 'Ele respondeu: "Por que a fé que vocês têm é pequena. Eu lhes asseguro que se vocês tiverem fé do tamanho de um grão de mostarda, poderão dizer a este monte: ‘Vá daqui para lá’, e ele irá. Nada lhes será impossível.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23722, 'nvi', 'Mateus', 'mt', 17, 21, 'Mas esta espécie só sai pela oração e pelo jejum".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23723, 'nvi', 'Mateus', 'mt', 17, 22, 'Reunindo-se eles na Galiléia, Jesus lhes disse: "O Filho do homem será entregue nas mãos dos homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23724, 'nvi', 'Mateus', 'mt', 17, 23, 'Eles o matarão, e no terceiro dia ele ressuscitará". E os discípulos ficaram cheios de tristeza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23725, 'nvi', 'Mateus', 'mt', 17, 24, 'Quando Jesus e seus discípulos chegaram a Cafarnaum, os coletores do imposto de duas dracmas vieram a Pedro e perguntaram: "O mestre de vocês não paga o imposto do templo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23726, 'nvi', 'Mateus', 'mt', 17, 25, '"Sim, paga", respondeu ele. Quando Pedro entrou na casa, Jesus foi o primeiro a falar, perguntando-lhe: "O que você acha, Simão? De quem os reis da terra cobram tributos e impostos: de seus próprios filhos ou dos outros? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23727, 'nvi', 'Mateus', 'mt', 17, 26, '"Dos outros", respondeu Pedro. Disse-lhe Jesus: "Então os filhos estão isentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23728, 'nvi', 'Mateus', 'mt', 17, 27, 'Mas, para não escandalizá-los, vá ao mar e jogue o anzol. Tire o primeiro peixe que você pegar, abra-lhe a boca, e você encontrará uma moeda de quatro dracmas. Pegue-a e entregue-a a eles, para pagar o meu imposto e o seu".');
