INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23729, 'nvi', 'Mateus', 'mt', 18, 1, 'Naquele momento os discípulos chegaram a Jesus e perguntaram: "Quem é o maior no Reino dos céus? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23730, 'nvi', 'Mateus', 'mt', 18, 2, 'Chamando uma criança, colocou-a no meio deles,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23731, 'nvi', 'Mateus', 'mt', 18, 3, 'e disse: "Eu lhes asseguro que, a não ser que vocês se convertam e se tornem como crianças, jamais entrarão no Reino dos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23732, 'nvi', 'Mateus', 'mt', 18, 4, 'Portanto, quem se faz humilde como esta criança, este é o maior no Reino dos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23733, 'nvi', 'Mateus', 'mt', 18, 5, '"Quem recebe uma destas crianças em meu nome, está me recebendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23734, 'nvi', 'Mateus', 'mt', 18, 6, 'Mas se alguém fizer tropeçar um destes pequeninos que crêem em mim, melhor lhe seria amarrar uma pedra de moinho no pescoço e se afogar nas profundezas do mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23735, 'nvi', 'Mateus', 'mt', 18, 7, '"Ai do mundo, por causa das coisas que fazem tropeçar! É inevitável que tais coisas aconteçam, mas ai daquele por meio de quem elas acontecem!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23736, 'nvi', 'Mateus', 'mt', 18, 8, 'Se a sua mão ou o seu pé o fizerem tropeçar, corte-os e jogue-os fora. É melhor entrar na vida mutilado ou aleijado do que, tendo as duas mãos ou os dois pés, ser lançado no fogo eterno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23737, 'nvi', 'Mateus', 'mt', 18, 9, 'E se o seu olho o fizer tropeçar, arranque-o e jogue-o fora. É melhor entrar na vida com um só olho do que, tendo os dois olhos, ser lançado no fogo do inferno".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23738, 'nvi', 'Mateus', 'mt', 18, 10, '"Cuidado para não desprezarem um só destes pequeninos! Pois eu lhes digo que os anjos deles nos céus estão sempre vendo a face de meu Pai celeste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23739, 'nvi', 'Mateus', 'mt', 18, 11, 'O Filho do homem veio para salvar o que se havia perdido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23740, 'nvi', 'Mateus', 'mt', 18, 12, '"O que acham vocês? Se alguém possui cem ovelhas, e uma delas se perde, não deixará as noventa e nove nos montes, indo procurar a que se perdeu?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23741, 'nvi', 'Mateus', 'mt', 18, 13, 'E se conseguir encontrá-la, garanto-lhes que ele ficará mais contente com aquela ovelha do que com as noventa e nove que não se perderam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23742, 'nvi', 'Mateus', 'mt', 18, 14, 'Da mesma forma, o Pai de vocês, que está nos céus, não quer que nenhum destes pequeninos se perca".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23743, 'nvi', 'Mateus', 'mt', 18, 15, '"Se o seu irmão pecar contra você, vá e, a sós com ele, mostre-lhe o erro. Se ele o ouvir, você ganhou seu irmão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23744, 'nvi', 'Mateus', 'mt', 18, 16, 'Mas se ele não o ouvir, leve consigo mais um ou dois outros, de modo que ‘qualquer acusação seja confirmada pelo depoimento de duas ou três testemunhas’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23745, 'nvi', 'Mateus', 'mt', 18, 17, 'Se ele se recusar a ouvi-los, conte à igreja; e se ele se recusar a ouvir também a igreja, trate-o como pagão ou publicano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23746, 'nvi', 'Mateus', 'mt', 18, 18, '"Digo-lhes a verdade: Tudo o que vocês ligarem na terra terá sido ligado no céu, e tudo o que vocês desligarem na terra terá sido desligado no céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23747, 'nvi', 'Mateus', 'mt', 18, 19, '"Também lhes digo que se dois de vocês concordarem na terra em qualquer assunto sobre o qual pedirem, isso lhes será feito por meu Pai que está nos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23748, 'nvi', 'Mateus', 'mt', 18, 20, 'Pois onde se reunirem dois ou três em meu nome, ali eu estou no meio deles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23749, 'nvi', 'Mateus', 'mt', 18, 21, 'Então Pedro aproximou-se de Jesus e perguntou: "Senhor, quantas vezes deverei perdoar a meu irmão quando ele pecar contra mim? Até sete vezes? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23750, 'nvi', 'Mateus', 'mt', 18, 22, 'Jesus respondeu: "Eu lhe digo: não até sete, mas até setenta vezes sete.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23751, 'nvi', 'Mateus', 'mt', 18, 23, '"Por isso, o Reino dos céus é como um rei que desejava acertar contas com seus servos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23752, 'nvi', 'Mateus', 'mt', 18, 24, 'Quando começou o acerto, foi trazido à sua presença um que lhe devia uma enorme quantidade de prata.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23753, 'nvi', 'Mateus', 'mt', 18, 25, 'Como não tinha condições de pagar, o senhor ordenou que ele, sua mulher, seus filhos e tudo o que ele possuía fossem vendidos para pagar a dívida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23754, 'nvi', 'Mateus', 'mt', 18, 26, '"O servo prostrou-se diante dele e lhe implorou: ‘Tem paciência comigo, e eu te pagarei tudo’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23755, 'nvi', 'Mateus', 'mt', 18, 27, 'O senhor daquele servo teve compaixão dele, cancelou a dívida e o deixou ir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23756, 'nvi', 'Mateus', 'mt', 18, 28, '"Mas quando aquele servo saiu, encontrou um de seus conservos, que lhe devia cem denários. Agarrou-o e começou a sufocá-lo, dizendo: ‘Pague-me o que me deve! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23757, 'nvi', 'Mateus', 'mt', 18, 29, '"Então o seu conservo caiu de joelhos e implorou-lhe: ‘Tenha paciência comigo, e eu lhe pagarei’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23758, 'nvi', 'Mateus', 'mt', 18, 30, '"Mas ele não quis. Antes, saiu e mandou lançá-lo na prisão, até que pagasse a dívida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23759, 'nvi', 'Mateus', 'mt', 18, 31, 'Quando os outros servos, companheiros dele, viram o que havia acontecido, ficaram muito tristes e foram contar ao seu senhor tudo o que havia acontecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23760, 'nvi', 'Mateus', 'mt', 18, 32, '"Então o senhor chamou o servo e disse: ‘Servo mau, cancelei toda a sua dívida porque você me implorou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23761, 'nvi', 'Mateus', 'mt', 18, 33, 'Você não devia ter tido misericórdia do seu conservo como eu tive de você? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23762, 'nvi', 'Mateus', 'mt', 18, 34, 'Irado, seu senhor entregou-o aos torturadores, até que pagasse tudo o que devia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23763, 'nvi', 'Mateus', 'mt', 18, 35, '"Assim também lhes fará meu Pai celestial, se cada um de vocês não perdoar de coração a seu irmão".');
