INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23764, 'nvi', 'Mateus', 'mt', 19, 1, 'Tendo acabado de dizer essas coisas, Jesus saiu da Galiléia e foi para a região da Judéia, no outro lado do Jordão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23765, 'nvi', 'Mateus', 'mt', 19, 2, 'Grandes multidões o seguiam, e ele as curou ali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23766, 'nvi', 'Mateus', 'mt', 19, 3, 'Alguns fariseus aproximaram-se dele para pô-lo à prova. E perguntaram-lhe: "É permitido ao homem divorciar-se de sua mulher por qualquer motivo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23767, 'nvi', 'Mateus', 'mt', 19, 4, 'Ele respondeu: "Vocês não leram que, no princípio, o Criador ‘os fez homem e mulher’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23768, 'nvi', 'Mateus', 'mt', 19, 5, 'e disse: ‘Por essa razão, o homem deixará pai e mãe e se unirá à sua mulher, e os dois se tornarão uma só carne’?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23769, 'nvi', 'Mateus', 'mt', 19, 6, 'Assim, eles já não são dois, mas sim uma só carne. Portanto, o que Deus uniu, ninguém o separe".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23770, 'nvi', 'Mateus', 'mt', 19, 7, 'Perguntaram eles: "Então, por que Moisés mandou dar uma certidão de divórcio à mulher e mandá-la embora? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23771, 'nvi', 'Mateus', 'mt', 19, 8, 'Jesus respondeu: "Moisés lhes permitiu divorciar-se de suas mulheres por causa da dureza de coração de vocês. Mas não foi assim desde o princípio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23772, 'nvi', 'Mateus', 'mt', 19, 9, 'Eu lhes digo que todo aquele que se divorciar de sua mulher, exceto por imoralidade sexual, e se casar com outra mulher, estará cometendo adultério".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23773, 'nvi', 'Mateus', 'mt', 19, 10, 'Os discípulos lhe disseram: "Se esta é a situação entre o homem e sua mulher, é melhor não casar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23774, 'nvi', 'Mateus', 'mt', 19, 11, 'Jesus respondeu: "Nem todos têm condições de aceitar esta palavra; somente aqueles a quem isso é dado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23775, 'nvi', 'Mateus', 'mt', 19, 12, 'Alguns são eunucos porque nasceram assim; outros foram feitos assim pelos homens; outros ainda se fizeram eunucos por causa do Reino dos céus. Quem puder aceitar isso, aceite".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23776, 'nvi', 'Mateus', 'mt', 19, 13, 'Depois trouxeram crianças a Jesus, para que lhes impusesse as mãos e orasse por elas. Mas os discípulos os repreendiam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23777, 'nvi', 'Mateus', 'mt', 19, 14, 'Então disse Jesus: "Deixem vir a mim as crianças e não as impeçam; pois o Reino dos céus pertence aos que são semelhantes a elas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23778, 'nvi', 'Mateus', 'mt', 19, 15, 'Depois de lhes impor as mãos, partiu dali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23779, 'nvi', 'Mateus', 'mt', 19, 16, 'Eis que alguém se aproximou de Jesus e lhe perguntou: "Mestre, que farei de bom para ter a vida eterna? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23780, 'nvi', 'Mateus', 'mt', 19, 17, 'Respondeu-lhe Jesus: "Por que você me pergunta sobre o que é bom? Há somente um que é bom. Se você quer entrar na vida, obedeça aos mandamentos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23781, 'nvi', 'Mateus', 'mt', 19, 18, '"Quais? ", perguntou ele. Jesus respondeu: " ‘Não matarás, não adulterarás, não furtarás, não darás falso testemunho,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23782, 'nvi', 'Mateus', 'mt', 19, 19, 'honra teu pai e tua mãe’ e ‘amarás o teu próximo como a ti mesmo’".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23783, 'nvi', 'Mateus', 'mt', 19, 20, 'Disse-lhe o jovem: "A tudo isso tenho obedecido. O que me falta ainda? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23784, 'nvi', 'Mateus', 'mt', 19, 21, 'Jesus respondeu: "Se você quer ser perfeito, vá, venda os seus bens e dê o dinheiro aos pobres, e você terá um tesouro no céu. Depois, venha e siga-me".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23785, 'nvi', 'Mateus', 'mt', 19, 22, 'Ouvindo isso, o jovem afastou-se triste, porque tinha muitas riquezas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23786, 'nvi', 'Mateus', 'mt', 19, 23, 'Então Jesus disse aos discípulos: "Digo-lhes a verdade: Dificilmente um rico entrará no Reino dos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23787, 'nvi', 'Mateus', 'mt', 19, 24, 'E lhes digo ainda: é mais fácil passar um camelo pelo fundo de uma agulha do que um rico entrar no Reino de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23788, 'nvi', 'Mateus', 'mt', 19, 25, 'Ao ouvirem isso, os discípulos ficaram perplexos e perguntaram: "Neste caso, quem pode ser salvo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23789, 'nvi', 'Mateus', 'mt', 19, 26, 'Jesus olhou para eles e respondeu: "Para o homem é impossível, mas para Deus todas as coisas são possíveis".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23790, 'nvi', 'Mateus', 'mt', 19, 27, 'Então Pedro lhe respondeu: "Nós deixamos tudo para seguir-te! Que será de nós? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23791, 'nvi', 'Mateus', 'mt', 19, 28, 'Jesus lhes disse: "Digo-lhes a verdade: Por ocasião da regeneração de todas as coisas, quando o Filho do homem se assentar em seu trono glorioso, vocês que me seguiram também se assentarão em doze tronos, para julgar as doze tribos de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23792, 'nvi', 'Mateus', 'mt', 19, 29, 'E todos os que tiverem deixado casas, irmãos, irmãs, pai, mãe, filhos ou campos, por minha causa, receberão cem vezes mais e herdarão a vida eterna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23793, 'nvi', 'Mateus', 'mt', 19, 30, 'Contudo, muitos primeiros serão últimos, e muitos últimos serão primeiros".');
