INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23171, 'nvi', 'Mateus', 'mt', 2, 1, 'Depois que Jesus nasceu em Belém da Judéia, nos dias do rei Herodes, magos vindos do Oriente chegaram a Jerusalém');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23172, 'nvi', 'Mateus', 'mt', 2, 2, 'e perguntaram: "Onde está o recém-nascido rei dos judeus? Vimos a sua estrela no Oriente e viemos adorá-lo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23173, 'nvi', 'Mateus', 'mt', 2, 3, 'Quando o rei Herodes ouviu isso, ficou perturbado, e com ele toda a Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23174, 'nvi', 'Mateus', 'mt', 2, 4, 'Tendo reunido todos os chefes dos sacerdotes do povo e os mestres da lei, perguntou-lhes onde deveria nascer o Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23175, 'nvi', 'Mateus', 'mt', 2, 5, 'E eles responderam: "Em Belém da Judéia; pois assim escreveu o profeta:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23176, 'nvi', 'Mateus', 'mt', 2, 6, '‘Mas tu, Belém, da terra de Judá, de forma alguma és a menor entre as principais cidades de Judá; pois de ti virá o líder que, como pastor, conduzirá Israel, o meu povo’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23177, 'nvi', 'Mateus', 'mt', 2, 7, 'Então Herodes chamou os magos secretamente e informou-se com eles a respeito do tempo exato em que a estrela tinha aparecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23178, 'nvi', 'Mateus', 'mt', 2, 8, 'Enviou-os a Belém e disse: "Vão informar-se com exatidão sobre o menino. Logo que o encontrarem, avisem-me, para que eu também vá adorá-lo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23179, 'nvi', 'Mateus', 'mt', 2, 9, 'Depois de ouvirem o rei, eles seguiram o seu caminho, e a estrela que tinham visto no Oriente foi adiante deles, até que finalmente parou sobre o lugar onde estava o menino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23180, 'nvi', 'Mateus', 'mt', 2, 10, 'Quando tornaram a ver a estrela, encheram-se de júbilo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23181, 'nvi', 'Mateus', 'mt', 2, 11, 'Ao entrarem na casa, viram o menino com Maria, sua mãe, e, prostrando-se, o adoraram. Então abriram os seus tesouros e lhe deram presentes: ouro, incenso e mirra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23182, 'nvi', 'Mateus', 'mt', 2, 12, 'E, tendo sido advertidos em sonho para não voltarem a Herodes, retornaram a sua terra por outro caminho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23183, 'nvi', 'Mateus', 'mt', 2, 13, 'Depois que partiram, um anjo do Senhor apareceu a José em sonho e disse-lhe: "Levante-se, tome o menino e sua mãe, e fuja para o Egito. Fique lá até que eu lhe diga, pois Herodes vai procurar o menino para matá-lo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23184, 'nvi', 'Mateus', 'mt', 2, 14, 'Então ele se levantou, tomou o menino e sua mãe durante a noite, e partiu para o Egito,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23185, 'nvi', 'Mateus', 'mt', 2, 15, 'onde ficou até a morte de Herodes. E assim se cumpriu o que o Senhor tinha dito pelo profeta: "Do Egito chamei o meu filho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23186, 'nvi', 'Mateus', 'mt', 2, 16, 'Quando Herodes percebeu que havia sido enganado pelos magos, ficou furioso e ordenou que matassem todos os meninos de dois anos para baixo, em Belém e nas proximidades, de acordo com a informação que havia obtido dos magos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23187, 'nvi', 'Mateus', 'mt', 2, 17, 'Então se cumpriu o que fora dito pelo profeta Jeremias:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23188, 'nvi', 'Mateus', 'mt', 2, 18, '"Ouviu-se uma voz em Ramá, choro e grande lamentação; é Raquel que chora por seus filhos e recusa ser consolada, porque já não existem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23189, 'nvi', 'Mateus', 'mt', 2, 19, 'Depois que Herodes morreu, um anjo do Senhor apareceu em sonho a José, no Egito,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23190, 'nvi', 'Mateus', 'mt', 2, 20, 'e disse: "Levante-se, tome o menino e sua mãe, e vá para a terra de Israel, pois estão mortos os que procuravam tirar a vida do menino".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23191, 'nvi', 'Mateus', 'mt', 2, 21, 'Ele se levantou, tomou o menino e sua mãe, e foi para a terra de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23192, 'nvi', 'Mateus', 'mt', 2, 22, 'Mas, ao ouvir que Arquelau estava reinando na Judéia em lugar de seu pai Herodes, teve medo de ir para lá. Tendo sido avisado em sonho, retirou-se para a região da Galiléia');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23193, 'nvi', 'Mateus', 'mt', 2, 23, 'e foi viver numa cidade chamada Nazaré. Assim cumpriu-se o que fora dito pelos profetas: Ele será chamado Nazareno.');
