INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23828, 'nvi', 'Mateus', 'mt', 21, 1, 'Quando se aproximaram de Jerusalém e chegaram a Betfagé, ao monte das Oliveiras, Jesus enviou dois discípulos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23829, 'nvi', 'Mateus', 'mt', 21, 2, 'dizendo-lhes: "Vão ao povoado que está adiante de vocês; logo encontrarão uma jumenta amarrada, com um jumentinho ao lado. Desamarrem-nos e tragam-nos para mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23830, 'nvi', 'Mateus', 'mt', 21, 3, 'Se alguém lhes perguntar algo, digam-lhe que o Senhor precisa deles e logo os enviará de volta".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23831, 'nvi', 'Mateus', 'mt', 21, 4, 'Isso aconteceu para que se cumprisse o que fora dito pelo profeta:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23832, 'nvi', 'Mateus', 'mt', 21, 5, '"Digam à cidade de Sião: ‘Eis que o seu rei vem a você, humilde e montado num jumento, num jumentinho, cria de jumenta’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23833, 'nvi', 'Mateus', 'mt', 21, 6, 'Os discípulos foram e fizeram o que Jesus tinha ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23834, 'nvi', 'Mateus', 'mt', 21, 7, 'Trouxeram a jumenta e o jumentinho, colocaram sobre eles os seus mantos, e sobre estes Jesus montou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23835, 'nvi', 'Mateus', 'mt', 21, 8, 'Uma grande multidão estendeu seus mantos pelo caminho, outros cortavam ramos de árvores e os espalhavam pelo caminho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23836, 'nvi', 'Mateus', 'mt', 21, 9, 'A multidão que ia adiante dele e os que o seguiam gritavam: "Hosana ao Filho de Davi! " "Bendito é o que vem em nome do Senhor! " "Hosana nas alturas! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23837, 'nvi', 'Mateus', 'mt', 21, 10, 'Quando Jesus entrou em Jerusalém, toda a cidade ficou agitada e perguntava: "Quem é este? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23838, 'nvi', 'Mateus', 'mt', 21, 11, 'A multidão respondia: "Este é Jesus, o profeta de Nazaré da Galiléia".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23839, 'nvi', 'Mateus', 'mt', 21, 12, 'Jesus entrou no templo e expulsou todos os que ali estavam comprando e vendendo. Derrubou as mesas dos cambistas e as cadeiras dos que vendiam pombas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23840, 'nvi', 'Mateus', 'mt', 21, 13, 'e lhes disse: "Está escrito: ‘A minha casa será chamada casa de oração’; mas vocês estão fazendo dela um ‘covil de ladrões’".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23841, 'nvi', 'Mateus', 'mt', 21, 14, 'Os cegos e os mancos aproximaram-se dele no templo, e ele os curou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23842, 'nvi', 'Mateus', 'mt', 21, 15, 'Mas quando os chefes dos sacerdotes e os mestres da lei viram as coisas maravilhosas que Jesus fazia e as crianças gritando no templo: "Hosana ao Filho de Davi", ficaram indignados,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23843, 'nvi', 'Mateus', 'mt', 21, 16, 'e lhe perguntaram: "Não estás ouvindo o que estas crianças estão dizendo? " Respondeu Jesus: "Sim, vocês nunca leram: ‘dos lábios das crianças e dos recém-nascidos suscitaste louvor’? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23844, 'nvi', 'Mateus', 'mt', 21, 17, 'E, deixando-os, saiu da cidade para Betânia, onde passou a noite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23845, 'nvi', 'Mateus', 'mt', 21, 18, 'De manhã cedo, quando voltava para a cidade, Jesus teve fome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23846, 'nvi', 'Mateus', 'mt', 21, 19, 'Vendo uma figueira à beira do caminho, aproximou-se dela, mas nada encontrou, a não ser folhas. Então lhe disse: "Nunca mais dê frutos! " Imediatamente a árvore secou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23847, 'nvi', 'Mateus', 'mt', 21, 20, 'Ao verem isso, os discípulos ficaram espantados e perguntaram: "Como a figueira secou tão depressa? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23848, 'nvi', 'Mateus', 'mt', 21, 21, 'Jesus respondeu: "Eu lhes asseguro que, se vocês tiverem fé e não duvidarem, poderão fazer não somente o que foi feito à figueira, mas também dizer a este monte: ‘Levante-se e atire-se no mar’, e assim será feito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23849, 'nvi', 'Mateus', 'mt', 21, 22, 'E tudo o que pedirem em oração, se crerem, vocês receberão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23850, 'nvi', 'Mateus', 'mt', 21, 23, 'Jesus entrou no templo e, enquanto ensinava, aproximaram-se dele os chefes dos sacerdotes e os líderes religiosos do povo e perguntaram: "Com que autoridade estás fazendo estas coisas? E quem te deu tal autoridade? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23851, 'nvi', 'Mateus', 'mt', 21, 24, 'Respondeu Jesus: "Eu também lhes farei uma pergunta. Se vocês me responderem, eu lhes direi com que autoridade estou fazendo estas coisas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23852, 'nvi', 'Mateus', 'mt', 21, 25, 'De onde era o batismo de João? Do céu ou dos homens? " Eles discutiam entre si, dizendo: "Se dissermos: ‘do céu’, ele perguntará: ‘Então por que vocês não creram nele? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23853, 'nvi', 'Mateus', 'mt', 21, 26, 'Mas se dissermos: ‘dos homens’ — temos medo do povo, pois todos consideram João um profeta".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23854, 'nvi', 'Mateus', 'mt', 21, 27, 'Eles responderam a Jesus: "Não sabemos". E ele lhes disse: "Tampouco lhes direi com que autoridade estou fazendo estas coisas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23855, 'nvi', 'Mateus', 'mt', 21, 28, '"O que acham? Havia um homem que tinha dois filhos. Chegando ao primeiro, disse: ‘Filho, vá trabalhar hoje na vinha’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23856, 'nvi', 'Mateus', 'mt', 21, 29, '"E este respondeu: ‘Não quero! ’ Mas depois mudou de idéia e foi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23857, 'nvi', 'Mateus', 'mt', 21, 30, '"O pai chegou ao outro filho e disse a mesma coisa. Ele respondeu: ‘Sim, senhor! ’ Mas não foi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23858, 'nvi', 'Mateus', 'mt', 21, 31, '"Qual dos dois fez a vontade do pai? " "O primeiro", responderam eles. Jesus lhes disse: "Digo-lhes a verdade: Os publicanos e as prostitutas estão entrando antes de vocês no Reino de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23859, 'nvi', 'Mateus', 'mt', 21, 32, 'Porque João veio para lhes mostrar o caminho da justiça, e vocês não creram nele, mas os publicanos e as prostitutas creram. E, mesmo depois de verem isso, vocês não se arrependeram nem creram nele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23860, 'nvi', 'Mateus', 'mt', 21, 33, '"Ouçam outra parábola: Havia um proprietário de terras que plantou uma vinha. Colocou uma cerca ao redor dela, cavou um tanque para prensar as uvas e construiu uma torre. Depois arrendou a vinha a alguns lavradores e foi fazer uma viagem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23861, 'nvi', 'Mateus', 'mt', 21, 34, 'Aproximando-se a época da colheita, enviou seus servos aos lavradores, para receber os frutos que lhe pertenciam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23862, 'nvi', 'Mateus', 'mt', 21, 35, '"Os lavradores agarraram seus servos; a um espancaram, a outro mataram e apedrejaram o terceiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23863, 'nvi', 'Mateus', 'mt', 21, 36, 'Então enviou-lhes outros servos em maior número, e os lavradores os trataram da mesma forma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23864, 'nvi', 'Mateus', 'mt', 21, 37, 'Por último, enviou-lhes seu filho, dizendo: ‘A meu filho respeitarão’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23865, 'nvi', 'Mateus', 'mt', 21, 38, '"Mas quando os lavradores viram o filho, disseram uns aos outros: ‘Este é o herdeiro. Venham, vamos matá-lo e tomar a sua herança’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23866, 'nvi', 'Mateus', 'mt', 21, 39, 'Assim eles o agarraram, lançaram-no para fora da vinha e o mataram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23867, 'nvi', 'Mateus', 'mt', 21, 40, '"Portanto, quando vier o dono da vinha, o que fará àqueles lavradores? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23868, 'nvi', 'Mateus', 'mt', 21, 41, 'Responderam eles: "Matará de modo horrível esses perversos e arrendará a vinha a outros lavradores, que lhe dêem a sua parte no tempo da colheita".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23869, 'nvi', 'Mateus', 'mt', 21, 42, 'Jesus lhes disse: "Vocês nunca leram nas Escrituras? ‘A pedra que os construtores rejeitaram tornou-se a pedra angular; isso vem do Senhor, e é algo maravilhoso para nós’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23870, 'nvi', 'Mateus', 'mt', 21, 43, '"Portanto eu lhes digo que o Reino de Deus será tirado de vocês e será dado a um povo que dê os frutos do Reino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23871, 'nvi', 'Mateus', 'mt', 21, 44, 'Aquele que cair sobre esta pedra será despedaçado, e aquele sobre quem ela cair será reduzido a pó".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23872, 'nvi', 'Mateus', 'mt', 21, 45, 'Quando os chefes dos sacerdotes e os fariseus ouviram as parábolas de Jesus, compreenderam que ele falava a respeito deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23873, 'nvi', 'Mateus', 'mt', 21, 46, 'E procuravam um meio de prendê-lo; mas tinham medo das multidões, pois elas o consideravam profeta.');
