INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23874, 'nvi', 'Mateus', 'mt', 22, 1, 'Jesus lhes falou novamente por parábolas, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23875, 'nvi', 'Mateus', 'mt', 22, 2, '"O Reino dos céus é como um rei que preparou um banquete de casamento para seu filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23876, 'nvi', 'Mateus', 'mt', 22, 3, 'Enviou seus servos aos que tinham sido convidados para o banquete, dizendo-lhes que viessem; mas eles não quiseram vir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23877, 'nvi', 'Mateus', 'mt', 22, 4, '"De novo enviou outros servos e disse: ‘Digam aos que foram convidados que preparei meu banquete: meus bois e meus novilhos gordos foram abatidos, e tudo está preparado. Venham para o banquete de casamento! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23878, 'nvi', 'Mateus', 'mt', 22, 5, '"Mas eles não lhes deram atenção e saíram, um para o seu campo, outro para os seus negócios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23879, 'nvi', 'Mateus', 'mt', 22, 6, 'Os restantes, agarrando os servos, maltrataram-nos e os mataram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23880, 'nvi', 'Mateus', 'mt', 22, 7, 'O rei ficou irado e, enviando o seu exército, destruiu aqueles assassinos e queimou a cidade deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23881, 'nvi', 'Mateus', 'mt', 22, 8, '"Então disse a seus servos: ‘O banquete de casamento está pronto, mas os meus convidados não eram dignos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23882, 'nvi', 'Mateus', 'mt', 22, 9, 'Vão às esquinas e convidem para o banquete todos os que vocês encontrarem’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23883, 'nvi', 'Mateus', 'mt', 22, 10, 'Então os servos saíram para as ruas e reuniram todas as pessoas que puderam encontrar, gente boa e gente má, e a sala do banquete de casamento ficou cheia de convidados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23884, 'nvi', 'Mateus', 'mt', 22, 11, '"Mas quando o rei entrou para ver os convidados, notou ali um homem que não estava usando veste nupcial.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23885, 'nvi', 'Mateus', 'mt', 22, 12, 'E lhe perguntou: ‘Amigo, como você entrou aqui sem veste nupcial? ’ O homem emudeceu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23886, 'nvi', 'Mateus', 'mt', 22, 13, '"Então o rei disse aos que serviam: ‘Amarrem-lhe as mãos e os pés, e lancem-no para fora, nas trevas; ali haverá choro e ranger de dentes’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23887, 'nvi', 'Mateus', 'mt', 22, 14, '"Pois muitos são chamados, mas poucos são escolhidos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23888, 'nvi', 'Mateus', 'mt', 22, 15, 'Então os fariseus saíram e começaram a planejar um meio de enredá-lo em suas próprias palavras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23889, 'nvi', 'Mateus', 'mt', 22, 16, 'Enviaram-lhe seus discípulos juntamente com os herodianos que lhe disseram: "Mestre, sabemos que és íntegro e que ensinas o caminho de Deus conforme a verdade. Tu não te deixas influenciar por ninguém, porque não te prendes à aparência dos homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23890, 'nvi', 'Mateus', 'mt', 22, 17, 'Dize-nos, pois: Qual é a tua opinião? É certo pagar imposto a César ou não? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23891, 'nvi', 'Mateus', 'mt', 22, 18, 'Mas Jesus, percebendo a má intenção deles, perguntou: "Hipócritas! Por que vocês estão me pondo à prova?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23892, 'nvi', 'Mateus', 'mt', 22, 19, 'Mostrem-me a moeda usada para pagar o imposto". Eles lhe mostraram um denário,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23893, 'nvi', 'Mateus', 'mt', 22, 20, 'e ele lhes perguntou: "De quem é esta imagem e esta inscrição? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23894, 'nvi', 'Mateus', 'mt', 22, 21, '"De César", responderam eles. E ele lhes disse: "Então, dêem a César o que é de César e a Deus o que é de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23895, 'nvi', 'Mateus', 'mt', 22, 22, 'Ao ouvirem isso, eles ficaram admirados; e, deixando-o, retiraram-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23896, 'nvi', 'Mateus', 'mt', 22, 23, 'Naquele mesmo dia, os saduceus, que dizem que não há ressurreição, aproximaram-se dele com a seguinte questão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23897, 'nvi', 'Mateus', 'mt', 22, 24, '"Mestre, Moisés disse que se um homem morrer sem deixar filhos, seu irmão deverá casar-se com a viúva e dar-lhe descendência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23898, 'nvi', 'Mateus', 'mt', 22, 25, 'Entre nós havia sete irmãos. O primeiro casou-se e morreu. Como não teve filhos, deixou a mulher para seu irmão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23899, 'nvi', 'Mateus', 'mt', 22, 26, 'A mesma coisa aconteceu com o segundo, com o terceiro, até o sétimo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23900, 'nvi', 'Mateus', 'mt', 22, 27, 'Finalmente, depois de todos, morreu a mulher.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23901, 'nvi', 'Mateus', 'mt', 22, 28, 'Pois bem, na ressurreição, de qual dos sete ela será esposa, visto que todos foram casados com ela? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23902, 'nvi', 'Mateus', 'mt', 22, 29, 'Jesus respondeu: "Vocês estão enganados porque não conhecem as Escrituras nem o poder de Deus!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23903, 'nvi', 'Mateus', 'mt', 22, 30, 'Na ressurreição, as pessoas não se casam nem são dadas em casamento; mas são como os anjos no céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23904, 'nvi', 'Mateus', 'mt', 22, 31, 'E quanto à ressurreição dos mortos, vocês não leram o que Deus lhes disse:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23905, 'nvi', 'Mateus', 'mt', 22, 32, '‘Eu sou o Deus de Abraão, o Deus de Isaque e o Deus de Jacó’? Ele não é Deus de mortos, mas de vivos! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23906, 'nvi', 'Mateus', 'mt', 22, 33, 'Ouvindo isso, a multidão ficou admirada com o seu ensino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23907, 'nvi', 'Mateus', 'mt', 22, 34, 'Ao ouvirem dizer que Jesus havia deixado os saduceus sem resposta, os fariseus se reuniram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23908, 'nvi', 'Mateus', 'mt', 22, 35, 'Um deles, perito na lei, o pôs à prova com esta pergunta:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23909, 'nvi', 'Mateus', 'mt', 22, 36, '"Mestre, qual é o maior mandamento da Lei? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23910, 'nvi', 'Mateus', 'mt', 22, 37, 'Respondeu Jesus: " ‘Ame o Senhor, o seu Deus de todo o seu coração, de toda a sua alma e de todo o seu entendimento’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23911, 'nvi', 'Mateus', 'mt', 22, 38, 'Este é o primeiro e maior mandamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23912, 'nvi', 'Mateus', 'mt', 22, 39, 'E o segundo é semelhante a ele: ‘Ame o seu próximo como a si mesmo’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23913, 'nvi', 'Mateus', 'mt', 22, 40, 'Destes dois mandamentos dependem toda a Lei e os Profetas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23914, 'nvi', 'Mateus', 'mt', 22, 41, 'Estando os fariseus reunidos, Jesus lhes perguntou:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23915, 'nvi', 'Mateus', 'mt', 22, 42, '"O que vocês pensam a respeito do Cristo? De quem ele é filho? " "É filho de Davi", responderam eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23916, 'nvi', 'Mateus', 'mt', 22, 43, 'Ele lhes disse: "Então, como é que Davi, falando pelo Espírito, o chama ‘Senhor’? Pois ele afirma:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23917, 'nvi', 'Mateus', 'mt', 22, 44, '‘O Senhor disse ao meu Senhor: "Senta-te à minha direita, até que eu ponha os teus inimigos debaixo de teus pés" ’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23918, 'nvi', 'Mateus', 'mt', 22, 45, 'Se, pois, Davi o chama ‘Senhor’, como pode ser ele seu filho? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23919, 'nvi', 'Mateus', 'mt', 22, 46, 'Ninguém conseguia responder-lhe uma palavra; e daquele dia em diante, ninguém jamais se atreveu a lhe fazer perguntas.');
