INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23920, 'nvi', 'Mateus', 'mt', 23, 1, 'Então, Jesus disse à multidão e aos seus discípulos:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23921, 'nvi', 'Mateus', 'mt', 23, 2, '"Os mestres da lei e os fariseus se assentam na cadeira de Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23922, 'nvi', 'Mateus', 'mt', 23, 3, 'Obedeçam-lhes e façam tudo o que eles lhes dizem. Mas não façam o que eles fazem, pois não praticam o que pregam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23923, 'nvi', 'Mateus', 'mt', 23, 4, 'Eles atam fardos pesados e os colocam sobre os ombros dos homens, mas eles mesmos não estão dispostos a levantar um só dedo para movê-los.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23924, 'nvi', 'Mateus', 'mt', 23, 5, '"Tudo o que fazem é para serem vistos pelos homens. Eles fazem seus filactérios bem largos e as franjas de suas vestes bem longas;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23925, 'nvi', 'Mateus', 'mt', 23, 6, 'gostam do lugar de honra nos banquetes e dos assentos mais importantes nas sinagogas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23926, 'nvi', 'Mateus', 'mt', 23, 7, 'de serem saudados nas praças e de serem chamados ‘rabis’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23927, 'nvi', 'Mateus', 'mt', 23, 8, '"Mas vocês não devem ser chamados ‘rabis’; um só é o mestre de vocês, e todos vocês são irmãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23928, 'nvi', 'Mateus', 'mt', 23, 9, 'A ninguém na terra chamem ‘pai’, porque vocês só têm um Pai, aquele que está nos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23929, 'nvi', 'Mateus', 'mt', 23, 10, 'Tampouco vocês devem ser chamados ‘chefes’, porquanto vocês têm um só Chefe, o Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23930, 'nvi', 'Mateus', 'mt', 23, 11, 'O maior entre vocês deverá ser servo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23931, 'nvi', 'Mateus', 'mt', 23, 12, 'Pois todo aquele que a si mesmo se exaltar será humilhado, e todo aquele que a si mesmo se humilhar será exaltado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23932, 'nvi', 'Mateus', 'mt', 23, 13, '"Ai de vocês, mestres da lei e fariseus, hipócritas! Vocês fecham o Reino dos céus diante dos homens! Vocês mesmos não entram, nem deixam entrar aqueles que gostariam de fazê-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23933, 'nvi', 'Mateus', 'mt', 23, 14, '"Ai de vocês, mestres da lei e fariseus, hipócritas! Vocês devoram as casas das viúvas e, para disfarçar, fazem longas orações. Por isso serão castigados mais severamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23934, 'nvi', 'Mateus', 'mt', 23, 15, '"Ai de vocês, mestres da lei e fariseus, hipócritas, porque percorrem terra e mar para fazer um convertido e, quando conseguem, vocês o tornam duas vezes mais filho do inferno do que vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23935, 'nvi', 'Mateus', 'mt', 23, 16, '"Ai de vocês, guias cegos!, pois dizem: ‘Se alguém jurar pelo santuário, isto nada significa; mas se alguém jurar pelo ouro do santuário, está obrigado por seu juramento’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23936, 'nvi', 'Mateus', 'mt', 23, 17, 'Cegos insensatos! Que é mais importante: o ouro ou o santuário que santifica o ouro?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23937, 'nvi', 'Mateus', 'mt', 23, 18, 'Vocês também dizem: ‘Se alguém jurar pelo altar, isto nada significa; mas se alguém jurar pela oferta que está sobre ele, está obrigado por seu juramento’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23938, 'nvi', 'Mateus', 'mt', 23, 19, 'Cegos! Que é mais importante: a oferta, ou o altar que santifica a oferta?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23939, 'nvi', 'Mateus', 'mt', 23, 20, 'Portanto, aquele que jurar pelo altar, jura por ele e por tudo o que está sobre ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23940, 'nvi', 'Mateus', 'mt', 23, 21, 'E o que jurar pelo santuário, jura por ele e por aquele que nele habita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23941, 'nvi', 'Mateus', 'mt', 23, 22, 'E aquele que jurar pelo céu, jura pelo trono de Deus e por aquele que nele se assenta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23942, 'nvi', 'Mateus', 'mt', 23, 23, '"Ai de vocês, mestres da lei e fariseus, hipócritas! Vocês dão o dízimo da hortelã, do endro e do cominho, mas têm negligenciado os preceitos mais importantes da lei: a justiça, a misericórdia e a fidelidade. Vocês devem praticar estas coisas, sem omitir aquelas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23943, 'nvi', 'Mateus', 'mt', 23, 24, 'Guias cegos! Vocês coam um mosquito e engolem um camelo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23944, 'nvi', 'Mateus', 'mt', 23, 25, '"Ai de vocês, mestres da lei e fariseus, hipócritas! Vocês limpam o exterior do copo e do prato, mas por dentro eles estão cheios de ganância e cobiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23945, 'nvi', 'Mateus', 'mt', 23, 26, 'Fariseu cego! Limpe primeiro o interior do copo e do prato, para que o exterior também fique limpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23946, 'nvi', 'Mateus', 'mt', 23, 27, '"Ai de vocês, mestres da lei e fariseus, hipócritas! Vocês são como sepulcros caiados: bonitos por fora, mas por dentro estão cheios de ossos e de todo tipo de imundície.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23947, 'nvi', 'Mateus', 'mt', 23, 28, 'Assim são vocês: por fora parecem justos ao povo, mas por dentro estão cheios de hipocrisia e maldade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23948, 'nvi', 'Mateus', 'mt', 23, 29, '"Ai de vocês, mestres da lei e fariseus, hipócritas! Vocês edificam os túmulos dos profetas e adornam os monumentos dos justos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23949, 'nvi', 'Mateus', 'mt', 23, 30, 'E dizem: ‘Se tivéssemos vivido no tempo dos nossos antepassados, não teríamos tomado parte com eles no derramamento do sangue dos profetas’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23950, 'nvi', 'Mateus', 'mt', 23, 31, 'Assim, vocês testemunham contra si mesmos que são descendentes dos que assassinaram os profetas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23951, 'nvi', 'Mateus', 'mt', 23, 32, 'Acabem, pois, de encher a medida do pecado dos seus antepassados!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23952, 'nvi', 'Mateus', 'mt', 23, 33, '"Serpentes! Raça de víboras! Como vocês escaparão da condenação ao inferno?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23953, 'nvi', 'Mateus', 'mt', 23, 34, 'Por isso, eu lhes estou enviando profetas, sábios e mestres. A uns vocês matarão e crucificarão; a outros açoitarão nas sinagogas de vocês e perseguirão de cidade em cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23954, 'nvi', 'Mateus', 'mt', 23, 35, 'E, assim, sobre vocês recairá todo o sangue justo derramado na terra, desde o sangue do justo Abel, até o sangue de Zacarias, filho de Baraquias, a quem vocês assassinaram entre o santuário e o altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23955, 'nvi', 'Mateus', 'mt', 23, 36, 'Eu lhes asseguro que tudo isso sobrevirá a esta geração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23956, 'nvi', 'Mateus', 'mt', 23, 37, '"Jerusalém, Jerusalém, você, que mata os profetas e apedreja os que lhe são enviados! Quantas vezes eu quis reunir os seus filhos, como a galinha reúne os seus pintinhos debaixo das suas asas, mas vocês não quiseram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23957, 'nvi', 'Mateus', 'mt', 23, 38, 'Eis que a casa de vocês ficará deserta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23958, 'nvi', 'Mateus', 'mt', 23, 39, 'Pois eu lhes digo que vocês não me verão desde agora, até que digam: ‘Bendito é o que vem em nome do Senhor’".');
