INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23959, 'nvi', 'Mateus', 'mt', 24, 1, 'Jesus saiu do templo e, enquanto caminhava, seus discípulos aproximaram-se dele para lhe mostrar as construções do templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23960, 'nvi', 'Mateus', 'mt', 24, 2, '"Vocês estão vendo tudo isto? ", perguntou ele. "Eu lhes garanto que não ficará aqui pedra sobre pedra; serão todas derrubadas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23961, 'nvi', 'Mateus', 'mt', 24, 3, 'Tendo Jesus se assentado no monte das Oliveiras, os discípulos dirigiram-se a ele em particular e disseram: "Dize-nos, quando acontecerão essas coisas? E qual será o sinal da tua vinda e do fim dos tempos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23962, 'nvi', 'Mateus', 'mt', 24, 4, 'Jesus respondeu: "Cuidado, que ninguém os engane.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23963, 'nvi', 'Mateus', 'mt', 24, 5, 'Pois muitos virão em meu nome, dizendo: ‘Eu sou o Cristo! ’ e enganarão a muitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23964, 'nvi', 'Mateus', 'mt', 24, 6, 'Vocês ouvirão falar de guerras e rumores de guerras, mas não tenham medo. É necessário que tais coisas aconteçam, mas ainda não é o fim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23965, 'nvi', 'Mateus', 'mt', 24, 7, 'Nação se levantará contra nação, e reino contra reino. Haverá fomes e terremotos em vários lugares.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23966, 'nvi', 'Mateus', 'mt', 24, 8, 'Tudo isso será o início das dores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23967, 'nvi', 'Mateus', 'mt', 24, 9, '"Então eles os entregarão para serem perseguidos e condenados à morte, e vocês serão odiados por todas as nações por minha causa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23968, 'nvi', 'Mateus', 'mt', 24, 10, 'Naquele tempo muitos ficarão escandalizados, trairão e odiarão uns aos outros,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23969, 'nvi', 'Mateus', 'mt', 24, 11, 'e numerosos falsos profetas surgirão e enganarão a muitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23970, 'nvi', 'Mateus', 'mt', 24, 12, 'Devido ao aumento da maldade, o amor de muitos esfriará,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23971, 'nvi', 'Mateus', 'mt', 24, 13, 'mas aquele que perseverar até o fim será salvo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23972, 'nvi', 'Mateus', 'mt', 24, 14, 'E este evangelho do Reino será pregado em todo o mundo como testemunho a todas as nações, e então virá o fim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23973, 'nvi', 'Mateus', 'mt', 24, 15, '"Assim, quando vocês virem ‘o sacrilégio terrível’, do qual falou o profeta Daniel, no lugar santo — quem lê, entenda —');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23974, 'nvi', 'Mateus', 'mt', 24, 16, 'então, os que estiverem na Judéia fujam para os montes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23975, 'nvi', 'Mateus', 'mt', 24, 17, 'Quem estiver no telhado de sua casa não desça para tirar dela coisa alguma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23976, 'nvi', 'Mateus', 'mt', 24, 18, 'Quem estiver no campo não volte para pegar seu manto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23977, 'nvi', 'Mateus', 'mt', 24, 19, 'Como serão terríveis aqueles dias para as grávidas e para as que estiverem amamentando!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23978, 'nvi', 'Mateus', 'mt', 24, 20, 'Orem para que a fuga de vocês não aconteça no inverno nem no sábado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23979, 'nvi', 'Mateus', 'mt', 24, 21, 'Porque haverá então grande tribulação, como nunca houve desde o princípio do mundo até agora, nem jamais haverá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23980, 'nvi', 'Mateus', 'mt', 24, 22, 'Se aqueles dias não fossem abreviados, ninguém sobreviveria; mas, por causa dos eleitos, aqueles dias serão abreviados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23981, 'nvi', 'Mateus', 'mt', 24, 23, 'Se, então, alguém lhes disser: ‘Vejam, aqui está o Cristo! ’ ou: ‘Ali está ele! ’, não acreditem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23982, 'nvi', 'Mateus', 'mt', 24, 24, 'Pois aparecerão falsos cristos e falsos profetas que realizarão grandes sinais e maravilhas para, se possível, enganar até os eleitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23983, 'nvi', 'Mateus', 'mt', 24, 25, 'Vejam que eu os avisei antecipadamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23984, 'nvi', 'Mateus', 'mt', 24, 26, '"Assim, se alguém lhes disser: ‘Ele está lá, no deserto! ’, não saiam; ou: ‘Ali está ele, dentro da casa! ’, não acreditem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23985, 'nvi', 'Mateus', 'mt', 24, 27, 'Porque assim como o relâmpago sai do Oriente e se mostra no Ocidente, assim será a vinda do Filho do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23986, 'nvi', 'Mateus', 'mt', 24, 28, 'Onde houver um cadáver, aí se ajuntarão os abutres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23987, 'nvi', 'Mateus', 'mt', 24, 29, '"Imediatamente após a tribulação daqueles dias ‘o sol escurecerá, e a lua não dará a sua luz; as estrelas cairão do céu, e os poderes celestes serão abalados’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23988, 'nvi', 'Mateus', 'mt', 24, 30, '"Então aparecerá no céu o sinal do Filho do homem, e todas as nações da terra se lamentarão e verão o Filho do homem vindo nas nuvens do céu com poder e grande glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23989, 'nvi', 'Mateus', 'mt', 24, 31, 'E ele enviará os seus anjos com grande som de trombeta, e estes reunirão os seus eleitos dos quatro ventos, de uma a outra extremidade dos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23990, 'nvi', 'Mateus', 'mt', 24, 32, '"Aprendam a lição da figueira: quando seus ramos se renovam e suas folhas começam a brotar, vocês sabem que o verão está próximo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23991, 'nvi', 'Mateus', 'mt', 24, 33, 'Assim também, quando virem todas estas coisas, saibam que ele está próximo, às portas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23992, 'nvi', 'Mateus', 'mt', 24, 34, 'Eu lhes asseguro que não passará esta geração até que todas essas coisas aconteçam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23993, 'nvi', 'Mateus', 'mt', 24, 35, 'O céu e a terra passarão, mas as minhas palavras jamais passarão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23994, 'nvi', 'Mateus', 'mt', 24, 36, '"Quanto ao dia e à hora ninguém sabe, nem os anjos dos céus, nem o Filho, senão somente o Pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23995, 'nvi', 'Mateus', 'mt', 24, 37, 'Como foi nos dias de Noé, assim também será na vinda do Filho do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23996, 'nvi', 'Mateus', 'mt', 24, 38, 'Pois nos dias anteriores ao dilúvio, o povo vivia comendo e bebendo, casando-se e dando-se em casamento, até o dia em que Noé entrou na arca;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23997, 'nvi', 'Mateus', 'mt', 24, 39, 'e eles nada perceberam, até que veio o dilúvio e os levou a todos. Assim acontecerá na vinda do Filho do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23998, 'nvi', 'Mateus', 'mt', 24, 40, 'Dois homens estarão no campo: um será levado e o outro deixado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23999, 'nvi', 'Mateus', 'mt', 24, 41, 'Duas mulheres estarão trabalhando num moinho: uma será levada e a outra deixada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24000, 'nvi', 'Mateus', 'mt', 24, 42, '"Portanto, vigiem, porque vocês não sabem em que dia virá o seu Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24001, 'nvi', 'Mateus', 'mt', 24, 43, 'Mas entendam isto: se o dono da casa soubesse a que hora da noite o ladrão viria, ele ficaria de guarda e não deixaria que a sua casa fosse arrombada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24002, 'nvi', 'Mateus', 'mt', 24, 44, 'Assim, também vocês precisam estar preparados, porque o Filho do homem virá numa hora em que vocês menos esperam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24003, 'nvi', 'Mateus', 'mt', 24, 45, '"Quem é, pois, o servo fiel e sensato, a quem seu senhor encarrega dos de sua casa para lhes dar alimento no tempo devido?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24004, 'nvi', 'Mateus', 'mt', 24, 46, 'Feliz o servo a quem seu senhor encontrar fazendo assim quando voltar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24005, 'nvi', 'Mateus', 'mt', 24, 47, 'Garanto-lhes que ele o encarregará de todos os seus bens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24006, 'nvi', 'Mateus', 'mt', 24, 48, 'Mas suponham que esse servo seja mau e diga a si mesmo: ‘Meu senhor se demora’,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24007, 'nvi', 'Mateus', 'mt', 24, 49, 'e então comece a bater em seus conservos e a comer e a beber com os beberrões.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24008, 'nvi', 'Mateus', 'mt', 24, 50, 'O senhor daquele servo virá num dia em que ele não o espera e numa hora que não sabe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24009, 'nvi', 'Mateus', 'mt', 24, 51, 'Ele o punirá severamente e lhe dará lugar com os hipócritas, onde haverá choro e ranger de dentes".');
