INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24010, 'nvi', 'Mateus', 'mt', 25, 1, '"O Reino dos céus, pois, será semelhante a dez virgens que pegaram suas candeias e saíram para encontrar-se com o noivo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24011, 'nvi', 'Mateus', 'mt', 25, 2, 'Cinco delas eram insensatas, e cinco eram prudentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24012, 'nvi', 'Mateus', 'mt', 25, 3, 'As insensatas pegaram suas candeias, mas não levaram óleo consigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24013, 'nvi', 'Mateus', 'mt', 25, 4, 'As prudentes, porém, levaram óleo em vasilhas juntamente com suas candeias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24014, 'nvi', 'Mateus', 'mt', 25, 5, 'O noivo demorou a chegar, e todas ficaram com sono e adormeceram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24015, 'nvi', 'Mateus', 'mt', 25, 6, '"À meia-noite, ouviu-se um grito: ‘O noivo se aproxima! Saiam para encontrá-lo! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24016, 'nvi', 'Mateus', 'mt', 25, 7, '"Então todas as virgens acordaram e prepararam suas candeias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24017, 'nvi', 'Mateus', 'mt', 25, 8, 'As insensatas disseram às prudentes: ‘Dêem-nos um pouco do seu óleo, pois as nossas candeias estão se apagando’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24018, 'nvi', 'Mateus', 'mt', 25, 9, '"Elas responderam: ‘Não, pois pode ser que não haja o suficiente para nós e para vocês. Vão comprar óleo para vocês’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24019, 'nvi', 'Mateus', 'mt', 25, 10, '"E saindo elas para comprar o óleo, chegou o noivo. As virgens que estavam preparadas entraram com ele para o banquete nupcial. E a porta foi fechada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24020, 'nvi', 'Mateus', 'mt', 25, 11, '"Mais tarde vieram também as outras e disseram: ‘Senhor! Senhor! Abra a porta para nós! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24021, 'nvi', 'Mateus', 'mt', 25, 12, '"Mas ele respondeu: ‘A verdade é que não as conheço! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24022, 'nvi', 'Mateus', 'mt', 25, 13, '"Portanto, vigiem, porque vocês não sabem o dia nem a hora! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24023, 'nvi', 'Mateus', 'mt', 25, 14, '"E também será como um homem que, ao sair de viagem, chamou seus servos e confiou-lhes os seus bens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24024, 'nvi', 'Mateus', 'mt', 25, 15, 'A um deu cinco talentos, a outro dois, e a outro um; a cada um de acordo com a sua capacidade. Em seguida partiu de viagem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24025, 'nvi', 'Mateus', 'mt', 25, 16, 'O que havia recebido cinco talentos saiu imediatamente, aplicou-os, e ganhou mais cinco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24026, 'nvi', 'Mateus', 'mt', 25, 17, 'Também o que tinha dois talentos ganhou mais dois.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24027, 'nvi', 'Mateus', 'mt', 25, 18, 'Mas o que tinha recebido um talento saiu, cavou um buraco no chão e escondeu o dinheiro do seu senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24028, 'nvi', 'Mateus', 'mt', 25, 19, '"Depois de muito tempo o senhor daqueles servos voltou e acertou contas com eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24029, 'nvi', 'Mateus', 'mt', 25, 20, 'O que tinha recebido cinco talentos trouxe os outros cinco e disse: ‘O senhor me confiou cinco talentos; veja, eu ganhei mais cinco’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24030, 'nvi', 'Mateus', 'mt', 25, 21, '"O senhor respondeu: ‘Muito bem, servo bom e fiel! Você foi fiel no pouco; eu o porei sobre o muito. Venha e participe da alegria do seu senhor! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24031, 'nvi', 'Mateus', 'mt', 25, 22, '"Veio também o que tinha recebido dois talentos e disse: ‘O senhor me confiou dois talentos; veja, eu ganhei mais dois’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24032, 'nvi', 'Mateus', 'mt', 25, 23, '"O senhor respondeu: ‘Muito bem, servo bom e fiel! Você foi fiel no pouco; eu o porei sobre o muito. Venha e participe da alegria do seu senhor! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24033, 'nvi', 'Mateus', 'mt', 25, 24, '"Por fim veio o que tinha recebido um talento e disse: ‘Eu sabia que o senhor é um homem severo, que colhe onde não plantou e junta onde não semeou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24034, 'nvi', 'Mateus', 'mt', 25, 25, 'Por isso, tive medo, saí e escondi o seu talento no chão. Veja, aqui está o que lhe pertence’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24035, 'nvi', 'Mateus', 'mt', 25, 26, '"O senhor respondeu: ‘Servo mau e negligente! Você sabia que eu colho onde não plantei e junto onde não semeei?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24036, 'nvi', 'Mateus', 'mt', 25, 27, 'Então você devia ter confiado o meu dinheiro aos banqueiros, para que, quando eu voltasse, o recebesse de volta com juros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24037, 'nvi', 'Mateus', 'mt', 25, 28, '" ‘Tirem o talento dele e entreguem-no ao que tem dez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24038, 'nvi', 'Mateus', 'mt', 25, 29, 'Pois a quem tem, mais será dado, e terá em grande quantidade. Mas a quem não tem, até o que tem lhe será tirado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24039, 'nvi', 'Mateus', 'mt', 25, 30, 'E lancem fora o servo inútil, nas trevas, onde haverá choro e ranger de dentes’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24040, 'nvi', 'Mateus', 'mt', 25, 31, '"Quando o Filho do homem vier em sua glória, com todos os anjos, assentar-se-á em seu trono na glória celestial.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24041, 'nvi', 'Mateus', 'mt', 25, 32, 'Todas as nações serão reunidas diante dele, e ele separará umas das outras como o pastor separa as ovelhas dos bodes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24042, 'nvi', 'Mateus', 'mt', 25, 33, 'E colocará as ovelhas à sua direita e os bodes à sua esquerda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24043, 'nvi', 'Mateus', 'mt', 25, 34, '"Então o Rei dirá aos que estiverem à sua direita: ‘Venham, benditos de meu Pai! Recebam como herança o Reino que lhes foi preparado desde a criação do mundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24044, 'nvi', 'Mateus', 'mt', 25, 35, 'Pois eu tive fome, e vocês me deram de comer; tive sede, e vocês me deram de beber; fui estrangeiro, e vocês me acolheram;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24045, 'nvi', 'Mateus', 'mt', 25, 36, 'necessitei de roupas, e vocês me vestiram; estive enfermo, e vocês cuidaram de mim; estive preso, e vocês me visitaram’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24046, 'nvi', 'Mateus', 'mt', 25, 37, '"Então os justos lhe responderão: ‘Senhor, quando te vimos com fome e te demos de comer, ou com sede e te demos de beber?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24047, 'nvi', 'Mateus', 'mt', 25, 38, 'Quando te vimos como estrangeiro e te acolhemos, ou necessitado de roupas e te vestimos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24048, 'nvi', 'Mateus', 'mt', 25, 39, 'Quando te vimos enfermo ou preso e fomos te visitar? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24049, 'nvi', 'Mateus', 'mt', 25, 40, '"O Rei responderá: ‘Digo-lhes a verdade: o que vocês fizeram a algum dos meus menores irmãos, a mim o fizeram’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24050, 'nvi', 'Mateus', 'mt', 25, 41, '"Então ele dirá aos que estiverem à sua esquerda: ‘Malditos, apartem-se de mim para o fogo eterno, preparado para o diabo e os seus anjos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24051, 'nvi', 'Mateus', 'mt', 25, 42, 'Pois eu tive fome, e vocês não me deram de comer; tive sede, e nada me deram para beber;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24052, 'nvi', 'Mateus', 'mt', 25, 43, 'fui estrangeiro, e vocês não me acolheram; necessitei de roupas, e vocês não me vestiram; estive enfermo e preso, e vocês não me visitaram’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24053, 'nvi', 'Mateus', 'mt', 25, 44, '"Eles também responderão: ‘Senhor, quando te vimos com fome ou com sede ou estrangeiro ou necessitado de roupas ou enfermo ou preso, e não te ajudamos? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24054, 'nvi', 'Mateus', 'mt', 25, 45, '"Ele responderá: ‘Digo-lhes a verdade: o que vocês deixaram de fazer a alguns destes mais pequeninos, também a mim deixaram de fazê-lo’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24055, 'nvi', 'Mateus', 'mt', 25, 46, '"E estes irão para o castigo eterno, mas os justos para a vida eterna".');
