INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24131, 'nvi', 'Mateus', 'mt', 27, 1, 'De manhã cedo, todos os chefes dos sacerdotes e líderes religiosos do povo tomaram a decisão de condenar Jesus à morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24132, 'nvi', 'Mateus', 'mt', 27, 2, 'E, amarrando-o, levaram-no e o entregaram a Pilatos, o governador.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24133, 'nvi', 'Mateus', 'mt', 27, 3, 'Quando Judas, que o havia traído, viu que Jesus fora condenado, foi tomado de remorso e devolveu aos chefes dos sacerdotes e aos líderes religiosos as trinta moedas de prata.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24134, 'nvi', 'Mateus', 'mt', 27, 4, 'E disse: "Pequei, pois traí sangue inocente". E eles retrucaram: "Que nos importa? A responsabilidade é sua".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24135, 'nvi', 'Mateus', 'mt', 27, 5, 'Então Judas jogou o dinheiro dentro do templo, saindo, foi e enforcou-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24136, 'nvi', 'Mateus', 'mt', 27, 6, 'Os chefes dos sacerdotes ajuntaram as moedas e disseram: "É contra a lei colocar este dinheiro no tesouro, visto que é preço de sangue".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24137, 'nvi', 'Mateus', 'mt', 27, 7, 'Então decidiram usar aquele dinheiro para comprar o campo do Oleiro, para cemitério de estrangeiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24138, 'nvi', 'Mateus', 'mt', 27, 8, 'Por isso ele se chama campo de Sangue até o dia de hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24139, 'nvi', 'Mateus', 'mt', 27, 9, 'Então se cumpriu o que fora dito pelo profeta Jeremias: "Tomaram as trinta moedas de prata, preço em que foi avaliado pelo povo de Israel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24140, 'nvi', 'Mateus', 'mt', 27, 10, 'e as usaram para comprar o campo do Oleiro, como o Senhor me ordenou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24141, 'nvi', 'Mateus', 'mt', 27, 11, 'Jesus foi posto diante do governador, e este lhe perguntou: "Você é o rei dos judeus? " Respondeu-lhe Jesus: "Tu o dizes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24142, 'nvi', 'Mateus', 'mt', 27, 12, 'Acusado pelos chefes dos sacerdotes e pelos líderes religiosos, ele nada respondeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24143, 'nvi', 'Mateus', 'mt', 27, 13, 'Então Pilatos lhe perguntou: "Você não ouve a acusação que eles estão fazendo contra você? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24144, 'nvi', 'Mateus', 'mt', 27, 14, 'Mas Jesus não lhe respondeu nenhuma palavra, de modo que o governador ficou muito impressionado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24145, 'nvi', 'Mateus', 'mt', 27, 15, 'Por ocasião da festa era costume do governador soltar um prisioneiro escolhido pela multidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24146, 'nvi', 'Mateus', 'mt', 27, 16, 'Eles tinham, naquela ocasião, um prisioneiro muito conhecido, chamado Barrabás.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24147, 'nvi', 'Mateus', 'mt', 27, 17, 'Pilatos perguntou à multidão que ali se havia reunido: "Qual destes vocês querem que lhes solte: Barrabás ou Jesus, chamado Cristo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24148, 'nvi', 'Mateus', 'mt', 27, 18, 'Porque sabia que o haviam entregado por inveja.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24149, 'nvi', 'Mateus', 'mt', 27, 19, 'Estando Pilatos sentado no tribunal, sua mulher lhe enviou esta mensagem: "Não se envolva com este inocente, porque hoje, em sonho, sofri muito por causa dele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24150, 'nvi', 'Mateus', 'mt', 27, 20, 'Mas os chefes dos sacerdotes e os líderes religiosos convenceram a multidão a que pedisse Barrabás e mandasse executar a Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24151, 'nvi', 'Mateus', 'mt', 27, 21, 'Então perguntou o governador: "Qual dos dois vocês querem que eu lhes solte? " Responderam eles: "Barrabás! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24152, 'nvi', 'Mateus', 'mt', 27, 22, 'Perguntou Pilatos: "Que farei então com Jesus, chamado Cristo? " Todos responderam: "Crucifica-o! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24153, 'nvi', 'Mateus', 'mt', 27, 23, '"Por quê? Que crime ele cometeu? ", perguntou Pilatos. Mas eles gritavam ainda mais: "Crucifica-o! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24154, 'nvi', 'Mateus', 'mt', 27, 24, 'Quando Pilatos percebeu que não estava obtendo nenhum resultado, mas, pelo contrário, estava se iniciando um tumulto, mandou trazer água, lavou as mãos diante da multidão e disse: "Estou inocente do sangue deste homem; a responsabilidade é de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24155, 'nvi', 'Mateus', 'mt', 27, 25, 'Todo o povo respondeu: "Que o sangue dele caia sobre nós e sobre nossos filhos! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24156, 'nvi', 'Mateus', 'mt', 27, 26, 'Então Pilatos soltou-lhes Barrabás, mandou açoitar Jesus e o entregou para ser crucificado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24157, 'nvi', 'Mateus', 'mt', 27, 27, 'Então, os soldados do governador levaram Jesus ao Pretório e reuniram toda a tropa ao seu redor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24158, 'nvi', 'Mateus', 'mt', 27, 28, 'Tiraram-lhe as vestes e puseram nele um manto vermelho;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24159, 'nvi', 'Mateus', 'mt', 27, 29, 'fizeram uma coroa de espinhos e a colocaram em sua cabeça. Puseram uma vara em sua mão direita e, ajoelhando-se diante dele, zombavam: "Salve, rei dos judeus! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24160, 'nvi', 'Mateus', 'mt', 27, 30, 'Cuspiram nele e, tirando-lhe a vara, batiam-lhe com ela na cabeça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24161, 'nvi', 'Mateus', 'mt', 27, 31, 'Depois de terem zombado dele, tiraram-lhe o manto e vestiram-lhe suas próprias roupas. Então o levaram para crucificá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24162, 'nvi', 'Mateus', 'mt', 27, 32, 'Ao saírem, encontraram um homem de Cirene, chamado Simão, e o forçaram a carregar a cruz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24163, 'nvi', 'Mateus', 'mt', 27, 33, 'Chegaram a um lugar chamado Gólgota, que quer dizer Lugar da Caveira,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24164, 'nvi', 'Mateus', 'mt', 27, 34, 'e lhe deram para beber vinho misturado com fel; mas, depois de prová-lo, recusou-se a beber.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24165, 'nvi', 'Mateus', 'mt', 27, 35, 'Depois de o crucificarem, dividiram as roupas dele, tirando sortes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24166, 'nvi', 'Mateus', 'mt', 27, 36, 'E, sentando-se, vigiavam-no ali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24167, 'nvi', 'Mateus', 'mt', 27, 37, 'Por cima de sua cabeça colocaram por escrito a acusação feita contra ele: ESTE É JESUS, O REI DOS JUDEUS.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24168, 'nvi', 'Mateus', 'mt', 27, 38, 'Dois ladrões foram crucificados com ele, um à sua direita e outro à sua esquerda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24169, 'nvi', 'Mateus', 'mt', 27, 39, 'Os que passavam lançavam-lhe insultos, balançando a cabeça');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24170, 'nvi', 'Mateus', 'mt', 27, 40, 'e dizendo: "Você que destrói o templo e o reedifica em três dias, salve-se! Desça da cruz, se é Filho de Deus! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24171, 'nvi', 'Mateus', 'mt', 27, 41, 'Da mesma forma, os chefes dos sacerdotes, os mestres da lei e os líderes religiosos zombavam dele,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24172, 'nvi', 'Mateus', 'mt', 27, 42, 'dizendo: "Salvou os outros, mas não é capaz de salvar a si mesmo! E é o rei de Israel! Desça agora da cruz, e creremos nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24173, 'nvi', 'Mateus', 'mt', 27, 43, 'Ele confiou em Deus. Que Deus o salve agora, se dele tem compaixão, pois disse: ‘Sou o Filho de Deus! ’ "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24174, 'nvi', 'Mateus', 'mt', 27, 44, 'Igualmente o insultavam os ladrões que haviam sido crucificados com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24175, 'nvi', 'Mateus', 'mt', 27, 45, 'E houve trevas sobre toda a terra, do meio dia às três horas da tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24176, 'nvi', 'Mateus', 'mt', 27, 46, 'Por volta das três horas da tarde, Jesus bradou em alta voz: "Eloí, Eloí, lamá sabactâni? " que significa: "Meu Deus! Meu Deus! Por que me abandonaste? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24177, 'nvi', 'Mateus', 'mt', 27, 47, 'Quando alguns dos que estavam ali ouviram isso, disseram: "Ele está chamando Elias".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24178, 'nvi', 'Mateus', 'mt', 27, 48, 'Imediatamente, um deles correu em busca de uma esponja, embebeu-a em vinagre, colocou-a na ponta de uma vara e deu-a a Jesus para beber.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24179, 'nvi', 'Mateus', 'mt', 27, 49, 'Mas os outros disseram: "Deixem-no. Vejamos se Elias vem salvá-lo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24180, 'nvi', 'Mateus', 'mt', 27, 50, 'Depois de ter bradado novamente em alta voz, Jesus entregou o espírito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24181, 'nvi', 'Mateus', 'mt', 27, 51, 'Naquele momento, o véu do santuário rasgou-se em duas partes, de alto a baixo. A terra tremeu, e as rochas se partiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24182, 'nvi', 'Mateus', 'mt', 27, 52, 'Os sepulcros se abriram, e os corpos de muitos santos que tinham morrido foram ressuscitados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24183, 'nvi', 'Mateus', 'mt', 27, 53, 'E, saindo dos sepulcros, depois da ressurreição de Jesus, entraram na cidade santa e apareceram a muitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24184, 'nvi', 'Mateus', 'mt', 27, 54, 'Quando o centurião e os que com ele vigiavam Jesus viram o terremoto e tudo o que havia acontecido, ficaram aterrorizados e exclamaram: "Verdadeiramente este era o Filho de Deus! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24185, 'nvi', 'Mateus', 'mt', 27, 55, 'Muitas mulheres estavam ali, observando de longe. Elas haviam seguido Jesus desde a Galiléia, para o servir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24186, 'nvi', 'Mateus', 'mt', 27, 56, 'Entre elas estavam Maria Madalena; Maria, mãe de Tiago e de José; e a mãe dos filhos de Zebedeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24187, 'nvi', 'Mateus', 'mt', 27, 57, 'Ao cair da tarde chegou um homem rico, de Arimatéia, chamado José, que se tornara discípulo de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24188, 'nvi', 'Mateus', 'mt', 27, 58, 'Dirigindo-se a Pilatos, pediu o corpo de Jesus, e Pilatos ordenou que lhe fosse entregue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24189, 'nvi', 'Mateus', 'mt', 27, 59, 'José tomou o corpo, envolveu-o num limpo lençol de linho');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24190, 'nvi', 'Mateus', 'mt', 27, 60, 'e o colocou num sepulcro novo, que ele havia mandado cavar na rocha. E, fazendo rolar uma grande pedra sobre a entrada do sepulcro, retirou-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24191, 'nvi', 'Mateus', 'mt', 27, 61, 'Maria Madalena e a outra Maria estavam assentadas ali, em frente do sepulcro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24192, 'nvi', 'Mateus', 'mt', 27, 62, 'No outro dia, que era o seguinte ao da Preparação, os chefes dos sacerdotes e os fariseus dirigiram-se a Pilatos');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24193, 'nvi', 'Mateus', 'mt', 27, 63, 'e disseram: "Senhor, lembramos que, enquanto ainda estava vivo, aquele impostor disse: ‘Depois de três dias ressuscitarei’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24194, 'nvi', 'Mateus', 'mt', 27, 64, 'Ordena, pois, que o sepulcro dele seja guardado até o terceiro dia, para que não venham seus discípulos e, roubando o corpo, digam ao povo que ele ressuscitou dentre os mortos. Este último engano será pior do que o primeiro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24195, 'nvi', 'Mateus', 'mt', 27, 65, '"Levem um destacamento", respondeu Pilatos. "Podem ir, e mantenham o sepulcro em segurança como acharem melhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24196, 'nvi', 'Mateus', 'mt', 27, 66, 'Eles foram e armaram um esquema de segurança no sepulcro; e além de deixarem um destacamento montando guarda, lacraram a pedra.');
