INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24197, 'nvi', 'Mateus', 'mt', 28, 1, 'Depois do sábado, tendo começado o primeiro dia da semana, Maria Madalena e a outra Maria foram ver o sepulcro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24198, 'nvi', 'Mateus', 'mt', 28, 2, 'E eis que sobreveio um grande terremoto, pois um anjo do Senhor desceu do céu e, chegando ao sepulcro, rolou a pedra da entrada e assentou-se sobre ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24199, 'nvi', 'Mateus', 'mt', 28, 3, 'Sua aparência era como um relâmpago, e suas vestes eram brancas como a neve.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24200, 'nvi', 'Mateus', 'mt', 28, 4, 'Os guardas tremeram de medo e ficaram como mortos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24201, 'nvi', 'Mateus', 'mt', 28, 5, 'O anjo disse às mulheres: "Não tenham medo! Sei que vocês estão procurando Jesus, que foi crucificado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24202, 'nvi', 'Mateus', 'mt', 28, 6, 'Ele não está aqui; ressuscitou, como tinha dito. Venham ver o lugar onde ele jazia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24203, 'nvi', 'Mateus', 'mt', 28, 7, 'Vão depressa e digam aos discípulos dele: ‘Ele ressuscitou dentre os mortos e está indo adiante de vocês para a Galiléia. Lá vocês o verão’. Notem que eu já os avisei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24204, 'nvi', 'Mateus', 'mt', 28, 8, 'As mulheres saíram depressa do sepulcro, amedrontadas e cheias de alegria, e foram correndo anunciá-lo aos discípulos de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24205, 'nvi', 'Mateus', 'mt', 28, 9, 'De repente, Jesus as encontrou e disse: "Salve! " Elas se aproximaram dele, abraçaram-lhe os pés e o adoraram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24206, 'nvi', 'Mateus', 'mt', 28, 10, 'Então Jesus lhes disse: "Não tenham medo. Vão dizer a meus irmãos que se dirijam para a Galiléia; lá eles me verão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24207, 'nvi', 'Mateus', 'mt', 28, 11, 'Enquanto as mulheres estavam a caminho, alguns dos guardas dirigiram-se à cidade e contaram aos chefes dos sacerdotes tudo o que havia acontecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24208, 'nvi', 'Mateus', 'mt', 28, 12, 'Quando os chefes dos sacerdotes se reuniram com os líderes religiosos, elaboraram um plano. Deram aos soldados grande soma de dinheiro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24209, 'nvi', 'Mateus', 'mt', 28, 13, 'dizendo-lhes: "Vocês devem declarar o seguinte: ‘Os discípulos dele vieram durante a noite e furtaram o corpo, enquanto estávamos dormindo’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24210, 'nvi', 'Mateus', 'mt', 28, 14, 'Se isso chegar aos ouvidos do governador, nós lhe daremos explicações e livraremos vocês de qualquer problema".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24211, 'nvi', 'Mateus', 'mt', 28, 15, 'Assim, os soldados receberam o dinheiro e fizeram como tinham sido instruídos. E esta versão se divulgou entre os judeus até o dia de hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24212, 'nvi', 'Mateus', 'mt', 28, 16, 'Os onze discípulos foram para a Galiléia, para o monte que Jesus lhes indicara.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24213, 'nvi', 'Mateus', 'mt', 28, 17, 'Quando o viram o adoraram; mas alguns duvidaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24214, 'nvi', 'Mateus', 'mt', 28, 18, 'Então, Jesus aproximou-se deles e disse: "Foi-me dada toda a autoridade no céu e na terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24215, 'nvi', 'Mateus', 'mt', 28, 19, 'Portanto, vão e façam discípulos de todas as nações, batizando-os em nome do Pai e do Filho e do Espírito Santo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24216, 'nvi', 'Mateus', 'mt', 28, 20, 'ensinando-os a obedecer a tudo o que eu lhes ordenei. E eu estarei sempre com vocês, até o fim dos tempos".');
