INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23211, 'nvi', 'Mateus', 'mt', 4, 1, 'Então Jesus foi levado pelo Espírito ao deserto, para ser tentado pelo diabo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23212, 'nvi', 'Mateus', 'mt', 4, 2, 'Depois de jejuar quarenta dias e quarenta noites, teve fome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23213, 'nvi', 'Mateus', 'mt', 4, 3, 'O tentador aproximou-se dele e disse: "Se você é o Filho de Deus, mande que estas pedras se transformem em pães".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23214, 'nvi', 'Mateus', 'mt', 4, 4, 'Jesus respondeu: "Está escrito: ‘Nem só de pão viverá o homem, mas de toda palavra que procede da boca de Deus’".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23215, 'nvi', 'Mateus', 'mt', 4, 5, 'Então o diabo o levou à cidade santa, colocou-o na parte mais alta do templo e lhe disse:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23216, 'nvi', 'Mateus', 'mt', 4, 6, '"Se você é o Filho de Deus, jogue-se daqui para baixo. Pois está escrito: ‘Ele dará ordens a seus anjos a seu respeito, e com as mãos eles o segurarão, para que você não tropece em alguma pedra’".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23217, 'nvi', 'Mateus', 'mt', 4, 7, 'Jesus lhe respondeu: "Também está escrito: ‘Não ponha à prova o Senhor, o seu Deus’".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23218, 'nvi', 'Mateus', 'mt', 4, 8, 'Depois, o diabo o levou a um monte muito alto e mostrou-lhe todos os reinos do mundo e o seu esplendor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23219, 'nvi', 'Mateus', 'mt', 4, 9, 'E lhe disse: "Tudo isto lhe darei, se você se prostrar e me adorar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23220, 'nvi', 'Mateus', 'mt', 4, 10, 'Jesus lhe disse: "Retire-se, Satanás! Pois está escrito: ‘Adore o Senhor, o seu Deus e só a ele preste culto’".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23221, 'nvi', 'Mateus', 'mt', 4, 11, 'Então o diabo o deixou, e anjos vieram e o serviram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23222, 'nvi', 'Mateus', 'mt', 4, 12, 'Quando Jesus ouviu que João tinha sido preso, voltou para a Galiléia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23223, 'nvi', 'Mateus', 'mt', 4, 13, 'Saindo de Nazaré, foi viver em Cafarnaum, que ficava junto ao mar, na região de Zebulom e Naftali,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23224, 'nvi', 'Mateus', 'mt', 4, 14, 'para cumprir o que fora dito pelo profeta Isaías:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23225, 'nvi', 'Mateus', 'mt', 4, 15, '"Terra de Zebulom e terra de Naftali, caminho do mar, além do Jordão, Galiléia dos gentios;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23226, 'nvi', 'Mateus', 'mt', 4, 16, 'o povo que vivia nas trevas viu uma grande luz; sobre os que viviam na terra da sombra da morte raiou uma luz".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23227, 'nvi', 'Mateus', 'mt', 4, 17, 'Daí em diante Jesus começou a pregar: "Arrependam-se, pois o Reino dos céus está próximo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23228, 'nvi', 'Mateus', 'mt', 4, 18, 'Andando à beira do mar da Galiléia, Jesus viu dois irmãos: Simão, chamado Pedro, e seu irmão André. Eles estavam lançando redes ao mar, pois eram pescadores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23229, 'nvi', 'Mateus', 'mt', 4, 19, 'E disse Jesus: "Sigam-me, e eu os farei pescadores de homens".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23230, 'nvi', 'Mateus', 'mt', 4, 20, 'No mesmo instante eles deixaram as suas redes e o seguiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23231, 'nvi', 'Mateus', 'mt', 4, 21, 'Indo adiante, viu outros dois irmãos: Tiago, filho de Zebedeu, e João, seu irmão. Eles estavam num barco com seu pai, Zebedeu, preparando as suas redes. Jesus os chamou,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23232, 'nvi', 'Mateus', 'mt', 4, 22, 'e eles, deixando imediatamente o barco e seu pai, o seguiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23233, 'nvi', 'Mateus', 'mt', 4, 23, 'Jesus foi por toda a Galiléia, ensinando nas sinagogas deles, pregando as boas novas do Reino e curando todas as enfermidades e doenças entre o povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23234, 'nvi', 'Mateus', 'mt', 4, 24, 'Notícias sobre ele se espalharam por toda a Síria, e o povo lhe trouxe todos os que estavam padecendo vários males e tormentos: endemoninhados, epiléticos e paralíticos; e ele os curou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23235, 'nvi', 'Mateus', 'mt', 4, 25, 'Grandes multidões o seguiam, vindas da Galiléia, Decápolis, Jerusalém, Judéia e da região do outro lado do Jordão.');
