INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23236, 'nvi', 'Mateus', 'mt', 5, 1, 'Vendo as multidões, Jesus subiu ao monte e se assentou. Seus discípulos aproximaram-se dele,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23237, 'nvi', 'Mateus', 'mt', 5, 2, 'e ele começou a ensiná-los, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23238, 'nvi', 'Mateus', 'mt', 5, 3, '"Bem-aventurados os pobres em espírito, pois deles é o Reino dos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23239, 'nvi', 'Mateus', 'mt', 5, 4, 'Bem-aventurados os que choram, pois serão consolados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23240, 'nvi', 'Mateus', 'mt', 5, 5, 'Bem-aventurados os humildes, pois eles receberão a terra por herança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23241, 'nvi', 'Mateus', 'mt', 5, 6, 'Bem-aventurados os que têm fome e sede de justiça, pois serão satisfeitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23242, 'nvi', 'Mateus', 'mt', 5, 7, 'Bem-aventurados os misericordiosos, pois obterão misericórdia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23243, 'nvi', 'Mateus', 'mt', 5, 8, 'Bem-aventurados os puros de coração, pois verão a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23244, 'nvi', 'Mateus', 'mt', 5, 9, 'Bem-aventurados os pacificadores, pois serão chamados filhos de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23245, 'nvi', 'Mateus', 'mt', 5, 10, 'Bem-aventurados os perseguidos por causa da justiça, pois deles é o Reino dos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23246, 'nvi', 'Mateus', 'mt', 5, 11, '"Bem-aventurados serão vocês quando, por minha causa os insultarem, perseguirem e levantarem todo tipo de calúnia contra vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23247, 'nvi', 'Mateus', 'mt', 5, 12, 'Alegrem-se e regozijem-se, porque grande é a recompensa de vocês nos céus, pois da mesma forma perseguiram os profetas que viveram antes de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23248, 'nvi', 'Mateus', 'mt', 5, 13, '"Vocês são o sal da terra. Mas se o sal perder o seu sabor, como restaurá-lo? Não servirá para nada, exceto para ser jogado fora e pisado pelos homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23249, 'nvi', 'Mateus', 'mt', 5, 14, '"Vocês são a luz do mundo. Não se pode esconder uma cidade construída sobre um monte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23250, 'nvi', 'Mateus', 'mt', 5, 15, 'E, também, ninguém acende uma candeia e a coloca debaixo de uma vasilha. Pelo contrário, coloca-a no lugar apropriado, e assim ilumina a todos os que estão na casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23251, 'nvi', 'Mateus', 'mt', 5, 16, 'Assim brilhe a luz de vocês diante dos homens, para que vejam as suas boas obras e glorifiquem ao Pai de vocês, que está nos céus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23252, 'nvi', 'Mateus', 'mt', 5, 17, '"Não pensem que vim abolir a Lei ou os Profetas; não vim abolir, mas cumprir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23253, 'nvi', 'Mateus', 'mt', 5, 18, 'Digo-lhes a verdade: Enquanto existirem céus e terra, de forma alguma desaparecerá da Lei a menor letra ou o menor traço, até que tudo se cumpra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23254, 'nvi', 'Mateus', 'mt', 5, 19, 'Todo aquele que desobedecer a um desses mandamentos, ainda que dos menores, e ensinar os outros a fazerem o mesmo, será chamado menor no Reino dos céus; mas todo aquele que praticar e ensinar estes mandamentos será chamado grande no Reino dos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23255, 'nvi', 'Mateus', 'mt', 5, 20, 'Pois eu lhes digo que se a justiça de vocês não for muito superior à dos fariseus e mestres da lei, de modo nenhum entrarão no Reino dos céus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23256, 'nvi', 'Mateus', 'mt', 5, 21, '"Vocês ouviram o que foi dito aos seus antepassados: ‘Não matarás’, e ‘quem matar estará sujeito a julgamento’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23257, 'nvi', 'Mateus', 'mt', 5, 22, 'Mas eu lhes digo que qualquer que se irar contra seu irmão estará sujeito a julgamento. Também, qualquer que disser a seu irmão: ‘Racá’, será levado ao tribunal. E qualquer que disser: ‘Louco! ’, corre o risco de ir para o fogo do inferno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23258, 'nvi', 'Mateus', 'mt', 5, 23, '"Portanto, se você estiver apresentando sua oferta diante do altar e ali se lembrar de que seu irmão tem algo contra você,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23259, 'nvi', 'Mateus', 'mt', 5, 24, 'deixe sua oferta ali, diante do altar, e vá primeiro reconciliar-se com seu irmão; depois volte e apresente sua oferta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23260, 'nvi', 'Mateus', 'mt', 5, 25, '"Entre em acordo depressa com seu adversário que pretende levá-lo ao tribunal. Faça isso enquanto ainda estiver com ele a caminho, pois, caso contrário, ele poderá entregá-lo ao juiz, e o juiz ao guarda, e você poderá ser jogado na prisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23261, 'nvi', 'Mateus', 'mt', 5, 26, 'Eu lhe garanto que você não sairá de lá enquanto não pagar o último centavo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23262, 'nvi', 'Mateus', 'mt', 5, 27, '"Vocês ouviram o que foi dito: ‘Não adulterarás’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23263, 'nvi', 'Mateus', 'mt', 5, 28, 'Mas eu lhes digo: qualquer que olhar para uma mulher para desejá-la, já cometeu adultério com ela no seu coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23264, 'nvi', 'Mateus', 'mt', 5, 29, 'Se o seu olho direito o fizer pecar, arranque-o e lance-o fora. É melhor perder uma parte do seu corpo do que ser todo ele lançado no inferno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23265, 'nvi', 'Mateus', 'mt', 5, 30, 'E se a sua mão direita o fizer pecar, corte-a e lance-a fora. É melhor perder uma parte do seu corpo do que ir todo ele para o inferno".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23266, 'nvi', 'Mateus', 'mt', 5, 31, '"Foi dito: ‘Aquele que se divorciar de sua mulher deverá dar-lhe certidão de divórcio’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23267, 'nvi', 'Mateus', 'mt', 5, 32, 'Mas eu lhes digo que todo aquele que se divorciar de sua mulher, exceto por imoralidade sexual, faz que ela se torne adúltera, e quem se casar com a mulher divorciada estará cometendo adultério".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23268, 'nvi', 'Mateus', 'mt', 5, 33, '"Vocês também ouviram o que foi dito aos seus antepassados: ‘Não jure falsamente, mas cumpra os juramentos que você fez diante do Senhor’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23269, 'nvi', 'Mateus', 'mt', 5, 34, 'Mas eu lhes digo: Não jurem de forma alguma: nem pelo céu, porque é o trono de Deus;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23270, 'nvi', 'Mateus', 'mt', 5, 35, 'nem pela terra, porque é o estrado de seus pés; nem por Jerusalém, porque é a cidade do grande Rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23271, 'nvi', 'Mateus', 'mt', 5, 36, 'E não jure pela sua cabeça, pois você não pode tornar branco ou preto nem um fio de cabelo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23272, 'nvi', 'Mateus', 'mt', 5, 37, 'Seja o seu ‘sim’, ‘sim’, e o seu ‘não’, ‘não’; o que passar disso vem do Maligno".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23273, 'nvi', 'Mateus', 'mt', 5, 38, '"Vocês ouviram o que foi dito: ‘Olho por olho e dente por dente’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23274, 'nvi', 'Mateus', 'mt', 5, 39, 'Mas eu lhes digo: Não resistam ao perverso. Se alguém o ferir na face direita, ofereça-lhe também a outra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23275, 'nvi', 'Mateus', 'mt', 5, 40, 'E se alguém quiser processá-lo e tirar-lhe a túnica, deixe que leve também a capa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23276, 'nvi', 'Mateus', 'mt', 5, 41, 'Se alguém o forçar a caminhar com ele uma milha, vá com ele duas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23277, 'nvi', 'Mateus', 'mt', 5, 42, 'Dê a quem lhe pede, e não volte as costas àquele que deseja pedir-lhe algo emprestado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23278, 'nvi', 'Mateus', 'mt', 5, 43, '"Vocês ouviram o que foi dito: ‘Ame o seu próximo e odeie o seu inimigo’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23279, 'nvi', 'Mateus', 'mt', 5, 44, 'Mas eu lhes digo: Amem os seus inimigos e orem por aqueles que os perseguem,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23280, 'nvi', 'Mateus', 'mt', 5, 45, 'para que vocês venham a ser filhos de seu Pai que está nos céus. Porque ele faz raiar o seu sol sobre maus e bons e derrama chuva sobre justos e injustos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23281, 'nvi', 'Mateus', 'mt', 5, 46, 'Se vocês amarem aqueles que os amam, que recompensa receberão? Até os publicanos fazem isso!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23282, 'nvi', 'Mateus', 'mt', 5, 47, 'E se vocês saudarem apenas os seus irmãos, o que estarão fazendo de mais? Até os pagãos fazem isso!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23283, 'nvi', 'Mateus', 'mt', 5, 48, 'Portanto, sejam perfeitos como perfeito é o Pai celestial de vocês".');
