INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23284, 'nvi', 'Mateus', 'mt', 6, 1, '"Tenham o cuidado de não praticar suas ‘obras de justiça’ diante dos outros para serem vistos por eles. Se fizerem isso, vocês não terão nenhuma recompensa do Pai celestial.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23285, 'nvi', 'Mateus', 'mt', 6, 2, '"Portanto, quando você der esmola, não anuncie isso com trombetas, como fazem os hipócritas nas sinagogas e nas ruas, a fim de serem honrados pelos outros. Eu lhes garanto que eles já receberam sua plena recompensa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23286, 'nvi', 'Mateus', 'mt', 6, 3, 'Mas quando você der esmola, que a sua mão esquerda não saiba o que está fazendo a direita,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23287, 'nvi', 'Mateus', 'mt', 6, 4, 'de forma que você preste a sua ajuda em segredo. E seu Pai, que vê o que é feito em segredo, o recompensará".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23288, 'nvi', 'Mateus', 'mt', 6, 5, '"E quando vocês orarem, não sejam como os hipócritas. Eles gostam de ficar orando em pé nas sinagogas e nas esquinas, a fim de serem vistos pelos outros. Eu lhes asseguro que eles já receberam sua plena recompensa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23289, 'nvi', 'Mateus', 'mt', 6, 6, 'Mas quando você orar, vá para seu quarto, feche a porta e ore a seu Pai, que está no secreto. Então seu Pai, que vê no secreto, o recompensará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23290, 'nvi', 'Mateus', 'mt', 6, 7, 'E quando orarem, não fiquem sempre repetindo a mesma coisa, como fazem os pagãos. Eles pensam que por muito falarem serão ouvidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23291, 'nvi', 'Mateus', 'mt', 6, 8, 'Não sejam iguais a eles, porque o seu Pai sabe do que vocês precisam, antes mesmo de o pedirem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23292, 'nvi', 'Mateus', 'mt', 6, 9, 'Vocês, orem assim: ‘Pai nosso, que estás nos céus! Santificado seja o teu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23293, 'nvi', 'Mateus', 'mt', 6, 10, 'Venha o teu Reino; seja feita a tua vontade, assim na terra como no céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23294, 'nvi', 'Mateus', 'mt', 6, 11, 'Dá-nos hoje o nosso pão de cada dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23295, 'nvi', 'Mateus', 'mt', 6, 12, 'Perdoa as nossas dívidas, assim como perdoamos aos nossos devedores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23296, 'nvi', 'Mateus', 'mt', 6, 13, 'E não nos deixes cair em tentação, mas livra-nos do mal, porque teu é o Reino, o poder e a glória para sempre. Amém’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23297, 'nvi', 'Mateus', 'mt', 6, 14, 'Pois se perdoarem as ofensas uns dos outros, o Pai celestial também lhes perdoará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23298, 'nvi', 'Mateus', 'mt', 6, 15, 'Mas se não perdoarem uns aos outros, o Pai celestial não lhes perdoará as ofensas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23299, 'nvi', 'Mateus', 'mt', 6, 16, '"Quando jejuarem, não mostrem uma aparência triste como os hipócritas, pois eles mudam a aparência do rosto a fim de que os homens vejam que eles estão jejuando. Eu lhes digo verdadeiramente que eles já receberam sua plena recompensa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23300, 'nvi', 'Mateus', 'mt', 6, 17, 'Ao jejuar, ponha óleo sobre a cabeça e lave o rosto,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23301, 'nvi', 'Mateus', 'mt', 6, 18, 'para que não pareça aos outros que você está jejuando, mas apenas a seu Pai, que vê no secreto. E seu Pai, que vê no secreto, o recompensará".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23302, 'nvi', 'Mateus', 'mt', 6, 19, '"Não acumulem para vocês tesouros na terra, onde a traça e a ferrugem destroem, e onde os ladrões arrombam e furtam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23303, 'nvi', 'Mateus', 'mt', 6, 20, 'Mas acumulem para vocês tesouros no céu, onde a traça e a ferrugem não destroem, e onde os ladrões não arrombam nem furtam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23304, 'nvi', 'Mateus', 'mt', 6, 21, 'Pois onde estiver o seu tesouro, aí também estará o seu coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23305, 'nvi', 'Mateus', 'mt', 6, 22, '"Os olhos são a candeia do corpo. Se os seus olhos forem bons, todo o seu corpo será cheio de luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23306, 'nvi', 'Mateus', 'mt', 6, 23, 'Mas se os seus olhos forem maus, todo o seu corpo será cheio de trevas. Portanto, se a luz que está dentro de você são trevas, que tremendas trevas são!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23307, 'nvi', 'Mateus', 'mt', 6, 24, '"Ninguém pode servir a dois senhores; pois odiará a um e amará o outro, ou se dedicará a um e desprezará o outro. Vocês não podem servir a Deus e ao Dinheiro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23308, 'nvi', 'Mateus', 'mt', 6, 25, '"Portanto eu lhes digo: não se preocupem com suas próprias vidas, quanto ao que comer ou beber; nem com seus próprios corpos, quanto ao que vestir. Não é a vida mais importante do que a comida, e o corpo mais importante do que a roupa?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23309, 'nvi', 'Mateus', 'mt', 6, 26, 'Observem as aves do céu: não semeiam nem colhem nem armazenam em celeiros; contudo, o Pai celestial as alimenta. Não têm vocês muito mais valor do que elas?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23310, 'nvi', 'Mateus', 'mt', 6, 27, 'Quem de vocês, por mais que se preocupe, pode acrescentar uma hora que seja à sua vida?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23311, 'nvi', 'Mateus', 'mt', 6, 28, '"Por que vocês se preocupam com roupas? Vejam como crescem os lírios do campo. Eles não trabalham nem tecem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23312, 'nvi', 'Mateus', 'mt', 6, 29, 'Contudo, eu lhes digo que nem Salomão, em todo o seu esplendor, vestiu-se como um deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23313, 'nvi', 'Mateus', 'mt', 6, 30, 'Se Deus veste assim a erva do campo, que hoje existe e amanhã é lançada ao fogo, não vestirá muito mais a vocês, homens de pequena fé?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23314, 'nvi', 'Mateus', 'mt', 6, 31, 'Portanto, não se preocupem, dizendo: ‘Que vamos comer? ’ ou ‘que vamos beber? ’ ou ‘que vamos vestir? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23315, 'nvi', 'Mateus', 'mt', 6, 32, 'Pois os pagãos é que correm atrás dessas coisas; mas o Pai celestial sabe que vocês precisam delas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23316, 'nvi', 'Mateus', 'mt', 6, 33, 'Busquem, pois, em primeiro lugar o Reino de Deus e a sua justiça, e todas essas coisas lhes serão acrescentadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23317, 'nvi', 'Mateus', 'mt', 6, 34, 'Portanto, não se preocupem com o amanhã, pois o amanhã se preocupará consigo mesmo. Basta a cada dia o seu próprio mal".');
