INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23318, 'nvi', 'Mateus', 'mt', 7, 1, '"Não julguem, para que vocês não sejam julgados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23319, 'nvi', 'Mateus', 'mt', 7, 2, 'Pois da mesma forma que julgarem, vocês serão julgados; e a medida que usarem, também será usada para medir vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23320, 'nvi', 'Mateus', 'mt', 7, 3, '"Por que você repara no cisco que está no olho do seu irmão, e não se dá conta da viga que está em seu próprio olho?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23321, 'nvi', 'Mateus', 'mt', 7, 4, 'Como você pode dizer ao seu irmão: ‘Deixe-me tirar o cisco do seu olho’, quando há uma viga no seu?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23322, 'nvi', 'Mateus', 'mt', 7, 5, 'Hipócrita, tire primeiro a viga do seu olho, e então você verá claramente para tirar o cisco do olho do seu irmão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23323, 'nvi', 'Mateus', 'mt', 7, 6, '"Não dêem o que é sagrado aos cães, nem atirem suas pérolas aos porcos; caso contrário, estes as pisarão e, aqueles, voltando-se contra vocês, os despedaçarão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23324, 'nvi', 'Mateus', 'mt', 7, 7, '"Peçam, e lhes será dado; busquem, e encontrarão; batam, e a porta lhes será aberta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23325, 'nvi', 'Mateus', 'mt', 7, 8, 'Pois todo o que pede, recebe; o que busca, encontra; e àquele que bate, a porta será aberta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23326, 'nvi', 'Mateus', 'mt', 7, 9, '"Qual de vocês, se seu filho pedir pão, lhe dará uma pedra?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23327, 'nvi', 'Mateus', 'mt', 7, 10, 'Ou se pedir peixe, lhe dará uma cobra?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23328, 'nvi', 'Mateus', 'mt', 7, 11, 'Se vocês, apesar de serem maus, sabem dar boas coisas aos seus filhos, quanto mais o Pai de vocês, que está nos céus, dará coisas boas aos que lhe pedirem!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23329, 'nvi', 'Mateus', 'mt', 7, 12, 'Assim, em tudo, façam aos outros o que vocês querem que eles lhes façam; pois esta é a Lei e os Profetas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23330, 'nvi', 'Mateus', 'mt', 7, 13, '"Entrem pela porta estreita, pois larga é a porta e amplo o caminho que leva à perdição, e são muitos os que entram por ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23331, 'nvi', 'Mateus', 'mt', 7, 14, 'Como é estreita a porta, e apertado o caminho que leva à vida! São poucos os que a encontram".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23332, 'nvi', 'Mateus', 'mt', 7, 15, '"Cuidado com os falsos profetas. Eles vêm a vocês vestidos de peles de ovelhas, mas por dentro são lobos devoradores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23333, 'nvi', 'Mateus', 'mt', 7, 16, 'Vocês os reconhecerão por seus frutos. Pode alguém colher uvas de um espinheiro ou figos de ervas daninhas?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23334, 'nvi', 'Mateus', 'mt', 7, 17, 'Semelhantemente, toda árvore boa dá frutos bons, mas a árvore ruim dá frutos ruins.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23335, 'nvi', 'Mateus', 'mt', 7, 18, 'A árvore boa não pode dar frutos ruins, nem a árvore ruim pode dar frutos bons.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23336, 'nvi', 'Mateus', 'mt', 7, 19, 'Toda árvore que não produz bons frutos é cortada e lançada ao fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23337, 'nvi', 'Mateus', 'mt', 7, 20, 'Assim, pelos seus frutos vocês os reconhecerão!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23338, 'nvi', 'Mateus', 'mt', 7, 21, '"Nem todo aquele que me diz: ‘Senhor, Senhor’, entrará no Reino dos céus, mas apenas aquele que faz a vontade de meu Pai que está nos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23339, 'nvi', 'Mateus', 'mt', 7, 22, 'Muitos me dirão naquele dia: ‘Senhor, Senhor, não profetizamos nós em teu nome? Em teu nome não expulsamos demônios e não realizamos muitos milagres? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23340, 'nvi', 'Mateus', 'mt', 7, 23, 'Então eu lhes direi claramente: ‘Nunca os conheci. Afastem-se de mim vocês, que praticam o mal! ’ "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23341, 'nvi', 'Mateus', 'mt', 7, 24, '"Portanto, quem ouve estas minhas palavras e as pratica é como um homem prudente que construiu a sua casa sobre a rocha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23342, 'nvi', 'Mateus', 'mt', 7, 25, 'Caiu a chuva, transbordaram os rios, sopraram os ventos e deram contra aquela casa, e ela não caiu, porque tinha seus alicerces na rocha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23343, 'nvi', 'Mateus', 'mt', 7, 26, 'Mas quem ouve estas minhas palavras e não as pratica é como um insensato que construiu a sua casa sobre a areia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23344, 'nvi', 'Mateus', 'mt', 7, 27, 'Caiu a chuva, transbordaram os rios, sopraram os ventos e deram contra aquela casa, e ela caiu. E foi grande a sua queda".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23345, 'nvi', 'Mateus', 'mt', 7, 28, 'Quando Jesus acabou de dizer essas coisas, as multidões estavam maravilhadas com o seu ensino,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23346, 'nvi', 'Mateus', 'mt', 7, 29, 'porque ele as ensinava como quem tem autoridade, e não como os mestres da lei.');
