INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23347, 'nvi', 'Mateus', 'mt', 8, 1, 'Quando ele desceu do monte, grandes multidões o seguiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23348, 'nvi', 'Mateus', 'mt', 8, 2, 'Um leproso, aproximando-se, adorou-o de joelhos e disse: "Senhor, se quiseres, podes purificar-me! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23349, 'nvi', 'Mateus', 'mt', 8, 3, 'Jesus estendeu a mão, tocou nele e disse: "Quero. Seja purificado! " Imediatamente ele foi purificado da lepra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23350, 'nvi', 'Mateus', 'mt', 8, 4, 'Em seguida Jesus lhe disse: "Olhe, não conte isso a ninguém. Mas vá mostrar-se ao sacerdote e apresente a oferta que Moisés ordenou, para que sirva de testemunho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23351, 'nvi', 'Mateus', 'mt', 8, 5, 'Entrando Jesus em Cafarnaum, dirigiu-se a ele um centurião, pedindo-lhe ajuda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23352, 'nvi', 'Mateus', 'mt', 8, 6, 'E disse: "Senhor, meu servo está em casa, paralítico, em terrível sofrimento".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23353, 'nvi', 'Mateus', 'mt', 8, 7, 'Jesus lhe disse: "Eu irei curá-lo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23354, 'nvi', 'Mateus', 'mt', 8, 8, 'Respondeu o centurião: "Senhor, não mereço receber-te debaixo do meu teto. Mas dize apenas uma palavra, e o meu servo será curado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23355, 'nvi', 'Mateus', 'mt', 8, 9, 'Pois eu também sou homem sujeito à autoridade, com soldados sob o meu comando. Digo a um: ‘Vá’, e ele vai; e a outro: ‘Venha’, e ele vem. Digo a meu servo: ‘Faça isto’, e ele faz".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23356, 'nvi', 'Mateus', 'mt', 8, 10, 'Ao ouvir isso, Jesus admirou-se e disse aos que o seguiam: "Digo-lhes a verdade: Não encontrei em Israel ninguém com tamanha fé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23357, 'nvi', 'Mateus', 'mt', 8, 11, 'Eu lhes digo que muitos virão do Oriente e do Ocidente, e se sentarão à mesa com Abraão, Isaque e Jacó no Reino dos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23358, 'nvi', 'Mateus', 'mt', 8, 12, 'Mas os súditos do Reino serão lançados para fora, nas trevas, onde haverá choro e ranger de dentes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23359, 'nvi', 'Mateus', 'mt', 8, 13, 'Então Jesus disse ao centurião: "Vá! Como você creu, assim lhe acontecerá! " Na mesma hora o seu servo foi curado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23360, 'nvi', 'Mateus', 'mt', 8, 14, 'Entrando Jesus na casa de Pedro, viu a sogra deste de cama, com febre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23361, 'nvi', 'Mateus', 'mt', 8, 15, 'Tomando-a pela mão, a febre a deixou, e ela se levantou e começou a servi-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23362, 'nvi', 'Mateus', 'mt', 8, 16, 'Ao anoitecer foram trazidos a ele muitos endemoninhados, e ele expulsou os espíritos com uma palavra e curou todos os doentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23363, 'nvi', 'Mateus', 'mt', 8, 17, 'E assim se cumpriu o que fora dito pelo profeta Isaías: "Ele tomou sobre si as nossas enfermidades e sobre si levou as nossas doenças".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23364, 'nvi', 'Mateus', 'mt', 8, 18, 'Quando Jesus viu a multidão ao seu redor, deu ordens para que atravessassem para o outro lado do mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23365, 'nvi', 'Mateus', 'mt', 8, 19, 'Então, um mestre da lei aproximou-se e disse: "Mestre, eu te seguirei por onde quer que fores".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23366, 'nvi', 'Mateus', 'mt', 8, 20, 'Jesus respondeu: "As raposas têm suas tocas e as aves do céu têm seus ninhos, mas o Filho do homem não tem onde repousar a cabeça".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23367, 'nvi', 'Mateus', 'mt', 8, 21, 'Outro discípulo lhe disse: "Senhor, deixa-me ir primeiro sepultar meu pai".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23368, 'nvi', 'Mateus', 'mt', 8, 22, 'Mas Jesus lhe disse: "Siga-me, e deixe que os mortos sepultem os seus próprios mortos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23369, 'nvi', 'Mateus', 'mt', 8, 23, 'Entrando ele no barco, seus discípulos o seguiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23370, 'nvi', 'Mateus', 'mt', 8, 24, 'De repente, uma violenta tempestade abateu-se sobre o mar, de forma que as ondas inundavam o barco. Jesus, porém, dormia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23371, 'nvi', 'Mateus', 'mt', 8, 25, 'Os discípulos foram acordá-lo, clamando: "Senhor, salva-nos! Vamos morrer! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23372, 'nvi', 'Mateus', 'mt', 8, 26, 'Ele perguntou: "Por que vocês estão com tanto medo, homens de pequena fé? " Então ele se levantou e repreendeu os ventos e o mar, e fez-se completa bonança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23373, 'nvi', 'Mateus', 'mt', 8, 27, 'Os homens ficaram perplexos e perguntaram: "Quem é este que até os ventos e o mar lhe obedecem? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23374, 'nvi', 'Mateus', 'mt', 8, 28, 'Quando ele chegou ao outro lado, à região dos gadarenos, foram ao seu encontro dois endemoninhados, que vinham dos sepulcros. Eles eram tão violentos que ninguém podia passar por aquele caminho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23375, 'nvi', 'Mateus', 'mt', 8, 29, 'Então eles gritaram: "Que queres conosco, Filho de Deus? Vieste aqui para nos atormentar antes do devido tempo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23376, 'nvi', 'Mateus', 'mt', 8, 30, 'A certa distância deles estava pastando uma grande manada de porcos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23377, 'nvi', 'Mateus', 'mt', 8, 31, 'Os demônios imploravam a Jesus: "Se nos expulsas, manda-nos entrar naquela manada de porcos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23378, 'nvi', 'Mateus', 'mt', 8, 32, 'Ele lhes disse: "Vão! " Eles saíram e entraram nos porcos, e toda a manada atirou-se precipício abaixo, em direção ao mar, e morreu afogada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23379, 'nvi', 'Mateus', 'mt', 8, 33, 'Os que cuidavam dos porcos fugiram, foram à cidade e contaram tudo, inclusive o que acontecera aos endemoninhados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23380, 'nvi', 'Mateus', 'mt', 8, 34, 'Toda a cidade saiu ao encontro de Jesus, e quando o viram, suplicaram-lhe que saísse do território deles.');
