INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23381, 'nvi', 'Mateus', 'mt', 9, 1, 'Entrando Jesus num barco, atravessou o mar e foi para a sua própria cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23382, 'nvi', 'Mateus', 'mt', 9, 2, 'Alguns homens trouxeram-lhe um paralítico, deitado numa cama. Vendo a fé que eles tinham, Jesus disse ao paralítico: "Tenha bom ânimo, filho; os seus pecados estão perdoados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23383, 'nvi', 'Mateus', 'mt', 9, 3, 'Diante disso, alguns mestres da lei disseram a si mesmos: "Este homem está blasfemando! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23384, 'nvi', 'Mateus', 'mt', 9, 4, 'Conhecendo Jesus seus pensamentos, disse-lhes: "Por que vocês pensam maldosamente em seus corações?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23385, 'nvi', 'Mateus', 'mt', 9, 5, 'Que é mais fácil dizer: ‘Os seus pecados estão perdoados’, ou: ‘Levante-se e ande’?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23386, 'nvi', 'Mateus', 'mt', 9, 6, 'Mas, para que vocês saibam que o Filho do homem tem na terra autoridade para perdoar pecados" — disse ao paralítico: "Levante-se, pegue a sua maca e vá para casa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23387, 'nvi', 'Mateus', 'mt', 9, 7, 'Ele se levantou e foi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23388, 'nvi', 'Mateus', 'mt', 9, 8, 'Vendo isso, a multidão ficou cheia de temor e glorificou a Deus, que dera tal autoridade aos homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23389, 'nvi', 'Mateus', 'mt', 9, 9, 'Passando por ali, Jesus viu um homem chamado Mateus, sentado na coletoria, e disse-lhe: "Siga-me". Mateus levantou-se e o seguiu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23390, 'nvi', 'Mateus', 'mt', 9, 10, 'Estando Jesus em casa, foram comer com ele e seus discípulos muitos publicanos e "pecadores".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23391, 'nvi', 'Mateus', 'mt', 9, 11, 'Vendo isso, os fariseus perguntaram aos discípulos dele: "Por que o mestre de vocês come com publicanos e ‘pecadores’? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23392, 'nvi', 'Mateus', 'mt', 9, 12, 'Ouvindo isso, Jesus disse: "Não são os que têm saúde que precisam de médico, mas sim os doentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23393, 'nvi', 'Mateus', 'mt', 9, 13, 'Vão aprender o que significa isto: ‘Desejo misericórdia, não sacrifícios’. Pois eu não vim chamar justos, mas pecadores".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23394, 'nvi', 'Mateus', 'mt', 9, 14, 'Então os discípulos de João vieram perguntar-lhe: "Por que nós e os fariseus jejuamos, mas os teus discípulos não? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23395, 'nvi', 'Mateus', 'mt', 9, 15, 'Jesus respondeu: "Como podem os convidados do noivo ficar de luto enquanto o noivo está com eles? Virão dias quando o noivo lhes será tirado; então jejuarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23396, 'nvi', 'Mateus', 'mt', 9, 16, '"Ninguém põe remendo de pano novo em roupa velha, pois o remendo forçará a roupa, tornando pior o rasgo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23397, 'nvi', 'Mateus', 'mt', 9, 17, 'Nem se põe vinho novo em vasilhas de couro velhas; se o fizer, as vasilhas se rebentarão, o vinho se derramará e as vasilhas se estragarão. Pelo contrário, põe-se vinho novo em vasilhas de couro novas; e ambos se conservam".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23398, 'nvi', 'Mateus', 'mt', 9, 18, 'Falava ele ainda quando um dos dirigentes da sinagoga chegou, ajoelhou-se diante dele e disse: "Minha filha acaba de morrer. Vem e impõe a tua mão sobre ela, e ela viverá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23399, 'nvi', 'Mateus', 'mt', 9, 19, 'Jesus levantou-se e foi com ele, e também os seus discípulos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23400, 'nvi', 'Mateus', 'mt', 9, 20, 'Nisso uma mulher que havia doze anos vinha sofrendo de uma hemorragia, chegou por trás dele e tocou na borda do seu manto,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23401, 'nvi', 'Mateus', 'mt', 9, 21, 'pois dizia a si mesma: "Se eu tão-somente tocar em seu manto, ficarei curada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23402, 'nvi', 'Mateus', 'mt', 9, 22, 'Voltando-se, Jesus a viu e disse: "Ânimo, filha, a sua fé a curou! " E desde aquele instante a mulher ficou curada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23403, 'nvi', 'Mateus', 'mt', 9, 23, 'Quando ele chegou à casa do dirigente da sinagoga e viu os flautistas e a multidão agitada,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23404, 'nvi', 'Mateus', 'mt', 9, 24, 'disse: "Saiam! A menina não está morta, mas dorme". Todos começaram a rir dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23405, 'nvi', 'Mateus', 'mt', 9, 25, 'Depois que a multidão se afastou, ele entrou e tomou a menina pela mão, e ela se levantou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23406, 'nvi', 'Mateus', 'mt', 9, 26, 'A notícia deste acontecimento espalhou-se por toda aquela região.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23407, 'nvi', 'Mateus', 'mt', 9, 27, 'Saindo Jesus dali, dois cegos o seguiram, clamando: "Filho de Davi, tem misericórdia de nós! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23408, 'nvi', 'Mateus', 'mt', 9, 28, 'Entrando ele em casa, os cegos se aproximaram, e ele lhes perguntou: "Vocês crêem que eu sou capaz de fazer isso? " Eles responderam: "Sim, Senhor! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23409, 'nvi', 'Mateus', 'mt', 9, 29, 'E ele, tocando nos olhos deles, disse: "Que lhes seja feito segundo a fé que vocês têm! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23410, 'nvi', 'Mateus', 'mt', 9, 30, 'E a visão deles foi restaurada. Então Jesus os advertiu severamente: "Cuidem para que ninguém saiba disso".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23411, 'nvi', 'Mateus', 'mt', 9, 31, 'Eles, porém, saíram e espalharam a notícia por toda aquela região.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23412, 'nvi', 'Mateus', 'mt', 9, 32, 'Enquanto eles se retiravam, foi levado a Jesus um homem endemoninhado que não podia falar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23413, 'nvi', 'Mateus', 'mt', 9, 33, 'Quando o demônio foi expulso, o mudo começou a falar. A multidão ficou admirada e disse: "Nunca se viu nada parecido em Israel! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23414, 'nvi', 'Mateus', 'mt', 9, 34, 'Mas os fariseus diziam: "É pelo príncipe dos demônios que ele expulsa demônios".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23415, 'nvi', 'Mateus', 'mt', 9, 35, 'Jesus ia passando por todas as cidades e povoados, ensinando nas sinagogas, pregando as boas novas do Reino e curando todas as enfermidades e doenças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23416, 'nvi', 'Mateus', 'mt', 9, 36, 'Ao ver as multidões, teve compaixão delas, porque estavam aflitas e desamparadas, como ovelhas sem pastor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23417, 'nvi', 'Mateus', 'mt', 9, 37, 'Então disse aos seus discípulos: "A seara é grande, mas os trabalhadores são poucos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23418, 'nvi', 'Mateus', 'mt', 9, 38, 'Peçam, pois, ao Senhor da seara que envie trabalhadores para a sua seara".');
