INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22714, 'nvi', 'Naum', 'na', 3, 1, 'Ai da cidade sanguinária, repleta de fraudes e cheia de roubos, sempre fazendo as suas vítimas!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22715, 'nvi', 'Naum', 'na', 3, 2, 'Ah, o estalo dos chicotes, o barulho das rodas, o galope dos cavalos e o sacudir dos carros de guerra!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22716, 'nvi', 'Naum', 'na', 3, 3, 'Cavaleiros atacando, espadas reluzentes e lanças cintilantes! Muitos mortos, montanhas de cadáveres, corpos sem conta, gente tropeçando por cima deles!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22717, 'nvi', 'Naum', 'na', 3, 4, 'Tudo por causa do desejo desenfreado de uma prostituta sedutora, mestra de feitiçarias, que escravizou nações com a sua prostituição e povos, com a sua feitiçaria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22718, 'nvi', 'Naum', 'na', 3, 5, '“Eu estou contra você”, declara o SENHOR dos Exércitos, “vou levantar o seu vestido até a altura do seu rosto. Mostrarei às nações a sua nudez e aos reinos, as suas vergonhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22719, 'nvi', 'Naum', 'na', 3, 6, 'Eu jogarei imundície sobre você, e a tratarei com desprezo; farei de você um exemplo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22720, 'nvi', 'Naum', 'na', 3, 7, 'Todos os que a virem fugirão dizendo: ‘Nínive está arrasada! Quem a lamentará? ’ Onde encontrarei quem a console? ”');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22721, 'nvi', 'Naum', 'na', 3, 8, 'Acaso és melhor do que Tebas, situada junto ao Nilo, rodeada de águas? O rio era a sua defesa; as águas, o seu muro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22722, 'nvi', 'Naum', 'na', 3, 9, 'A Etiópia e o Egito eram a sua força ilimitada; Fute e a Líbia estavam entre os seus aliados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22723, 'nvi', 'Naum', 'na', 3, 10, 'Apesar disso, ela foi deportada, levada para o exílio. Em cada esquina as suas crianças foram massacradas. Tiraram sortes para decidir o destino dos seus nobres; todos os poderosos foram acorrentados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22724, 'nvi', 'Naum', 'na', 3, 11, 'Você também ficará embriagada; irá esconder-se, tentando proteger-se do inimigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22725, 'nvi', 'Naum', 'na', 3, 12, 'Todas as suas fortalezas são como figueiras carregadas de figos maduros; basta sacudi-las, e os figos caem em bocas vorazes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22726, 'nvi', 'Naum', 'na', 3, 13, 'Olhe bem para as suas tropas: não passam de mulheres! As suas portas estão escancaradas para os seus inimigos; o fogo devorou as suas trancas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22727, 'nvi', 'Naum', 'na', 3, 14, 'Reserve água para o tempo do cerco! Reforce as suas fortalezas! Entre no barro, pise a argamassa, prepare a forma para os tijolos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22728, 'nvi', 'Naum', 'na', 3, 15, 'Mesmo assim o fogo consumirá você; a espada a eliminará, e, como gafanhotos devastadores, a devorará! Multiplique-se como gafanhotos devastadores, multiplique-se como gafanhotos peregrinos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22729, 'nvi', 'Naum', 'na', 3, 16, 'Você multiplicou os seus comerciantes, tornando-os mais numerosos do que as estrelas do céu; mas como gafanhotos devastadores, devoram o país e depois voam para longe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22730, 'nvi', 'Naum', 'na', 3, 17, 'Os seus guardas são como gafanhotos peregrinos, os seus oficiais, como enxames de gafanhotos que se ajuntam sobre os muros em dias frios; mas quando o sol aparece, eles voam, ninguém sabe para onde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22731, 'nvi', 'Naum', 'na', 3, 18, 'Ó rei da Assíria, os seus pastores dormem; os seus nobres adormecem. O seu povo está espalhado pelos montes e não há ninguém para reuni-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22732, 'nvi', 'Naum', 'na', 3, 19, 'Não há cura para a sua chaga; a sua ferida é mortal. Quem ouve notícias a seu respeito bate palmas pela sua queda, pois, quem não sofreu a sua crueldade sem limites?');
