INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12551, 'nvi', 'Neemias', 'ne', 10, 1, 'Esta é a relação dos que o assinaram: Neemias, o governador, filho de Hacalias, e Zedequias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12552, 'nvi', 'Neemias', 'ne', 10, 2, 'Seraías, Azarias, Jeremias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12553, 'nvi', 'Neemias', 'ne', 10, 3, 'Pasur, Amarias, Malquias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12554, 'nvi', 'Neemias', 'ne', 10, 4, 'Hatus, Sebanias, Maluque,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12555, 'nvi', 'Neemias', 'ne', 10, 5, 'Harim, Meremote, Obadias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12556, 'nvi', 'Neemias', 'ne', 10, 6, 'Daniel, Ginetom, Baruque,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12557, 'nvi', 'Neemias', 'ne', 10, 7, 'Mesulão, Abias, Miamim,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12558, 'nvi', 'Neemias', 'ne', 10, 8, 'Maazias, Bilgai e Semaías. Esses eram sacerdotes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12559, 'nvi', 'Neemias', 'ne', 10, 9, 'Dos levitas: Jesua, filho de Azanias, Binui, dos filhos de Henadade, Cadmiel');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12560, 'nvi', 'Neemias', 'ne', 10, 10, 'e seus colegas: Sebanias, Hodias, Quelita, Pelaías, Hanã,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12561, 'nvi', 'Neemias', 'ne', 10, 11, 'Mica, Reobe, Hasabias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12562, 'nvi', 'Neemias', 'ne', 10, 12, 'Zacur, Serebias, Sebanias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12563, 'nvi', 'Neemias', 'ne', 10, 13, 'Hodias, Bani e Beninu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12564, 'nvi', 'Neemias', 'ne', 10, 14, 'Dos líderes do povo: Parós, Paate-Moabe, Elão, Zatu, Bani,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12565, 'nvi', 'Neemias', 'ne', 10, 15, 'Buni, Azgade, Bebai,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12566, 'nvi', 'Neemias', 'ne', 10, 16, 'Adonias, Bigvai, Adim,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12567, 'nvi', 'Neemias', 'ne', 10, 17, 'Ater, Ezequias, Azur,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12568, 'nvi', 'Neemias', 'ne', 10, 18, 'Hodias, Hasum, Besai,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12569, 'nvi', 'Neemias', 'ne', 10, 19, 'Harife, Anatote, Nebai,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12570, 'nvi', 'Neemias', 'ne', 10, 20, 'Magpias, Mesulão, Hezir,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12571, 'nvi', 'Neemias', 'ne', 10, 21, 'Mesezabeel, Zadoque, Jadua,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12572, 'nvi', 'Neemias', 'ne', 10, 22, 'Pelatias, Hanã, Anaías,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12573, 'nvi', 'Neemias', 'ne', 10, 23, 'Oséias, Hananias, Hassube,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12574, 'nvi', 'Neemias', 'ne', 10, 24, 'Haloés, Pílea, Sobeque,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12575, 'nvi', 'Neemias', 'ne', 10, 25, 'Reum, Hasabna, Maaséias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12576, 'nvi', 'Neemias', 'ne', 10, 26, 'Aías, Hanã, Anã,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12577, 'nvi', 'Neemias', 'ne', 10, 27, 'Maluque, Harim e Baaná.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12578, 'nvi', 'Neemias', 'ne', 10, 28, '"O restante do povo: sacerdotes, levitas, porteiros, cantores, servidores do templo e todos os que se separaram dos povos vizinhos por amor à Lei de Deus, juntamente com suas mulheres e com todos os seus filhos e filhas que eram capazes de entender,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12579, 'nvi', 'Neemias', 'ne', 10, 29, 'agora se unem aos seus irmãos, os nobres, e se obrigam sob maldição e sob juramento a seguir a Lei de Deus dada por meio do servo de Deus Moisés e a obedecer fielmente a todos os mandamentos, ordenanças e decretos do Senhor, o nosso Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12580, 'nvi', 'Neemias', 'ne', 10, 30, '"Prometemos não dar nossas filhas em casamento aos povos vizinhos nem aceitar que as filhas deles se casem com os nossos filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12581, 'nvi', 'Neemias', 'ne', 10, 31, '"Quando os povos vizinhos trouxerem mercadorias ou cereal para venderem em dia de sábado ou de festa, não compraremos deles nesses dias. Cada sete anos abriremos mão de trabalhar a terra e cancelaremos todas as dívidas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12582, 'nvi', 'Neemias', 'ne', 10, 32, '"Assumimos a responsabilidade de, conforme o mandamento, dar anualmente quatro gramas para o serviço do templo de nosso Deus:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12583, 'nvi', 'Neemias', 'ne', 10, 33, 'para os pães consagrados, para as ofertas regulares de cereal e para os holocaustos, para as ofertas dos sábados, das festas de lua nova e para as festas fixas, para as ofertas sagradas, para as ofertas pelo pecado para fazer propiciação por Israel, e para as necessidades do templo de nosso Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12584, 'nvi', 'Neemias', 'ne', 10, 34, '"Também tiramos sorte entre as famílias dos sacerdotes, dos levitas e do povo, para escalar anualmente a que deverá trazer lenha ao templo de nosso Deus, no tempo determinado, para queimar sobre o altar do Senhor, o nosso Deus, conforme está escrito na Lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12585, 'nvi', 'Neemias', 'ne', 10, 35, '"Também assumimos a responsabilidade de trazer anualmente ao templo do Senhor os primeiros frutos de nossas colheitas e de toda árvore frutífera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12586, 'nvi', 'Neemias', 'ne', 10, 36, '"Conforme também está escrito na Lei, traremos o primeiro de nossos filhos e a primeira cria de nossos rebanhos, tanto de ovelhas como de bois, para o templo de nosso Deus, para os sacerdotes que ali estiverem ministrando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12587, 'nvi', 'Neemias', 'ne', 10, 37, '"Além do mais, traremos para os depósitos do templo de nosso Deus, para os sacerdotes, a nossa primeira massa de cereal moído, e as nossas primeiras ofertas de cereal, do fruto de todas as nossas árvores e de nosso vinho e azeite. E traremos o dízimo das nossas colheitas para os levitas, pois são eles que recolhem os dízimos em todas as cidades onde trabalhamos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12588, 'nvi', 'Neemias', 'ne', 10, 38, 'Um sacerdote descendente de Arão deverá acompanhar os levitas quando receberem os dízimos, e os levitas terão que trazer um décimo dos dízimos ao templo de nosso Deus, aos depósitos do templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12589, 'nvi', 'Neemias', 'ne', 10, 39, 'O povo de Israel, inclusive os levitas, deverão trazer ofertas de cereal, de vinho novo e de azeite aos depósitos onde se guardam os utensílios para o santuário. É ali que os sacerdotes ministram e que os porteiros e os cantores ficam. "Não negligenciaremos o templo de nosso Deus".');
