INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12626, 'nvi', 'Neemias', 'ne', 12, 1, 'Estes foram os sacerdotes e os levitas que voltaram com Zorobabel, filho de Sealtiel, e com Jesua: Seraías, Jeremias, Esdras,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12627, 'nvi', 'Neemias', 'ne', 12, 2, 'Amarias, Maluque, Hatus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12628, 'nvi', 'Neemias', 'ne', 12, 3, 'Secanias, Reum, Meremote,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12629, 'nvi', 'Neemias', 'ne', 12, 4, 'Ido, Ginetom, Abias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12630, 'nvi', 'Neemias', 'ne', 12, 5, 'Miamim, Maadias, Bilga,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12631, 'nvi', 'Neemias', 'ne', 12, 6, 'Semaías, Joiaribe, Jedaías,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12632, 'nvi', 'Neemias', 'ne', 12, 7, 'Salu, Amoque, Hilquias e Jedaías. Esses foram os chefes dos sacerdotes e seus colegas nos dias de Jesua.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12633, 'nvi', 'Neemias', 'ne', 12, 8, 'Os levitas foram Jesua, Binui, Cadmiel, Serebias, Judá, e também Matanias, que, junto com seus colegas, estava encarregado dos cânticos de ações de graças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12634, 'nvi', 'Neemias', 'ne', 12, 9, 'Bacbuquias e Uni, colegas deles, ficavam em frente deles para responder-lhes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12635, 'nvi', 'Neemias', 'ne', 12, 10, 'Jesua foi o pai de Joiaquim, Joiaquim foi o pai de Eliasibe, Eliasibe foi o pai de Joiada,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12636, 'nvi', 'Neemias', 'ne', 12, 11, 'Joiada foi o pai de Jônatas, Jônatas foi o pai de Jadua.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12637, 'nvi', 'Neemias', 'ne', 12, 12, 'Nos dias de Joiaquim estes foram os líderes das famílias dos sacerdotes: da família de Seraías, Meraías; da família de Jeremias, Hananias;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12638, 'nvi', 'Neemias', 'ne', 12, 13, 'da família de Esdras, Mesulão; da família de Amarias, Joanã;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12639, 'nvi', 'Neemias', 'ne', 12, 14, 'da família de Maluqui, Jônatas; da família de Secanias, José;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12640, 'nvi', 'Neemias', 'ne', 12, 15, 'da família de Harim, Adna; da família de Meremote, Helcai;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12641, 'nvi', 'Neemias', 'ne', 12, 16, 'da família de Ido, Zacarias; da família de Ginetom, Mesulão;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12642, 'nvi', 'Neemias', 'ne', 12, 17, 'da família de Abias, Zicri; da família de Miniamim e de Maadias, Piltai;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12643, 'nvi', 'Neemias', 'ne', 12, 18, 'da família de Bilga, Samua; da família de Semaías, Jônatas;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12644, 'nvi', 'Neemias', 'ne', 12, 19, 'da família de Joiaribe, Matenai; da família de Jedaías, Uzi;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12645, 'nvi', 'Neemias', 'ne', 12, 20, 'da família de Salai, Calai; da família de Amoque, Héber;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12646, 'nvi', 'Neemias', 'ne', 12, 21, 'da família de Hilquias, Hasabias; da família de Jedaías, Natanael.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12647, 'nvi', 'Neemias', 'ne', 12, 22, 'Os chefes das famílias dos levitas e dos sacerdotes, nos dias de Eliasibe, Joiada, Joanã e Jadua, foram registrados no reinado de Dario, o persa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12648, 'nvi', 'Neemias', 'ne', 12, 23, 'Os chefes das famílias dos descendentes de Levi até à época de Joanã, filho de Eliasibe, foram registrados no livro das crônicas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12649, 'nvi', 'Neemias', 'ne', 12, 24, 'Os líderes dos levitas foram Hasabias, Serebias, Jesua, filho de Cadmiel, e seus colegas, que ficavam em frente deles quando entoavam louvores e ações de graças; um grupo respondia ao outro, conforme prescrito por Davi, homem de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12650, 'nvi', 'Neemias', 'ne', 12, 25, 'Matanias, Bacbuquias, Obadias, Mesulão, Talmom e Acube eram porteiros; vigiavam os depósitos junto às portas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12651, 'nvi', 'Neemias', 'ne', 12, 26, 'Eles serviram nos dias de Joiaquim, filho de Jesua, neto de Jozadaque, e nos dias do governador Neemias e de Esdras, sacerdote e escriba.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12652, 'nvi', 'Neemias', 'ne', 12, 27, 'Por ocasião da dedicação dos muros de Jerusalém, os levitas foram procurados e trazidos de onde moravam para Jerusalém para celebrarem a dedicação alegremente, com cânticos e ações de graças, ao som de címbalos, harpas e liras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12653, 'nvi', 'Neemias', 'ne', 12, 28, 'Os cantores foram trazidos dos arredores de Jerusalém, dos povoados dos netofatitas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12654, 'nvi', 'Neemias', 'ne', 12, 29, 'de Bete-Gilgal, e da região de Geba e Azmavete, pois os cantores haviam construído povoados para si ao redor de Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12655, 'nvi', 'Neemias', 'ne', 12, 30, 'Os sacerdotes e os levitas se purificaram cerimonialmente, e depois purificaram também o povo, as portas e os muros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12656, 'nvi', 'Neemias', 'ne', 12, 31, 'Ordenei aos líderes de Judá que subissem ao alto do muro. Também designei dois grandes coros para darem graças. Um deles avançou em cima do muro, para a direita, até à porta do Esterco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12657, 'nvi', 'Neemias', 'ne', 12, 32, 'Hosaías e metade dos líderes de Judá os seguiram,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12658, 'nvi', 'Neemias', 'ne', 12, 33, 'e mais Azarias, Esdras, Mesulão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12659, 'nvi', 'Neemias', 'ne', 12, 34, 'Judá, Benjamim, Semaías, Jeremias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12660, 'nvi', 'Neemias', 'ne', 12, 35, 'bem como alguns sacerdotes com trombetas, além de Zacarias, filho de Jônatas, neto de Semaías, bisneto de Matanias, que era filho de Micaías, neto de Zacur, bisneto de Asafe,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12661, 'nvi', 'Neemias', 'ne', 12, 36, 'e seus colegas, Semaías, Azareel, Milalai, Gilalai, Maai, Natanael, Judá e Hanani, que tocavam os instrumentos musicais prescritos por Davi, homem de Deus. Esdras, o escriba, ia na frente deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12662, 'nvi', 'Neemias', 'ne', 12, 37, 'À porta da Fonte eles subiram diretamente os degraus da cidade de Davi, na subida para o muro, e passaram sobre a casa de Davi até à porta das Águas, a leste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12663, 'nvi', 'Neemias', 'ne', 12, 38, 'O segundo coro avançou no sentido oposto. Eu os acompanhei, quando iam sobre o muro, levando comigo a metade do povo; passamos pela torre dos Fornos até à porta Larga,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12664, 'nvi', 'Neemias', 'ne', 12, 39, 'sobre a porta de Efraim, a porta Jesaná, a porta do Peixe, a torre de Hananeel e a torre dos Cem, indo até à porta das Ovelhas. Junto à porta da Guarda eles pararam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12665, 'nvi', 'Neemias', 'ne', 12, 40, 'Então, os dois coros encarregados das ações de graças, assumiram os seus lugares no templo de Deus, o que também fiz eu acompanhado da metade dos oficiais,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12666, 'nvi', 'Neemias', 'ne', 12, 41, 'e também dos sacerdotes Eliaquim, Maaséias, Miniamim, Micaías, Elioenai, Zacarias e Hananias, com suas trombetas');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12667, 'nvi', 'Neemias', 'ne', 12, 42, 'e também de Maaséias, Semaías, Eleazar, Uzi, Joanã, Malquias, Elão e Ézer. Os coros cantaram sob a direção de Jezraías.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12668, 'nvi', 'Neemias', 'ne', 12, 43, 'E naquele dia contentes ofereceram grandes sacrifícios, pois Deus lhes enchera de grande alegria. As mulheres e as crianças também se alegravam, e os sons da alegria de Jerusalém podiam ser ouvidos de longe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12669, 'nvi', 'Neemias', 'ne', 12, 44, 'Naquela ocasião foram designados alguns encarregados dos depósitos onde se recebiam as contribuições gerais, os primeiros frutos e os dízimos. Das lavouras que havia em torno das cidades eles deviam trazer para os depósitos as porções exigidas pela Lei para os sacerdotes e os levitas. E, de fato, o povo de Judá estava satisfeito com os sacerdotes e os levitas que ministravam no templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12670, 'nvi', 'Neemias', 'ne', 12, 45, 'Eles celebravam o culto ao seu Deus e o ritual de purificação, dos quais também participavam os cantores e os porteiros, de acordo com as ordens de Davi e do seu filho Salomão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12671, 'nvi', 'Neemias', 'ne', 12, 46, 'Pois muito tempo antes, nos dias de Davi e de Asafe, havia dirigentes dos cantores e que dirigiam os cânticos de louvor e de graças a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12672, 'nvi', 'Neemias', 'ne', 12, 47, 'Sucedeu, pois, que nos dias de Zorobabel e de Neemias, todo o Israel contribuía com ofertas diárias para os cantores e para os porteiros, além de separar a parte dos outros levitas, e os levitas separavam a porção dos descendentes de Arão.');
